INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (3, 'AstraZeneca', 1, 'Đang chờ nhập khẩu', null, '/storage/vaccine_photo/JUkyhTX1B3oRWk170GNf.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin ph&ograve;ng Covid-19 của AstraZeneca được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi Đại học Oxford v&agrave; h&atilde;ng dược nổi tiếng thế giới AstraZeneca (Vương quốc Anh).</li>
<li style="box-sizing: border-box; text-align: justify;">AstraZeneca l&agrave; c&ocirc;ng ty dược phẩm sinh học to&agrave;n cầu tập trung v&agrave;o việc ph&aacute;t minh, ph&aacute;t triển v&agrave; thương mại h&oacute;a c&aacute;c loại thuốc đặc trị trong ba lĩnh vực ch&iacute;nh: Ung thư; tim mạch, thận &amp; chuyển h&oacute;a; h&ocirc; hấp &amp; miễn dịch. C&oacute; trụ sở tại Cambridge (Vương quốc Anh), AstraZeneca hoạt động tại hơn 100 quốc gia v&agrave; c&aacute;c loại thuốc ti&ecirc;n tiến của c&ocirc;ng ty hiện được sử dụng bởi h&agrave;ng triệu bệnh nh&acirc;n tr&ecirc;n to&agrave;n thế giới.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin ph&ograve;ng Covid-19 của AstraZeneca l&agrave; vắc xin được chỉ định tạo miễn dịch chủ động để ph&ograve;ng bệnh Covid-19 g&acirc;y ra do virus SARS-CoV-2.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đối tượng c&oacute; thể sử dụng vắc xin</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin ph&ograve;ng Covid-19 của AstraZeneca được chỉ định ti&ecirc;m ph&ograve;ng cho những người từ 18 tuổi trở l&ecirc;n.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;">Lịch ti&ecirc;m gồm 2 mũi:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: Lần đầu ti&ecirc;n ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: Sau mũi đầu ti&ecirc;n từ 4 &ndash; 12 tuần.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin ph&ograve;ng Covid-19 của AstraZeneca được chỉ định ti&ecirc;m v&agrave;o bắp (thường ở c&aacute;nh tay tr&ecirc;n) với liều 0,5ml.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định /Ho&atilde;n ti&ecirc;m</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px;">
<li style="box-sizing: border-box; text-align: justify;">Chống chỉ định vắc xin với c&aacute;c trường hợp c&oacute; phản ứng dị ứng với bất k&igrave; th&agrave;nh phần n&agrave;o của vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Chống chỉ định ti&ecirc;m cho những người mắc hội chứng Guillain-Barr&eacute; hoặc bất cứ bệnh l&yacute; n&agrave;o c&oacute; t&igrave;nh trạng hủy myelin.</li>
<li style="box-sizing: border-box; text-align: justify;">Ho&atilde;n ti&ecirc;m cho những người nhiễm tr&ugrave;ng hoặc c&aacute;c bệnh l&yacute; cấp t&iacute;nh nặng kh&aacute;c, sốt &gt; 38&deg;C v&agrave;o ng&agrave;y trước ti&ecirc;m chủng v&agrave; ng&agrave;y ti&ecirc;m chủng.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m vắc xin với c&aacute;c trường hợp đ&atilde; từng nhiễm virus SARS-CoV-2.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m vắc xin đối với người mắc bệnh l&yacute; tim mạch, h&ocirc; hấp, ti&ecirc;u h&oacute;a, gan, thận, nội tiết, v&agrave; thần kinh nặng v&agrave;/hoặc kh&ocirc;ng kiểm so&aacute;t được.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px;">
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c nghi&ecirc;n cứu tương t&aacute;c, sử dụng đồng thời vắc xin COVID-19 vaccine AstraZeneca với c&aacute;c vắc xin kh&aacute;c chưa được nghi&ecirc;n cứu.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng tại chỗ ti&ecirc;m: Nổi ban đỏ, chai cứng, sưng đau, ngứa, tăng nhạy cảm tại chỗ ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng to&agrave;n th&acirc;n kh&aacute;c: Sốt, mệt mỏi, ớn lạnh, đau đầu, đau cơ hoặc đau khớp, buồn ngủ, giảm cảm gi&aacute;c th&egrave;m ăn, n&ocirc;n mửa, ti&ecirc;u chảy, hội chứng giống c&uacute;m.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px;">
<li style="box-sizing: border-box; text-align: justify;">Bảo quản ở nhiệt độ lạnh (từ 2 đến 8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C), tr&aacute;nh &aacute;nh s&aacute;ng. Đ&acirc;y l&agrave; vắc xin n<span style="font-weight: bold;">Nguồn gốc</span>
