INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (4, 'Infanrix Hexa', 2, 'Đang sử dụng', 126, '/storage/vaccine_photo/Q5dY5e9ttV9R7yDs4GVf.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Bạch hầu, ho g&agrave;, uốn v&aacute;n, vi&ecirc;m gan B, bại liệt v&agrave; c&aacute;c bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o do H.influenzae t&yacute;p B l&agrave; 6 căn bệnh v&ocirc; c&ugrave;ng nguy hiểm, chiếm tỷ lệ tử vong cao khi mắc phải ở trẻ em. Sau khi bị mắc bệnh, cơ thể kh&ocirc;ng thể tự sinh ra kh&aacute;ng thể miễn dịch tự nhi&ecirc;n n&ecirc;n bệnh c&oacute; thể t&aacute;i đi t&aacute;i lại nhiều lần. Bệnh c&oacute; khả năng g&acirc;y tử vong cao hoặc để lại những di chứng nặng nề như t&agrave;n tật, di chứng thần kinh, tổn thương n&atilde;o&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Tuy nhi&ecirc;n, 6 bệnh nguy hiểm n&agrave;y ho&agrave;n to&agrave;n c&oacute; thể ph&ograve;ng ngừa được bằng vắc xin chỉ với duy nhất 1 vắc xin kết hợp 6 trong 1. Đ&acirc;y l&agrave; loại vắc xin kết hợp ph&ograve;ng được nhiều bệnh nhất trong c&ugrave;ng 1 mũi ti&ecirc;m, đang được lưu h&agrave;nh tại Việt Nam.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về vắc xin Infanrix Hexa (Bỉ)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Ưu điểm nổi trội của vắc xin 6 trong 1 l&agrave; kết hợp ph&ograve;ng được 6 loại bệnh trong 1 mũi ti&ecirc;m, bao gồm: Ho g&agrave;, bạch hầu, uốn v&aacute;n, bại liệt, vi&ecirc;m gan B v&agrave; c&aacute;c bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o mủ do H.Influenzae t&yacute;p B (Hib). T&iacute;ch hợp trong duy nhất trong 1 vắc xin, 6 trong 1 Infanrix Hexa gi&uacute;p giảm số mũi ti&ecirc;m, đồng nghĩa với việc hạn chế đau đớn cho b&eacute; khi phải ti&ecirc;m qu&aacute; nhiều mũi.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Infanrix Hexa &ndash; Vắc xin 6 trong 1 của Bỉ được chỉ định ti&ecirc;m cho trẻ từ 2 th&aacute;ng đến 2 tuổi. Để đảm bảo hiệu quả tối ưu của vắc xin, n&ecirc;n lưu &yacute; ti&ecirc;m đ&uacute;ng v&agrave; đủ mũi cho con theo ph&aacute;c đồ. Trẻ cần ho&agrave;n th&agrave;nh 3 mũi cơ bản n&agrave;y trước 6 th&aacute;ng tuổi v&agrave; ho&agrave;n th&agrave;nh mũi ti&ecirc;m nhắc (mũi 4) trước 24 th&aacute;ng tuổi. Phụ huynh cần ghi nhớ ti&ecirc;m mũi nhắc lại cho trẻ nhằm tăng cường hệ miễn dịch, khống chế tối đa c&aacute;c nguy cơ g&acirc;y bệnh tiềm ẩn ở trẻ nhỏ.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Glaxo SmithKline (Bỉ)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Infanrix hexaTM được chỉ định ti&ecirc;m chủng cơ bản v&agrave; ti&ecirc;m chủng nhắc lại cho trẻ em ph&ograve;ng chống c&aacute;c bệnh: Ho g&agrave; &ndash; Bạch hầu &ndash; Uốn v&aacute;n &ndash; Bại liệt &ndash; Vi&ecirc;m gan B &ndash; C&aacute;c bệnh: vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o mủ do H.Influenzae tu&yacute;p B.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Lịch cơ bản : gồm 3 mũi với liều 0.5ml v&agrave;o c&aacute;c th&aacute;ng thứ 2, 3, 4&nbsp; hoặc c&aacute;c th&aacute;ng 3, 4, 5 hoặc c&aacute;c th&aacute;ng 2, 4, 6. Khoảng c&aacute;ch giữa c&aacute;c mũi ti&ecirc;m &iacute;t nhất l&agrave; 01 th&aacute;ng. Chỉ n&ecirc;n &aacute;p dụng theo lịch ti&ecirc;m của Chương tr&igrave;nh ti&ecirc;m chủng mở rộng (v&agrave;o c&aacute;c tuần 6, 10, 14) nếu trẻ đ&atilde; ti&ecirc;m vắc xin Vi&ecirc;m gan B l&uacute;c sinh. N&ecirc;n duy tr&igrave; phương ph&aacute;p dự ph&ograve;ng miễn dịch chống vi&ecirc;m gan B tại mỗi nước.</li>
<li style="box-sizing: border-box; text-align: justify;">Khi đ&atilde; ti&ecirc;m ph&ograve;ng vi&ecirc;m gan B l&uacute;c sinh th&igrave; c&oacute; thể d&ugrave;ng Infanrix hexaTM thay thế cho vắc xin vi&ecirc;m gan B mũi từ 6 tuần tuổi. N&ecirc;n sử dụng liều vắc xin vi&ecirc;m gan B đơn gi&aacute; nếu cần ti&ecirc;m&nbsp; mũi thứ 2 trước 6 tuần tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi ti&ecirc;m nhắc lại : Sau khi ti&ecirc;m chủng 2 mũi bằng Infanrix hexaTM, n&ecirc;n ti&ecirc;m mũi nhắc lại sau mũi ti&ecirc;m cuối c&ugrave;ng &iacute;t nhất 6 th&aacute;ng v&agrave; tốt nhất v&agrave;o giữa th&aacute;ng thứ 11 v&agrave; th&aacute;ng thứ 13.</li>
<li style="box-sizing: border-box; text-align: justify;">Sau khi ti&ecirc;m chủng đủ 3 mũi Infanrix hexaTM (theo lịch ti&ecirc;m chủng cơ bản) th&igrave; mũi ti&ecirc;m nhắc lại phải sau mũi ti&ecirc;m cuối c&ugrave;ng &iacute;t nhất l&agrave; 6 th&aacute;ng v&agrave; n&ecirc;n ho&agrave;n th&agrave;nh qu&aacute; tr&igrave;nh ti&ecirc;m trước 18 th&aacute;ng tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">N&ecirc;n ti&ecirc;m mũi nhắc lại theo khuyến c&aacute;o ch&iacute;nh thức.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Infanrix hexaTM được chỉ định ti&ecirc;m bắp s&acirc;u.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m tĩnh mạch hoặc trong da.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Chống chỉ định đối với những trường hợp mẫn cảm với c&aacute;c hoạt chất hay bất cứ t&aacute; dược hoặc chất tồn dư n&agrave;o trong thuốc.</li>
<li style="box-sizing: border-box; text-align: justify;">Qu&aacute; mẫn sau mũi ti&ecirc;m vắc xin bạch hầu, uốn v&aacute;n, ho g&agrave;, vi&ecirc;m gan B, bại liệt hoặc Hib trước đ&oacute;.</li>
<li style="box-sizing: border-box; text-align: justify;">Infanrix hexaTM chống chỉ định đối với những trẻ trong tiền sử đ&atilde; c&oacute; bệnh về n&atilde;o kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n trong v&ograve;ng 7 ng&agrave;y sau khi ti&ecirc;m vắc xin chứa th&agrave;nh phần ho g&agrave;.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">N&ecirc;n ho&atilde;n ti&ecirc;m vắc xin&nbsp; Infanrix hexaTM ở những trẻ sốt cao cấp t&iacute;nh.</li>
<li style="box-sizing: border-box; text-align: justify;">N&ecirc;n thận trọng c&acirc;n nhắc quyết định c&oacute; sử dụng th&ecirc;m vắc xin chứa th&agrave;nh phần ho g&agrave; hay kh&ocirc;ng nếu c&oacute; bất kỳ một trong những biểu hiện sau xuất hiện c&ugrave;ng một l&uacute;c v&agrave; c&oacute; li&ecirc;n quan đến việc ti&ecirc;m chủng bằng vắc xin chứa th&agrave;nh phần ho g&agrave;: sốt cao &ge; 40 độ C trong v&ograve;ng 48 giờ m&agrave; kh&ocirc;ng do c&aacute;c nguy&ecirc;n nh&acirc;n kh&aacute;c, suy sụp hoặc t&igrave;nh trạng giống sốc (giai đoạn sốc nhược &ndash; giảm đ&aacute;p ứng) trong v&ograve;ng 48 giờ sau ti&ecirc;m vắc xin, quấy kh&oacute;c k&eacute;o d&agrave;i &ge; 3 giờ, co giật c&oacute; hoặc kh&ocirc;ng k&egrave;m theo sốt xảy ra trong v&ograve;ng 3 ng&agrave;y sau khi ti&ecirc;m vắc xin. Ở những trẻ c&oacute; hội chứng thần kinh tiến triển bao gồm cơn co thắt giai đoạn sớm, cơn động kinh kh&ocirc;ng kiểm so&aacute;t hoặc bệnh n&atilde;o tiến triển tốt nhất n&ecirc;n ho&atilde;n ti&ecirc;m vắc xin ho g&agrave; (Pa v&agrave; Pw) cho đến khi bệnh đ&atilde; được x&aacute;c định r&otilde; hoặc đ&atilde; ổn định. Việc ti&ecirc;m vắc xin n&agrave;y phải được c&acirc;n nhắc kỹ giữa nguy cơ v&agrave; lợi &iacute;ch cho người ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Infanrix hexaTM chứa rất &iacute;t neomycin v&agrave; polymycin. N&ecirc;n thận trọng khi sử dụng vắc xin n&agrave;y cho những bệnh nh&acirc;n đ&atilde; biết qu&aacute; mẫn với một trong hai kh&aacute;ng sinh tr&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng chống chỉ định d&ugrave;ng Infanrix hexaTM khi c&oacute; tiền sử co giật do sốt, tiền sử gia đ&igrave;nh về co giật, tiền sử gia đ&igrave;nh về Hội chứng tử vong đột ngột ở nhũ nhi (SIDS). Trẻ c&oacute; tiền sử co giật do sốt n&ecirc;n được theo d&otilde;i chặt chẽ khi ti&ecirc;m vắc xin v&igrave; c&oacute; thể xuất hiện t&aacute;c dụng phụ trong v&ograve;ng 2 đến 3 ng&agrave;y sau ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Suy giảm miễn dịch mắc phải do virus (HIV) kh&ocirc;ng được xem l&agrave; chống chỉ định. C&oacute; thể kh&ocirc;ng đạt được đ&aacute;p ứng miễn dịch như mong đợi sau khi ti&ecirc;m chủng cho những bệnh nh&acirc;n ức chế miễn dịch.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng phải tất cả c&aacute;c trẻ ti&ecirc;m vắc xin đều c&oacute; đ&aacute;p ứng miễn dịch bảo vệ. C&aacute;c số liệu nghi&ecirc;n cứu giới hạn ở 169 trẻ đẻ non đ&atilde; chỉ ra rằng vắc xin Infanrix hexaTM c&oacute; thể ti&ecirc;m cho những trẻ đẻ non. Tuy nhi&ecirc;n, đ&aacute;p ứng miễn dịch ở những trẻ n&agrave;y thấp hơn v&agrave; mức độ bảo vệ tr&ecirc;n l&acirc;m s&agrave;ng vẫn chưa được biết r&otilde;.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng ngoại &yacute;:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">T&aacute;c dụng ngoại &yacute; được b&aacute;o c&aacute;o theo kết quả thử nghiệm l&acirc;m s&agrave;ng như: nhiễm khuẩn đường h&ocirc; hấp tr&ecirc;n, k&iacute;ch th&iacute;ch, bồn chồn, quấy kh&oacute;c bất thường, ngủ lơ mơ, co giật (c&oacute; sốt hoặc kh&ocirc;ng sốt), co thắt phế quản, n&ocirc;n, ti&ecirc;u chảy, vi&ecirc;m da, m&agrave;y đay (được quan s&aacute;t thấy ở những vaccine kh&aacute;c của GSK c&oacute; chứa th&agrave;nh phần ho g&agrave;-DTPa), đau, sưng đỏ tại chỗ ti&ecirc;m (&le; 50mm), sốt &ge; 38 độ C, mệt mỏi, phản ứng tại chỗ ti&ecirc;m kể cả chai cứng, sưng lan tỏa tại c&aacute;nh tay ti&ecirc;m vắc xin, đ&ocirc;i khi lan đến khớp gần kề.</li>
<li style="box-sizing: border-box; text-align: justify;">T&iacute;nh an to&agrave;n tr&igrave;nh b&agrave;y ở tr&ecirc;n dựa tr&ecirc;n dữ liệu từ hơn 16,000 trẻ. Như đ&atilde; quan s&aacute;t từ vắc xin DTPa v&agrave; vắc xin kết hợp chứa DTPa, tăng phản ứng tại chỗ v&agrave; sốt sau khi ti&ecirc;m nhắc lại bằng Infanrix hexaTM so với ti&ecirc;m chủng cơ bản được b&aacute;o c&aacute;o.</li>
<li style="box-sizing: border-box; text-align: justify;">T&aacute;c dụng phụ được b&aacute;o c&aacute;o từ kết quả điều tra hậu m&atilde;i: Bệnh hạch bạch huyết, giảm tiểu cầu, phản ứng dị ứng (kể cả phản ứng qu&aacute; mẫn v&agrave; shock phản vệ), suy sụp hoặc t&igrave;nh trạng giống shock (giai đoạn sốc nhược &ndash; giảm đ&aacute;p ứng), phản ứng sưng lan rộng, sưng tại c&aacute;nh tay ti&ecirc;m vắc xin, mụn nước tại chỗ ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Phản ứng sưng sau mũi nhắc lại thường xuất hiện ở trẻ ti&ecirc;m chủng cơ bản bằng vắc xin ho g&agrave; v&ocirc; b&agrave;o nhiều hơn so với trẻ ti&ecirc;m chủng cơ bản bằng vắc xin ho g&agrave; to&agrave;n tế b&agrave;o. Phản ứng n&agrave;y thường hết sau khoảng 4 ng&agrave;y.</li>
<li style="box-sizing: border-box; text-align: justify;">Kinh nghiệm với vắc xin vi&ecirc;m gan B: mất cảm gi&aacute;c, bệnh về thần kinh, hội chứng Guillain-Barr&eacute;, bệnh n&atilde;o, vi&ecirc;m n&atilde;o v&agrave; vi&ecirc;m m&agrave;ng n&atilde;o đ&atilde; được b&aacute;o c&aacute;o trong thời gian điều tra hậu m&atilde;i sau khi ti&ecirc;m vắc xin vi&ecirc;m gan B của GSK cho trẻ &lt; 2 tuổi. Chưa thiết lập được mối li&ecirc;n quan đến việc ti&ecirc;m vắc xin.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Dữ liệu về việc sử dụng đồng thời Infanrix hexaTM với Prevenar TM (vắc xin ph&ograve;ng c&aacute;c bệnh do li&ecirc;n cầu Pneumococcal saccharide) kh&ocirc;ng cho thấy c&oacute; ảnh hưởng đ&aacute;p ứng kh&aacute;ng thể với mỗi kh&aacute;ng nguy&ecirc;n ri&ecirc;ng biệt. Tuy nhi&ecirc;n, đ&atilde; c&oacute; b&aacute;o c&aacute;o tăng tỷ lệ sốt cao (&gt;39<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C) ở trẻ ti&ecirc;m Infanrix hexaTM v&agrave; Prevenar TM so với trẻ chỉ ti&ecirc;m vắc xin Infanrix hexaTM. C&oacute; thể &aacute;p dụng điều trị hạ sốt theo hướng dẫn của từng nước</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Infanrix hexaTM n&ecirc;n được bảo quản ở 2-8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C, tr&aacute;nh &aacute;nh s&aacute;ng. Kh&ocirc;ng đ&ocirc;ng đ&aacute; huyền dịch DTPa-HB-IPV v&agrave; vắc xin đ&atilde; ho&agrave;n nguy&ecirc;n. Loại bỏ nếu vắc xin bị đ&ocirc;ng băng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: right;"><em style="box-sizing: border-box;">Nguồn: mims.com</em></p>
<p><em style="box-sizing: border-box;">&nbsp;</em></p>', '2021-07-01 03:38:40', '2021-07-03 19:21:47', 10, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (5, 'Hexaxim', 2, 'Đang sử dụng', 126, '/storage/vaccine_photo/7lFMCHrwIw4TM7fTnr3d.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Bạch hầu, ho g&agrave;, uốn v&aacute;n, vi&ecirc;m gan B, bại liệt v&agrave; C&aacute;c bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o do H.influenzae t&yacute;p B l&agrave; 6 căn bệnh v&ocirc; c&ugrave;ng nguy hiểm v&agrave; chiếm tỷ lệ tử vong cao khi mắc phải ở trẻ em. Sau khi bị mắc bệnh, cơ thể kh&ocirc;ng thể tự sinh ra kh&aacute;ng thể miễn dịch tự nhi&ecirc;n n&ecirc;n bệnh c&oacute; thể t&aacute;i đi t&aacute;i lại nhiều lần. Bệnh c&oacute; khả năng g&acirc;y tử vong cao hoặc để lại những di chứng nặng nề như t&agrave;n tật, di chứng thần kinh, tổn thương n&atilde;o&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Tuy nhi&ecirc;n, 6 bệnh nguy hiểm n&agrave;y ho&agrave;n to&agrave;n c&oacute; thể ph&ograve;ng ngừa được bằng vắc xin chỉ với 1 mũi ti&ecirc;m kết hợp 6 trong 1. Đ&acirc;y l&agrave; loại vắc xin kết hợp ph&ograve;ng được nhiều bệnh nhất trong c&ugrave;ng 1 mũi ti&ecirc;m, đang được lưu h&agrave;nh tại Việt Nam.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Ưu điểm nổi trội của vắc xin 6 trong 1 l&agrave; kết hợp ph&ograve;ng được 6 loại bệnh trong 1 mũi ti&ecirc;m, gi&uacute;p giảm số mũi ti&ecirc;m, đồng nghĩa với việc hạn chế đau đớn cho b&eacute; khi phải ti&ecirc;m qu&aacute; nhiều mũi.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về vắc xin 6 trong 1 Hexaxim (Ph&aacute;p)</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Hexaxim &ndash; Vắc xin 6 trong 1 của Ph&aacute;p được chỉ định ti&ecirc;m cho trẻ từ 2 th&aacute;ng đến 2 tuổi. Để đảm bảo hiệu quả bảo vệ tối ưu của vắc xin, phụ huynh n&ecirc;n lưu &yacute; ti&ecirc;m đ&uacute;ng v&agrave; đủ mũi cho con theo ph&aacute;c đồ.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Sanofi Pasteur (Ph&aacute;p)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Ph&ograve;ng bệnh:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Hexaxim được chỉ định ti&ecirc;m chủng cho trẻ từ 6 tuần tuổi để ph&ograve;ng c&aacute;c bệnh: Ho g&agrave;, Bạch hầu, Uốn v&aacute;n, Vi&ecirc;m gan B, Bại liệt v&agrave; c&aacute;c bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o do H.influenzae tu&yacute;p B.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Gồm 3 mũi được ti&ecirc;m v&agrave;o c&aacute;c th&aacute;ng thứ 2, 3, 4 hoặc 3, 4, 5 hoặc 2, 4, 6. Khoảng c&aacute;ch giữa c&aacute;c mũi ti&ecirc;m tối thiểu l&agrave; 4 tuần. Mũi ti&ecirc;m nhắc lại c&aacute;ch mũi thứ 3 tối thiểu 6 th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Hexaxim được chỉ định ti&ecirc;m bắp. Vị tr&iacute; ti&ecirc;m l&agrave; mặt trước &ndash; ngo&agrave;i của phần tr&ecirc;n đ&ugrave;i v&agrave; v&ugrave;ng cơ delta ở trẻ 15 th&aacute;ng tuổi trở l&ecirc;n.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Kh&ocirc;ng được d&ugrave;ng Hexaxim nếu:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Tiền sử trước đ&acirc;y bị phản ứng phản vệ sau khi ti&ecirc;m Hexaxim</li>
<li style="box-sizing: border-box; text-align: justify;">Qu&aacute; mẫn với c&aacute;c hoạt chất hay bất cứ t&aacute; dược n&agrave;o được liệt k&ecirc; trong th&agrave;nh phần c&ugrave;a vắc xin, với c&aacute;c dư lượng vết (glutaraldehyde, formaldehyde, neomycin, streptomycin, polymyxin B), với vắc xin ho g&agrave; bất kỳ, hoặc trước đ&acirc;y từng bị phản ứng qu&aacute; mẫn sau khi ti&ecirc;m Hexaxim hoặc sau khi ti&ecirc;m vắc xin chứa c&aacute;c th&agrave;nh phần tương tự .</li>
<li style="box-sizing: border-box; text-align: justify;">Đối tượng&nbsp; c&oacute; bệnh l&yacute; n&atilde;o (tổn thương ở n&atilde;o) kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n, xảy ra trong v&ograve;ng 7 ng&agrave;y sau khi ti&ecirc;m 1 vắc xin chứa th&agrave;nh phần ho g&agrave; (vắc xin ho g&agrave; v&ocirc; b&agrave;o hay nguy&ecirc;n b&agrave;o). Trong trường hợp n&agrave;y, n&ecirc;n ngừng ti&ecirc;m vắc xin ho g&agrave; v&agrave; c&oacute; thể tiếp tục với qu&aacute; tr&igrave;nh ti&ecirc;m chủng với c&aacute;c vắc xin Bạch hầu, Uốn v&aacute;n, Vi&ecirc;m gan B, Bại liệt v&agrave; Hib.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng n&ecirc;n ti&ecirc;m vắc xin ho g&agrave; cho người c&oacute; rối loạn thần kinh kh&ocirc;ng kiểm so&aacute;t hoặc động kinh kh&ocirc;ng kiểm so&aacute;t cho đến khi bệnh được điều trị, bệnh ổn định v&agrave; lợi &iacute;ch r&otilde; r&agrave;ng vượt trội nguy cơ.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ bị dị ứng với một trong c&aacute;c th&agrave;nh phần của vắc xin hay với vắc xin ho g&agrave; (v&ocirc; b&agrave;o hoặc nguy&ecirc;n b&agrave;o), hay trước đ&acirc;y trẻ đ&atilde; c&oacute; phản ứng dị ứng sau khi ti&ecirc;m vắc xin c&oacute; chứa c&aacute;c chất tương tự.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ c&oacute; bệnh n&atilde;o tiến triển hoặc tổn thương ở n&atilde;o.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">&ndash; Như tất cả c&aacute;c vắc xin d&ugrave;ng đường ti&ecirc;m kh&aacute;c, phải thận trọng khi sử dụng vắc xin n&agrave;y cho người bị giảm tiểu cầu hay bị rối loạn đ&ocirc;ng m&aacute;u v&igrave; c&oacute; thể bị chảy m&aacute;u sau khi ti&ecirc;m bắp.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">&ndash; Trong vắc xin c&oacute; một lượng rất nhỏ glutaraldehyde, neomycin, streptomycin v&agrave; polymycin B, v&igrave; vậy h&atilde;y cẩn trọng đối với những trẻ c&oacute; tiền sử dị ứng với một trong c&aacute;c th&agrave;nh phần n&agrave;y.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">&ndash; Cần c&acirc;n nhắc cẩn thận khi quyết định d&ugrave;ng tiếp c&aacute;c liều vắc xin chữa ho g&agrave; nếu đ&atilde; từng c&oacute; bất kỳ một trong c&aacute;c triệu chứng sau đ&acirc;y xảy ra :</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Sốt &ge; 40<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C trong v&ograve;ng 48 giờ sau khi ti&ecirc;m, m&agrave; kh&ocirc;ng phải do một nguy&ecirc;n nh&acirc;n x&aacute;c định n&agrave;o kh&aacute;c.</li>
<li style="box-sizing: border-box; text-align: justify;">Trụy mạch hay trạng th&aacute;i giống sốc (giảm trương lực- giảm cơ đ&aacute;p ứng) trong v&ograve;ng 48 giờ sau khi ti&ecirc;m ngừa.</li>
<li style="box-sizing: border-box; text-align: justify;">Quấy kh&oacute;c dai dẳng, k&eacute;o d&agrave;i &ge; 3 giờ, xảy ra trong v&ograve;ng 48 giờ sau khi ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Co giật c&oacute; k&egrave;m theo sốt hay kh&ocirc;ng sốt, xảy ra trong v&ograve;ng 3 ng&agrave;y sau khi ti&ecirc;m.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">&ndash; Nếu trẻ đang/đ&atilde; c&oacute; vấn đề sức khỏe hay bị dị ứng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">&ndash; Nếu trước đ&acirc;y, sau khi ti&ecirc;m vắc xin c&oacute; chứa giải độc tố uốn v&aacute;n (vắc xin uốn v&aacute;n)&nbsp; trẻ bị hội hội chứng Guillain-Barr&eacute; (nhạy cảm bất thường, liệt) hay vi&ecirc;m d&acirc;y thần kinh c&aacute;nh tay (liệt, đau lan tỏa ở c&aacute;nh tay v&agrave; vai), b&aacute;c sĩ sẽ quyết định c&oacute; tiếp tục d&ugrave;ng vắc xin c&oacute; chứa giải độc tố uốn v&aacute;n nữa hay kh&ocirc;ng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">&ndash; Nếu trẻ đang được điều trị bằng thuốc ức chế miễn dịch hoặc bị suy giảm miễn dịch, th&igrave; đ&aacute;p ứng miễn dịch đối với vắc xin sẽ bị giảm.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng thường gặp nhất l&agrave; đau chỗ ti&ecirc;m, dễ k&iacute;ch động, quấy kh&oacute;c, tại nơi ti&ecirc;m c&oacute; nổi quầng đỏ. C&aacute;c dấu hiệu v&agrave; triệu chứng n&agrave;y thường gặp trong v&ograve;ng 48 giờ sau khi ti&ecirc;m v&agrave; c&oacute; thể k&eacute;o d&agrave;i 48-72 giờ. Ch&uacute;ng thường tự khỏi m&agrave; kh&ocirc;ng cần điều trị đặc hiệu.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng to&agrave;n th&acirc;n: sốt, dễ k&iacute;ch động, buồn ngủ, ch&aacute;n ăn, ti&ecirc;u chảy, &oacute;i mửa, quấy kh&oacute;c k&eacute;o d&agrave;i. Hiếm hơn, c&oacute; thể thấy nổi mề đay, ph&aacute;t ban ngo&agrave;i da, co giật k&egrave;m sốt hoặc kh&ocirc;ng k&egrave;m sốt trong v&ograve;ng 48 giờ sau khi ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Sau khi ti&ecirc;m c&aacute;c vắc xin chứa th&agrave;nh phần Haemophilus influenzae t&yacute;p b, phản ứng sưng ph&ugrave; chi dưới cũng đ&atilde; được b&aacute;o c&aacute;o. Những phản ứng n&agrave;y khởi ph&aacute;t trong 24-72 giờ sau ti&ecirc;m, đ&ocirc;i khi đi k&egrave;m với sốt, đau v&agrave; quấy kh&oacute;c v&agrave; tự khỏi trong 3-5 ng&agrave;y.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Nếu đang d&ugrave;ng thuốc hoặc muốn ti&ecirc;m c&ugrave;ng với c&aacute;c vắc xin kh&aacute;c v&agrave;o c&ugrave;ng một thời điểm, h&atilde;y hỏi &yacute; kiến của b&aacute;c sĩ hoặc dược sĩ.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Hexaxim n&ecirc;n được bảo quản ở nhiệt độ từ 20C đến 80C , kh&ocirc;ng được để đ&ocirc;ng băng.</li>
<li style="box-sizing: border-box; text-align: justify;">Giữ vắc xin trong hộp để tr&aacute;nh &aacute;nh s&aacute;ng.</li>
</ul>', '2021-07-01 03:38:40', '2021-07-03 19:23:20', 10, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (6, 'VẮC XIN 5 TRONG 1', 0, null, null, null, null, null, '2021-07-01 03:38:40', '2021-07-01 03:38:40', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (7, 'Pentaxim', 6, 'Đang sử dụng', 200, '/storage/vaccine_photo/PW3rR6GTLrZNNvHZKqhk.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Theo Tổ chức Y tế Thế giới (WHO), bạch hầu, ho g&agrave;, uốn v&aacute;n, bại liệt v&agrave; vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o do vi khuẩn Hib l&agrave; 5 bệnh trẻ dễ mắc phải &ndash; đặc biệt trong những th&aacute;ng đầu đời, c&oacute; thể để lại hậu quả v&ocirc; c&ugrave;ng nặng nề như suy dinh dưỡng, chậm ph&aacute;t triển về tr&iacute; tuệ, tiếp thu k&eacute;m, thậm ch&iacute; tử vong.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về vắc xin 5 trong 1 Pentaxim (Ph&aacute;p)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Bệnh bạch hầu, ho g&agrave;, uốn v&aacute;n, bại liệt v&agrave; vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o do vi khuẩn Hib c&oacute; thể t&aacute;i đi t&aacute;i lại nhiều lần do cơ thể sẽ kh&ocirc;ng thể tự sinh ra kh&aacute;ng thể miễn dịch tự nhi&ecirc;n sau khi bị mắc bệnh. Bệnh c&oacute; khả năng g&acirc;y tử vong cao hoặc để lại những di chứng nặng nề như t&agrave;n tật, di chứng thần kinh, tổn thương n&atilde;o&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Kể từ khi vắc xin 5 trong 1 được sử dụng ti&ecirc;m cho trẻ nhỏ, số ca tử vong do c&aacute;c bệnh n&agrave;y đ&atilde; giảm đ&aacute;ng kể. Đặc biệt, ưu điểm của vacxin 5 trong 1 l&agrave; ph&ograve;ng được 5 bệnh trong 1 mũi ti&ecirc;m n&ecirc;n hạn chế được số lần ti&ecirc;m cho trẻ, gi&uacute;p tiết kiệm được thời gian, chi ph&iacute; v&agrave; bảo vệ trẻ sớm trong những năm đầu đời. Vắc xin 5 trong 1 Pentaxim (Ph&aacute;p) l&agrave; lựa chọn được nhiều phụ huynh tin tưởng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Sanofi Pasteur (Ph&aacute;p)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Ph&ograve;ng bệnh</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Pentaxim được chỉ định ti&ecirc;m chủng ph&ograve;ng c&aacute;c bệnh: Ho g&agrave;, Bạch hầu, Uốn v&aacute;n, Bại liệt v&agrave; c&aacute;c bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o do H.influenzae tu&yacute;p B.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc-xin n&agrave;y kh&ocirc;ng c&oacute; t&aacute;c dụng ph&ograve;ng nhiễm khuẩn do c&aacute;c tu&yacute;p Haemophilus influenzae kh&aacute;c hoặc ph&ograve;ng vi&ecirc;m m&agrave;ng n&atilde;o do c&aacute;c loại vi sinh vật kh&aacute;c g&acirc;y ra.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Gồm 3 mũi được ti&ecirc;m v&agrave;o c&aacute;c th&aacute;ng thứ 2, 3, 4 hoặc 3, 4, 5 hoặc 2, 4, 6. Khoảng c&aacute;ch giữa c&aacute;c mũi ti&ecirc;m tối thiểu l&agrave; 1 th&aacute;ng. Mũi ti&ecirc;m nhắc lại tốt nhất l&agrave; v&agrave;o th&aacute;ng thứ 16.</li>
<li style="box-sizing: border-box; text-align: justify;">Để biết th&ecirc;m th&ocirc;ng tin chi tiết lịch ti&ecirc;m chủng cho trẻ em, h&atilde;y xem qua b&agrave;i viết n&agrave;y</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp (ở mặt trước-b&ecirc;n đ&ugrave;i).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Chống chỉ định đối với những trường hợp mẫn cảm với c&aacute;c hoạt chất hay bất cứ t&aacute; dược hoặc chất tồn dư n&agrave;o trong thuốc.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ bị dị ứng với một trong c&aacute;c th&agrave;nh phần của vắc xin hay với vắc xin ho g&agrave; (v&ocirc; b&agrave;o hoặc nguy&ecirc;n b&agrave;o), hay trước đ&acirc;y trẻ đ&atilde; c&oacute; phản ứng dị ứng sau khi ti&ecirc;m vắc xin c&oacute; chứa c&aacute;c chất tương tự.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ c&oacute; bệnh n&atilde;o tiến triển hoặc tổn thương ở n&atilde;o.</li>
<li style="box-sizing: border-box; text-align: justify;">Nếu lần trước trẻ từng bị bệnh n&atilde;o (tổn thương ở n&atilde;o) trong v&ograve;ng 7 ng&agrave;y sau khi ti&ecirc;m vắc xin ho g&agrave; (ho g&agrave; v&ocirc; b&agrave;o hay nguy&ecirc;n b&agrave;o).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">&ndash; C&oacute; nguy cơ bị chảy m&aacute;u khi ti&ecirc;m bắp nếu trẻ bị giảm tiểu cầu hay bị rối loạn đ&ocirc;ng m&aacute;u.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">&ndash; Trong vắc xin c&oacute; một lượng rất nhỏ glutaraldehyde, neomycin, streptomycin v&agrave; polymycin B, v&igrave; vậy h&atilde;y cẩn trọng đối với những trẻ c&oacute; tiền sử dị ứng với một trong c&aacute;c th&agrave;nh phần n&agrave;y.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">&ndash; Cần c&acirc;n nhắc cẩn thận khi quyết định d&ugrave;ng tiếp c&aacute;c liều vắc xin chứa ho g&agrave; nếu đ&atilde; từng c&oacute;&nbsp; bất kỳ một trong c&aacute;c triệu chứng sau đ&acirc;y xảy ra :</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Sốt &ge; 40<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C trong v&ograve;ng 48 giờ sau khi ti&ecirc;m, m&agrave; kh&ocirc;ng phải do một nguy&ecirc;n nh&acirc;n x&aacute;c định n&agrave;o kh&aacute;c.</li>
<li style="box-sizing: border-box; text-align: justify;">Trụy mạch hay giống sốc với giai đoạn giảm trương lực- giảm đ&aacute;p ứng trong v&ograve;ng 38 giờ sau khi ti&ecirc;m ngừa.</li>
<li style="box-sizing: border-box; text-align: justify;">Quấy kh&oacute;c dai dẳng, k&eacute;o d&agrave;i &ge; 3 giờ, xảy ra trong v&ograve;ng 48 giờ sau khi ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Co giật c&oacute; k&egrave;m theo sốt hay kh&ocirc;ng sốt, xảy ra trong v&ograve;ng 3 ng&agrave;y sau khi ti&ecirc;m.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">&ndash; Nếu trẻ đang/đ&atilde; c&oacute; vấn đề sức khỏe hay bị dị ứng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">&ndash; Nếu trước đ&acirc;y, sau khi ti&ecirc;m vắc xin c&oacute; chứa giải độc tố uốn v&aacute;n (vắc xin uốn v&aacute;n) trẻ bị hội hội chứng Guillain-Barr&eacute; (nhạy cảm bất thường, liệt) hay vi&ecirc;m d&acirc;y thần kinh c&aacute;nh tay (liệt, đau lan tỏa ở c&aacute;nh tay v&agrave; vai), b&aacute;c sĩ sẽ quyết định c&oacute; tiếp tục d&ugrave;ng vắc xin c&oacute; chứa giải độc tố uốn v&aacute;n nữa hay kh&ocirc;ng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">&ndash; Nếu trẻ đang được điều trị bằng thuốc ức chế miễn dịch hoặc bị suy giảm miễn dịch, th&igrave; đ&aacute;p ứng miễn dịch đối với vắc xin sẽ bị giảm.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng thường gặp nhất l&agrave; bị k&iacute;ch th&iacute;ch, tại nơi ti&ecirc;m c&oacute; nổi quầng đỏ, nốt cứng lớn hơn 2cm. C&aacute;c dấu hiệu v&agrave; triệu chứng n&agrave;y thường gặp trong v&ograve;ng 48 giờ sau khi ti&ecirc;m v&agrave; c&oacute; thể k&eacute;o d&agrave;i 48-72 giờ. Ch&uacute;ng thường tự khỏi m&agrave; kh&ocirc;ng cần điều trị đặc hiệu.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng to&agrave;n th&acirc;n: sốt, dễ k&iacute;ch động, buồn ngủ, rối loạn giấc ngủ v&agrave; ăn uống, ti&ecirc;u chảy, &oacute;i mửa, kh&oacute;c nh&egrave; kh&oacute; dỗ v&agrave; k&eacute;o d&agrave;i. Hiếm hơn, c&oacute; thể thấy nổi mề đay, ph&aacute;t ban ngo&agrave;i da, co giật k&egrave;m sốt hoặc kh&ocirc;ng k&egrave;m sốt trong v&ograve;ng 48 giờ sau khi ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">T&igrave;nh trạng giảm trương lực cơ hoặc c&aacute;c đợt giảm trương lực cơ-giảm phản ứng đ&atilde; được b&aacute;o c&aacute;o.</li>
<li style="box-sizing: border-box; text-align: justify;">Sau khi ti&ecirc;m c&aacute;c vắc xin chứa th&agrave;nh phần Haemophilus influenzae t&yacute;p b, phản ứng sưng ph&ugrave; chi dưới cũng đ&atilde; được b&aacute;o c&aacute;o. Những phản ứng n&agrave;y đ&ocirc;i khi đi k&egrave;m với sốt, đau v&agrave; quấy kh&oacute;c.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Nếu đang d&ugrave;ng thuốc hoặc muốn ti&ecirc;m c&ugrave;ng với c&aacute;c vắc xin kh&aacute;c v&agrave;o c&ugrave;ng một thời điểm, h&atilde;y hỏi &yacute; kiến của b&aacute;c sĩ hoặc dược sĩ.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Pentaxim n&ecirc;n được bảo quản ở nhiệt độ từ 2 độ C đến 8 độ C v&agrave; kh&ocirc;ng được để đ&ocirc;ng băng.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin phải được ho&agrave;n nguy&ecirc;n trước khi ti&ecirc;m, tạo n&ecirc;n hỗn dịch m&agrave;u trắng đục. Sau khi ho&agrave;n nguy&ecirc;n n&ecirc;n sử dụng ngay.</li>
</ul>', '2021-07-01 03:42:03', '2021-07-03 19:24:54', 8, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (8, 'Infanrix IPV+Hib', 6, 'Đang sử dụng', 115, '/storage/vaccine_photo/Pa42ZJ0CqKcL0POsJEtd.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Tổ chức Y tế Thế giới (WHO) khuyến c&aacute;o, Bạch hầu, Ho g&agrave;, Uốn v&aacute;n, Bại liệt v&agrave; c&aacute;c bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o do vi khuẩn Hib đ&atilde; v&agrave; đang đe dọa cướp đi mạng sống của h&agrave;ng triệu trẻ em tr&ecirc;n thế giới, đặc biệt ở trẻ trong những th&aacute;ng đầu đời. C&aacute;c bệnh nguy hiểm n&agrave;y c&oacute; thể để lại hậu quả v&ocirc; c&ugrave;ng nặng nề như: suy dinh dưỡng, tiếp thu k&eacute;m, chậm ph&aacute;t triển về tr&iacute; tuệ,&hellip; thậm ch&iacute; tử vong. Ti&ecirc;m vắc xin l&agrave; biện ph&aacute;p ph&ograve;ng bệnh đơn giản, hữu hiệu v&agrave; an to&agrave;n nhất cho trẻ.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về vắc xin 5 trong 1 Infanrix IPV+Hib (Bỉ)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin 5 trong 1 Infanrix IPV+Hib l&agrave; vắc xin phối hợp được chỉ định ti&ecirc;m chủng ph&ograve;ng c&aacute;c bệnh: Bạch hầu, Ho g&agrave;, Uốn v&aacute;n, Bại liệt v&agrave; c&aacute;c bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o do H.influenzae tu&yacute;p B (Hib).</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Điểm ưu việt của vắc xin 5 trong 1 l&agrave; ph&ograve;ng được 5 bệnh chỉ trong 1 mũi ti&ecirc;m n&ecirc;n gi&uacute;p hạn chế được số lần ti&ecirc;m, hạn chế tối đa đau đớn cho trẻ khi phải ti&ecirc;m nhiều mũi, tiết kiệm được thời gian, chi ph&iacute; v&agrave; bảo vệ trẻ sớm trong những năm đầu đời.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">GlaxoSmithKline (Bỉ)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin Infanrix IPV+Hib. c&oacute; lịch ti&ecirc;m 3 mũi, được ti&ecirc;m trong 6 th&aacute;ng đầu đời v&agrave; c&oacute; thể bắt đầu từ 2 th&aacute;ng tuổi. Khoảng c&aacute;ch giữa c&aacute;c mũi ti&ecirc;m c&aacute;ch nhau &iacute;t nhất 1 th&aacute;ng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Lịch ti&ecirc;m ti&ecirc;u chuẩn được khuyến c&aacute;o như sau:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n (khi trẻ từ 2 th&aacute;ng tuổi).</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: c&aacute;ch mũi 1 &iacute;t nhất 1 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: C&aacute;ch mũi 2 &iacute;t nhất 1 th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><em style="box-sizing: border-box;">Mũi nhắc lại</em>: Được khuyến c&aacute;o ti&ecirc;m cho trẻ v&agrave;o năm tuổi thứ 2 v&agrave; c&aacute;ch mũi ti&ecirc;m cuối c&ugrave;ng trong lịch ti&ecirc;m cơ bản &iacute;t nhất 6 th&aacute;ng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp s&acirc;u (ở mặt trước &ndash; b&ecirc;n đ&ugrave;i).</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin 5 trong 1 Infanrix IPV+Hib được chỉ định ti&ecirc;m cho trẻ từ 2 th&aacute;ng tuổi &ndash; &lt;5 tuổi.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Trẻ qu&aacute; mẫn cảm với bất kỳ th&agrave;nh phần n&agrave;o của vắc-xin Infanrix IPV+Hib.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ đ&atilde; từng c&oacute; phản ứng qu&aacute; mẫn sau mũi ti&ecirc;m vắc xin Bạch hầu, Ho g&agrave;, Uốn v&aacute;n, Bại liệt bất hoạt hoặc Hib trước đ&oacute;.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ mắc c&aacute;c bệnh về n&atilde;o nhưng kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n, xảy ra trong v&ograve;ng 7 ng&agrave;y sau khi ti&ecirc;m vắc xin ho g&agrave; trước đ&oacute;.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng khuyến c&aacute;o sử dụng cho người lớn, trẻ vị th&agrave;nh ni&ecirc;n v&agrave; trẻ em từ 5 tuổi trở l&ecirc;n.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Ho&atilde;n ti&ecirc;m vắc xin cho c&aacute;c đối tượng đang sốt.</li>
<li style="box-sizing: border-box; text-align: justify;">Rối loạn thần kinh tiến triển bao gồm chứng co thắt, động kinh kh&ocirc;ng kiểm so&aacute;t hoặc bệnh n&atilde;o tiến triển (ho&atilde;n ti&ecirc;m cho đến khi bệnh khỏi hoặc ổn định, cần c&acirc;n nhắc yếu tố nguy cơ/ lợi &iacute;ch).</li>
<li style="box-sizing: border-box; text-align: justify;">Thận trọng khi ti&ecirc;m vắc xin cho những đối tượng bị giảm tiểu cầu hoặc rối loạn m&aacute;u chảy m&aacute;u v&igrave; c&oacute; thể g&acirc;y chảy m&aacute;u sau khi ti&ecirc;m bắp ở những đối tượng n&agrave;y.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể ti&ecirc;m vắc xin Infanrix IPV + Hib đồng thời với vắc xin: Vi&ecirc;m gan B, Phế cầu khuẩn, Rotavirus v&agrave; một số loại vắc xin kh&aacute;c.</li>
<li style="box-sizing: border-box; text-align: justify;">N&ecirc;n ti&ecirc;m vắc xin Infanrix IPV + Hib đ&atilde; ho&agrave;n nguy&ecirc;n v&agrave; vắc xin ti&ecirc;m kh&aacute;c ở những vị tr&iacute; kh&aacute;c nhau.</li>
<li style="box-sizing: border-box; text-align: justify;">Cũng như vắc xin kh&aacute;c, vắc xin Infanrix IPV + Hib c&oacute; thể kh&ocirc;ng đạt được đ&aacute;p ứng miễn dịch như mong đợi ở những bệnh nh&acirc;n bị suy giảm miễn dịch hoặc đang điều trị ức chế miễn dịch.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng thường gặp: đau, đỏ, sưng tại vị tr&iacute; ti&ecirc;m. C&aacute;c dấu hiệu v&agrave; triệu chứng n&agrave;y thường gặp trong v&ograve;ng 48 giờ sau khi ti&ecirc;m, thường tự khỏi m&agrave; kh&ocirc;ng cần điều trị đặc hiệu.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng to&agrave;n th&acirc;n: c&oacute; thể sốt, dễ k&iacute;ch th&iacute;ch, buồn ngủ, rối loạn giấc ngủ v&agrave; ăn uống, ti&ecirc;u chảy, n&ocirc;n, quấy kh&oacute;c, bứt rứt.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin Infanrix IPV+Hib được bảo quản ở nhiệt độ 2<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C đến 8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C. Kh&ocirc;ng được để th&agrave;nh phần Infanrix IPV đ&ocirc;ng băng. Tr&aacute;nh &aacute;nh s&aacute;ng.</p>', '2021-07-01 03:42:03', '2021-07-03 19:26:18', 8, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (9, 'PHÒNG TIÊU CHẢY CẤP DO ROTA VIRUS', 0, null, null, null, null, null, '2021-07-01 03:42:03', '2021-07-01 03:42:03', 5, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (10, 'Rotateq', 9, 'Đang sử dụng', 126, '/storage/vaccine_photo/QMIt68kigEVUPB64mbR0.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Ti&ecirc;u chảy cấp do virus Rota l&agrave; bệnh cấp t&iacute;nh do virus g&acirc;y n&ecirc;n. Bệnh thường gặp ở trẻ nhỏ với biểu hiện: n&ocirc;n &oacute;i, đi ngo&agrave;i ph&acirc;n lỏng, đau bụng, dẫn tới mất nước, rối loạn điện giải g&acirc;y trụy mạch v&agrave; tử vong nhanh.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Theo thống k&ecirc;, h&agrave;ng năm tr&ecirc;n thế giới c&oacute; khoảng 1,5 tỷ lượt trẻ dưới 5 tuổi mắc ti&ecirc;u chảy v&agrave; 4 triệu trẻ chết v&igrave; bệnh n&agrave;y, trong đ&oacute; 80% xảy ra ở trẻ dưới 2 tuổi.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Tại Việt Nam, c&oacute; đến 56% trẻ nhập viện do vi&ecirc;m dạ d&agrave;y ruột l&agrave; do nhiễm virus Rota, trong đ&oacute;, số trẻ chết do vi r&uacute;t Rota chiếm từ 4-8% trong tổng số trẻ dưới 5 tuổi bị chết v&igrave; mọi nguy&ecirc;n nh&acirc;n.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Uống vắc xin l&agrave; c&aacute;ch duy nhất gi&uacute;p trẻ ph&ograve;ng ngừa căn bệnh ti&ecirc;u chảy cấp g&acirc;y ra do Rotavirus. C&aacute;c chuy&ecirc;n gia khuyến c&aacute;o, trẻ n&ecirc;n uống vắc xin Rotateq (Mỹ) sớm để ph&ograve;ng bệnh hiệu quả.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về vắc xin Rotateq (Mỹ)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin Rotateq được chỉ định cho trẻ từ 6 tuần tuổi ph&ograve;ng vi&ecirc;m dạ d&agrave;y &ndash; ruột v&agrave; c&aacute;c bệnh do virus Rota thuộc tu&yacute;p huyết thanh G1, G2, G3, G4, G9 g&acirc;y ra. Lịch tr&igrave;nh uống vắc xin của trẻ phải kết th&uacute;c trước tuần thứ 32.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Meck Sharp and Dohme (Mỹ)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Rotateq được chỉ định để ph&ograve;ng c&aacute;c bệnh do virus Rota thuộc c&aacute;c t&yacute;p huyết thanh G1, G2, G3, G4, G9 g&acirc;y ra. Đ&acirc;y l&agrave; những chủng virus chiếm tỷ lệ lớn g&acirc;y ra bệnh vi&ecirc;m dạ d&agrave;y ruột ở trẻ nhỏ.</li>
<li style="box-sizing: border-box; text-align: justify;">Rotateq kh&ocirc;ng c&oacute; chỉ định ở người lớn n&ecirc;n kh&ocirc;ng d&ugrave;ng cho phụ nữ c&oacute; thai v&agrave; cho con b&uacute;.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch uống:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Liều 1: c&oacute; thể bắt đầu khi trẻ được 7.5 tuần tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Liều 2: sau liều 1 l&agrave; 4 tuần.</li>
<li style="box-sizing: border-box; text-align: justify;">Liều 3: sau liều 2 l&agrave; 4 tuần.</li>
<li style="box-sizing: border-box; text-align: justify;">Lịch tr&igrave;nh uống vắc xin của trẻ phải kết th&uacute;c trước tuần thứ 32.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Chỉ d&ugrave;ng đường uống. Kh&ocirc;ng được ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Nếu trẻ bị n&ocirc;n trớ hoặc nhổ ra th&igrave; kh&ocirc;ng được uống liều thay thế v&igrave; chưa c&oacute; nghi&ecirc;n cứu l&acirc;m s&agrave;ng cho việc uống thay thế. Cứ d&ugrave;ng liều tiếp theo trong lịch uống vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin được đ&oacute;ng trong tu&yacute;p định liều c&oacute; thể vặn nắp v&agrave; cho uống lu&ocirc;n, kh&ocirc;ng được pha lo&atilde;ng bằng nước hoặc sữa.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng d&ugrave;ng vắc xin cho trẻ khi c&oacute; dị ứng với bất kỳ th&agrave;nh phần n&agrave;o trong vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng d&ugrave;ng liều tiếp theo nếu trẻ c&oacute; biểu hiện mẫn cảm với lần uống vắc xin Rotateq trước.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng d&ugrave;ng vắc xin Rotateq cho trẻ suy giảm miễn dịch kết hợp trầm trọng, v&igrave; đ&atilde; c&oacute; b&aacute;o c&aacute;o về trường hợp vi&ecirc;m dạ d&agrave;y ruột khi d&ugrave;ng vắc xin ở trẻ suy giảm miễn dịch kết hợp trầm trọng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Đối với những trẻ đang c&oacute; bệnh dạ d&agrave;y ruột tiến triển, ti&ecirc;u chảy mạn t&iacute;nh hoặc chậm ph&aacute;t triển, c&oacute; bệnh l&yacute; bẩm sinh như lồng ruột th&igrave; việc c&acirc;n nhắc giữa lợi &iacute;ch v&agrave; nguy cơ phải được b&aacute;c sĩ đ&aacute;nh gi&aacute;.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể tạm ho&atilde;n việc sử dụng vắc xin Rotateq nếu trẻ đang sốt hoặc nhiễm tr&ugrave;ng cấp t&iacute;nh. Tuy nhi&ecirc;n t&igrave;nh trạng sốt v&agrave; nhiễm tr&ugrave;ng ở thể nhẹ kh&ocirc;ng phải l&agrave; chống chỉ định của Rotateq.</li>
<li style="box-sizing: border-box; text-align: justify;">Cũng như c&aacute;c vắc xin kh&aacute;c, việc ph&ograve;ng ngừa bằng Rotateq kh&ocirc;ng đảm bảo ph&ograve;ng tr&aacute;nh bệnh ho&agrave;n to&agrave;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Rotateq đ&atilde; được chứng minh hiệu quả khi d&ugrave;ng cho trẻ từ 6 tuần đến 32 tuần tuổi. Kh&ocirc;ng c&oacute; dữ liệu l&acirc;m s&agrave;ng về t&iacute;nh hiệu quả v&agrave; độ an to&agrave;n khi d&ugrave;ng cho trẻ dưới 6 tuần tuổi.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Hay gặp: n&ocirc;n, ti&ecirc;u chảy, sốt nhẹ (&gt;38<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">0</span>C).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Rotateq c&oacute; thể d&ugrave;ng c&ugrave;ng thời điểm với c&aacute;c vắc xin đơn gi&aacute; v&agrave; vắc xin kết hợp kh&aacute;c như vắc xin ho g&agrave; &ndash; bạch hầu &ndash; uốn v&aacute;n, vắc xin bại liệt bất hoạt, vắc xin vi&ecirc;m gan B, vắc xin cộng hợp phế cầu khuẩn, vắc xin ph&ograve;ng c&aacute;c bệnh do Hib.</li>
<li style="box-sizing: border-box; text-align: justify;">Rotateq c&oacute; thể d&ugrave;ng c&ugrave;ng với vắc xin ph&ograve;ng bại liệt dạng uống OPV m&agrave; kh&ocirc;ng l&agrave;m ảnh hưởng đến hiệu lực của OPV. Mặc d&ugrave; OPV l&agrave;m giảm một v&agrave;i đ&aacute;p ứng miễn dịch của Rotateq nhưng theo nghi&ecirc;n cứu cho thấy kh&ocirc;ng l&agrave;m ảnh hưởng tới khả năng chống lại virus Rota. Đ&aacute;p ứng miễn dịch của Rotateq&nbsp; kh&ocirc;ng bị ảnh hưởng nếu uống vắc xin bại liệt OPV sau 2 tuần kể từ khi uống Rotateq.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Rotateq được bảo quản ở nhiệt độ 2-8 độ C. Sau khi lấy ra khỏi tủ lạnh, vắc xin cần được sử dụng ngay. Khi bảo quản ở nhiệt độ 25 độ C, vắc xin Rotateq c&oacute; thể sử dụng trong v&ograve;ng 48 giờ. Sau 48 giờ vắc xin cần phải loại bỏ theo quy định.</li>
</ul>', '2021-07-01 03:45:50', '2021-07-03 19:37:26', 5, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (11, 'Rotarix', 9, 'Đang sử dụng', 126, '/storage/vaccine_photo/zyejxecbLuBy8VqCDVwD.jpeg', null, '<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">ược ở tất cả c&aacute;c trẻ uống vắc xin. Rotarix kh&ocirc;ng ngừa được c&aacute;c trường hợp vi&ecirc;m dạ d&agrave;y ruột m&agrave; kh&ocirc;ng phải do Rotavirus.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Thường gặp: bị k&iacute;ch th&iacute;ch, ăn k&eacute;m hơn do mất cảm gi&aacute;c ngon miệng, c&oacute; thể c&oacute; n&ocirc;n trớ, đau bụng, đầy hơi hoặc đi ngo&agrave;i.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Rotarix c&oacute; thể d&ugrave;ng c&ugrave;ng thời điểm với c&aacute;c vắc xin đơn gi&aacute; hoặc vắc xin kết hợp kh&aacute;c như: vắc xin ho g&agrave;- bạch hầu- uốn v&aacute;n, vắc xin vi&ecirc;m gan B&hellip;Khi uống c&ugrave;ng với vắc xin bại liệt dạng uống (OPV) th&igrave; kh&ocirc;ng thấy hiệu lực của vắc xin bại liệt bị ảnh hưởng m&agrave; ngược lại c&oacute; sự suy giảm nhẹ đ&aacute;p ứng miễn dịch của vắc xin ph&ograve;ng Rotavirus. Tuy nhi&ecirc;n điều n&agrave;y kh&ocirc;ng l&agrave;m ảnh hưởng nhiều đến khả năng bảo vệ chống lại vi khuẩn g&acirc;y vi&ecirc;m dạ d&agrave;y ruột. Đ&aacute;p ứng miễn dịch của vắc xin ph&ograve;ng Rotavirus kh&ocirc;ng bị ảnh hưởng nếu uống vắc xin OPV sau 2 tuần kể từ khi uống vắc xin Rota virus.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin đ&ocirc;ng kh&ocirc; được bảo quản ở nhiệt độ 2-8 độ C, tr&aacute;nh &aacute;nh s&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Dung m&ocirc;i ho&agrave;n nguy&ecirc;n c&oacute; thể bảo quản ở 2-8 độ C hoặc ở nhiệt độ ph&ograve;ng (&lt;37 độ C).</li>
<li style="box-sizing: border-box; text-align: justify;">Sau khi ho&agrave;n nguy&ecirc;n, vắc xin được sử dụng ngay hoặc bảo quản trong tủ lạnh từ 2-8 độ C trong v&ograve;ng 24 giờ. Sau 24 giờ phải loại bỏ vắc xin đ&atilde; ho&agrave;n nguy&ecirc;n.</li>
</ul>', '2021-07-01 03:45:50', '2021-07-03 20:33:28', 5, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (12, 'Nanocovax', 1, 'Đang thử nghiệm lâm sàng', null, '/storage/vaccine_photo/ZNRAreL4Y58kQ6UAX5uc.jpeg', null, '<div style="box-sizing: border-box; color: #333333; font-family: Roboto, sans-serif; font-size: 18px;"><span style="box-sizing: border-box; text-align: justify;">Vaccine COVID-19 k&iacute;ch th&iacute;ch cơ thể tạo đ&aacute;p ứng miễn dịch chống lại virus SARS-CoV-2 m&agrave; kh&ocirc;ng cần nhiễm bệnh. Với tất cả c&aacute;c loại vaccine, cơ thể sẽ được cung cấp tế b&agrave;o lympho T v&agrave; tế b&agrave;o lympho B ghi nhớ c&aacute;ch chống lại virus để hoạt động khi bị nhiễm bệnh. Hiện nay c&oacute; 3 loại vaccine COVID-19 ch&iacute;nh đang được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển: vaccine mRNA, vaccine tiểu đơn vị protein v&agrave; vaccine v&eacute;c-tơ.</span></div>
<div style="box-sizing: border-box; color: #333333; font-family: Roboto, sans-serif; font-size: 18px; text-align: justify;">&nbsp;</div>
<div style="box-sizing: border-box; color: #333333; font-family: Roboto, sans-serif; font-size: 18px; text-align: justify;">NANOGEN đ&atilde; nghi&ecirc;n cứu v&agrave; sản xuất vaccine t&aacute;i tổ hợp ph&ograve;ng bệnh do SARS-CoV-2 theo hướng vaccine tiểu đơn vị protein, trong đ&oacute; subunit protein S t&aacute;i tổ hợp gắn tr&ecirc;n gi&aacute; thể l&agrave; c&aacute;c hạt nano silica. Thay v&igrave; sử dụng to&agrave;n bộ mầm bệnh, vaccine tiểu đơn vị l&agrave; loại vaccine chỉ sử dụng những mảnh kh&aacute;ng nguy&ecirc;n v&ocirc; hại (protein) của vi sinh vật th&iacute;ch hợp nhất để k&iacute;ch th&iacute;ch tạo đ&aacute;p ứng miễn dịch ph&ugrave; hợp.</div>
<div style="box-sizing: border-box; color: #333333; font-family: Roboto, sans-serif; font-size: 18px; text-align: justify;">&nbsp;</div>
<div style="box-sizing: border-box; color: #333333; font-family: Roboto, sans-serif; font-size: 18px; text-align: justify;">Để tạo ra vaccine sub-unit, domain xuy&ecirc;n m&agrave;ng của protein S bj loại bỏ, protein S được sản xuất bằng c&ocirc;ng nghệ DNA t&aacute;i tổ hợp tr&ecirc;n tế b&agrave;o CHO (tế b&agrave;o buồng trứng của chuột hamster Trung Quốc).&nbsp;</div>
<div class="prod-rela" style="box-sizing: border-box; color: #333333; font-family: Roboto, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;">
<div class="block-cate-prod" style="box-sizing: border-box;">&nbsp;</div>
</div>
<div class="block-collap-prod" style="box-sizing: border-box; margin-bottom: 40px; color: #333333; font-family: Roboto, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: #ffffff; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;">
<div style="box-sizing: border-box; text-align: justify;">S đ&oacute;ng vai tr&ograve; như 1 tiểu thể vận chuyển protein (protein cargo) mang protein kh&aacute;ng nguy&ecirc;n S, M, E, N của SARS-COV-2 để k&iacute;ch th&iacute;ch đ&aacute;p ứng miễn dịch. Adjuvant cũng được sử dụng để tắng cường đ&aacute;p ứng miễn dịch.</div>
<div style="box-sizing: border-box; text-align: justify;">&nbsp;</div>
<div style="box-sizing: border-box; text-align: justify;">Sau hơn 4 th&aacute;ng nghi&ecirc;n cứu, C&ocirc;ng ty NANOGEN đ&atilde; nghi&ecirc;n cứu th&agrave;nh c&ocirc;ng dự tuyển vaccine dựa tr&ecirc;n S-protein (dạng wild type v&agrave; 4 dạng đột biến). Vaccine n&agrave;y hấp phụ v&agrave;o hạt nh&ocirc;m v&agrave; c&oacute; sử dụng adjuvant chuy&ecirc;n biệt để k&iacute;ch đ&aacute;p ứng miễn dịch dịch thể v&agrave; miễn dịch qua trung gian tế b&agrave;o. C&oacute; thể nhược điểm của việc ph&aacute;t triển loại vaccine n&agrave;y l&agrave; vấn đề thời gian v&igrave; phải tạo d&ograve;ng v&agrave; chọn lọc tr&ecirc;n tế b&agrave;o nhưng ưu điểm lớn nhất l&agrave; độ an to&agrave;n cao, c&oacute; khả năng tạo đ&aacute;p ứng miễn dịch tốt.</div>
<div style="box-sizing: border-box;">&nbsp;</div>
<div style="box-sizing: border-box; text-align: justify;">&Ocirc;ng Nguyễn Ng&ocirc; Quang, Ph&oacute; Cục trưởng Khoa học c&ocirc;ng nghệ v&agrave; Đ&agrave;o tạo, Bộ Y tế cho biết vaccine Covid-19 do C&ocirc;ng ty NANOGEN nghi&ecirc;n cứu v&agrave; sản xuất l&agrave; vaccine COVID-19 đầu ti&ecirc;n do Việt Nam sản xuất thử nghiệm tr&ecirc;n người. Vaccine bắt đầu được thử nghiệm tr&ecirc;n người từ th&aacute;ng 12/2020. Thử nghiệm l&acirc;m s&agrave;ng bao gồm 3 giai đoạn, dự kiến giai đoạn một sẽ thử nghiệm tr&ecirc;n 20 người, giai đoạn hai thử nghiệm tr&ecirc;n 600 người v&agrave; giai đoạn ba hơn 10.000 người. Hệ thống nh&agrave; xưởng sẵn c&oacute; của NANOGEN c&oacute; thể sản xuất ngay 50.000 - 100.000 liều/mẻ (20-30 triệu liều/năm) v&agrave; tăng dần l&ecirc;n tới 100 triệu liều/năm để đ&aacute;p ứng nhu cầu trong nước v&agrave; xuất khẩu.</div>
</div>', '2021-07-01 03:53:41', '2021-07-03 19:52:08', 5, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (13, 'Novavax', 1, 'Đang thử nghiệm lâm sàng', null, '/storage/vaccine_photo/Mn1psbNlSnz4YxIEryJu.jpeg', null, '<p><span style="color: #222222; font-family: Roboto-Bold, Arial, Verdana, Helvetica, sans-serif; font-size: 16px;">C&ocirc;ng ty c&ocirc;ng nghệ sinh học Novavax của Mỹ th&ocirc;ng b&aacute;o đ&atilde; bắt đầu thử nghiệm l&acirc;m s&agrave;ng vaccine COVID-19 được đề xuất tr&ecirc;n trẻ em với sự tham gia của 3.000 thanh thiếu ni&ecirc;n từ 12-17 tuổi.<br /></span></p>
<p style="box-sizing: border-box; margin: 20px 0px; color: #222222; font-family: Roboto-Regular, Arial, Verdana, Helvetica, sans-serif; font-size: 16px;">Ph&oacute;ng vi&ecirc;n TTXVN tại Mỹ dẫn th&ocirc;ng b&aacute;o của Novavax cho biết c&aacute;c cuộc thử nghiệm sẽ kiểm tra &ldquo;t&iacute;nh hiệu quả (v&agrave;) an to&agrave;n&rdquo; của loại vaccine được đề xuất. Những người tham gia chương tr&igrave;nh thử nghiệm sẽ được ti&ecirc;m loại vaccine tr&ecirc;n&nbsp;hoặc với giả dược gồm 2 liều v&agrave; c&aacute;ch nhau 21 ng&agrave;y.</p>
<p style="box-sizing: border-box; margin: 20px 0px; color: #222222; font-family: Roboto-Regular, Arial, Verdana, Helvetica, sans-serif; font-size: 16px;">C&aacute;c t&igrave;nh nguyện vi&ecirc;n sẽ được theo d&otilde;i tối đa 2 năm sau khi ti&ecirc;m.<br style="box-sizing: border-box; display: block; margin: 20px 0px; content: &quot;&quot;;" />Hiện vaccine ngừa COVID-19 của Novavax vẫn chưa được cấp ph&eacute;p ở bất kỳ quốc gia n&agrave;o, kể cả loại d&agrave;nh cho người lớn. Tuy nhi&ecirc;n, c&ocirc;ng ty n&agrave;y c&oacute; kế hoạch nộp đơn xin cấp ph&eacute;p khẩn cấp ở Anh trong qu&yacute; II/2021 v&agrave; sau đ&oacute; l&agrave; ở Mỹ.</p>
<p style="box-sizing: border-box; margin: 20px 0px; color: #222222; font-family: Roboto-Regular, Arial, Verdana, Helvetica, sans-serif; font-size: 16px;">Cuối th&aacute;ng 1 vừa qua, Novavax cho biết c&aacute;c thử nghiệm l&acirc;m s&agrave;ng được tiến h&agrave;nh ở Anh với sự tham gia của 15.000 người trưởng th&agrave;nh cho thấy hiệu quả 89,3%.</p>
<p style="box-sizing: border-box; margin: 20px 0px; color: #222222; font-family: Roboto-Regular, Arial, Verdana, Helvetica, sans-serif; font-size: 16px;">Vaccine của Novavax, sử dụng c&ocirc;ng nghệ kh&aacute;c với c&aacute;c loại vaccine được cấp ph&eacute;p tr&ecirc;n thế giới, được điều chế từ protein tạo ra từ tr&igrave;nh tự di truyền của d&ograve;ng virus SARS-CoV-2 đầu ti&ecirc;n. Loại vaccine n&agrave;y c&oacute; thể được bảo quản ở nhiệt độ từ 2 - 8 độ C (35 - 46,4 độ F).</p>
<p style="box-sizing: border-box; margin: 20px 0px; color: #222222; font-family: Roboto-Regular, Arial, Verdana, Helvetica, sans-serif; font-size: 16px;">Được biết, c&aacute;c c&ocirc;ng ty sản xuất vaccine kh&aacute;c như Moderna, Johnson &amp; Johnson v&agrave; Pfizer cũng đang tiến h&agrave;nh thử nghiệm tr&ecirc;n đối tượng trẻ vị th&agrave;nh ni&ecirc;n. Việc cấp ph&eacute;p ti&ecirc;m&nbsp;vaccine cho trẻ em được coi l&agrave; một bước quan trọng để đạt được miễn dịch cộng đồng.</p>
<p style="box-sizing: border-box; margin: 20px 0px; color: #222222; font-family: Roboto-Regular, Arial, Verdana, Helvetica, sans-serif; font-size: 16px;">C&ugrave;ng ng&agrave;y, Cơ quan Quản l&yacute; Dược phẩm ch&acirc;u &Acirc;u (EMA) cho biết đ&atilde; bắt đầu đ&aacute;nh gi&aacute; việc sử dụng vaccine của Pfizer/BioNTech với trẻ em từ 12-15 tuổi, sau khi c&oacute; y&ecirc;u cầu tương tự ở Mỹ../.</p>', '2021-07-01 03:53:41', '2021-07-03 19:33:08', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (14, 'Pfizer-BioNTech', 1, 'Đang thử nghiệm lâm sàng', null, '/storage/vaccine_photo/q8QmeP6oamRWBrlB4QHB.jpg', null, null, '2021-07-01 03:53:41', '2021-07-04 22:06:23', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (15, 'Moderna', 1, 'Chưa được sử dụng tại Việt Nam', null, '/storage/vaccine_photo/C06jpErnK95DyabBFdCG.jpg', null, null, '2021-07-01 03:53:41', '2021-07-04 22:06:36', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (16, 'PHÒNG BỆNH UNG THƯ CỔ TỬ CUNG', 0, null, null, null, 'Chỉ dành cho giới tính nữ', null, '2021-07-01 03:56:08', '2021-07-01 03:56:08', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (17, 'Gardasil', 16, 'Đang sử dụng', 126, '/storage/vaccine_photo/OHEma8SzSqd2GNarFOAr.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Ung thư cổ tử cung là căn bệnh thường gặp đứng thứ 3 ở phụ nữ Việt Nam từ độ tu&ocirc;̉i 15 &ndash; 44. 80% phụ nữ bị nhi&ecirc;̃m HPV ít nh&acirc;́t 1 l&acirc;̀n trong đời. M&ocirc;̃i ngày có 7 phụ nữ tử vong vì ung thư c&ocirc;̉ tử cung, 14 trường hợp mới được ch&acirc;̉n đoán mắc bệnh.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Kh&ocirc;ng phải ai nhiễm HPV cũng sẽ bị ung thư cổ tử cung, tuy nhi&ecirc;n, c&oacute; hơn 90% c&aacute;c trường hợp ung thư cổ tử cung ph&aacute;t hiện do virus HPV g&acirc;y ra. Chỉ t&iacute;nh ri&ecirc;ng tại Việt Nam, gần 5.000 phụ nữ mắc ung thư cổ tử cung, hơn 2.000 trường hợp tử vong v&igrave; ung thư cổ tử cung trong năm 2018</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Virus HPV c&oacute; thể l&acirc;y truyền qua nhiều đường như sinh nở, dụng cụ cắt m&oacute;ng tay, đồ l&oacute;t v&agrave; đặc biệt l&agrave; quan hệ t&igrave;nh dục. 50% người c&oacute; hoạt động t&igrave;nh dục c&oacute; nguy cơ nhiễm virus HPV cao v&agrave;o một thời điểm nhất định. Tức l&agrave; trong suốt cuộc sống vợ chồng người phụ nữ c&oacute; khả năng nhiễm HPV đến 80% m&agrave; HPV lại l&agrave; s&aacute;t thủ g&acirc;y ra căn bệnh ung thư cổ tử cung.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin Gardasil (Mỹ)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Được sản xuất tại Mỹ, Vắc xin Gardasil c&oacute; khả năng ph&ograve;ng ngừa 4 tu&yacute;p virus HPV nguy cơ cao g&acirc;y ung thư cổ tử cung (6, 11, 16 v&agrave; 18) v&agrave; được chỉ định ti&ecirc;m cho phụ nữ từ 9 &ndash; 26 tuổi. Ngo&agrave;i t&aacute;c dụng ph&ograve;ng bệnh ung thư cổ tử cung, vắc xin n&agrave;y c&ograve;n c&oacute; thể ph&ograve;ng c&aacute;c bệnh ung thư &acirc;m hộ, ung thư &acirc;m đạo, mụn c&oacute;c sinh dục v&agrave; ung thư hậu m&ocirc;n.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Merck Sharp and Dohm (Mỹ)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Gardasil được chỉ định cho trẻ em và vị thành ni&ecirc;n trong đ&ocirc;̣ tu&ocirc;̉i từ 9-17 tu&ocirc;̉i v&agrave; phụ nữ trong đ&ocirc;̣ tu&ocirc;̉i từ 9-26 tuổi để ph&ograve;ng ngừa ung thư cổ tử cung, &acirc;m hộ, &acirc;m đạo, c&aacute;c tổn thương tiền ung thư v&agrave; loạn sản, mụn c&oacute;c sinh dục v&agrave; bệnh l&yacute; do nhiễm virus HPV.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Do đ&oacute; Gardasil được d&ugrave;ng để ngừa c&aacute;c bệnh sau đ&acirc;y:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Ung thư cổ tử cung v&agrave; &acirc;m hộ, &acirc;m đạo g&acirc;y ra bởi HPV tu&yacute;p 16 v&agrave; 18.</li>
<li style="box-sizing: border-box; text-align: justify;">Mụn c&oacute;c sinh dục (m&agrave;o g&agrave; sinh dục) g&acirc;y ra bởi HPV tu&yacute;p 6 v&agrave; 11.</li>
<li style="box-sizing: border-box; text-align: justify;">Nhiễm v&agrave; bị c&aacute;c tổn thương tiền ung thư hoặc loạn sản g&acirc;y ra bởi HPV tu&yacute;p 6, 11, 16 v&agrave; 18.</li>
<li style="box-sizing: border-box; text-align: justify;">T&acirc;n sinh trong biểu m&ocirc; cổ tử cung độ 2/3 v&agrave; ung thư biểu m&ocirc; tuyến tử cung tại chỗ.</li>
<li style="box-sizing: border-box; text-align: justify;">T&acirc;n sinh biểu m&ocirc; cổ tử cung độ 1.</li>
<li style="box-sizing: border-box; text-align: justify;">T&acirc;n sinh trong biểu m&ocirc; &acirc;m hộ độ 2 v&agrave; 3.</li>
<li style="box-sizing: border-box; text-align: justify;">T&acirc;n sinh trong biểu m&ocirc; &acirc;m đạo độ 2 v&agrave; 3.</li>
<li style="box-sizing: border-box; text-align: justify;">T&acirc;n sinh trong biểu m&ocirc; &acirc;m hộ v&agrave; t&acirc;n sinh trong biểu m&ocirc; &acirc;m đạo độ 1.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m chủng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Gồm 3 mũi</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: l&agrave; ng&agrave;y ti&ecirc;m mũi đầu ti&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: 2 th&aacute;ng sau mũi đầu ti&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: 6 th&aacute;ng sau liều đầu ti&ecirc;n.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Khi cần điều chỉnh lịch ti&ecirc;m th&igrave; mũi 2 phải c&aacute;ch mũi 1 tối thiểu l&agrave; 1 th&aacute;ng v&agrave; mũi 3 phải c&aacute;ch mũi 2 tối thiểu 3 th&aacute;ng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp với liều 0.5ml v&agrave;o v&ugrave;ng cơ Delta v&agrave;o phần tr&ecirc;n c&aacute;nh tay hoặc phần trước b&ecirc;n của ph&iacute;a tr&ecirc;n đ&ugrave;i.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m tĩnh mạch. Chưa c&oacute; nghi&ecirc;n cứu về đường ti&ecirc;m trong da hoặc dưới da n&ecirc;n kh&ocirc;ng c&oacute; khuyến c&aacute;o ti&ecirc;m theo hai đường ti&ecirc;m n&agrave;y</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Người mẫn cảm với c&aacute;c th&agrave;nh phần c&oacute; trong vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được tiếp tục d&ugrave;ng Gardasil nếu c&oacute; phản ứng qu&aacute; mẫn với lần ti&ecirc;m trước</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">*&nbsp;<em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Tr&ecirc;n phụ nữ c&oacute; thai:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Chưa c&oacute; nghi&ecirc;n cứu đầy đủ về việc ti&ecirc;m vắc xin Gardasil tr&ecirc;n phụ nữ c&oacute; thai v&igrave; vậy tr&aacute;nh mang thai trong thời kỳ ti&ecirc;m chủng Gardasil. Nếu c&oacute; thai trong giai đoạn chưa ho&agrave;n th&agrave;nh 3 mũi ti&ecirc;m th&igrave; cần ho&atilde;n ti&ecirc;m chủng để giải quyết xong thai kỳ.</li>
<li style="box-sizing: border-box; text-align: justify;">Chưa c&oacute; bằng chứng n&agrave;o gợi &yacute; cho việc sử dụng Gardasil l&agrave;m ảnh hưởng g&acirc;y hại đến khả năng sinh sản, t&igrave;nh trạng mang thai hoặc g&acirc;y hậu quả cho trẻ sơ sinh.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">*&nbsp;<em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Tr&ecirc;n phụ nữ cho con b&uacute;:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể sử dụng Gardasil cho đối tượng n&agrave;y. Vẫn chưa r&otilde; kh&aacute;ng nguy&ecirc;n của vắc xin v&agrave; kh&aacute;ng thể từ mẹ c&oacute; b&agrave;i tiết qua sữa được kh&ocirc;ng</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng tại chỗ ti&ecirc;m: sưng đau, c&oacute; ban đỏ, hay gặp bầm t&iacute;m v&agrave; ngứa.</li>
<li style="box-sizing: border-box; text-align: justify;">Rất hiếm gặp: co thắt kh&iacute; quản nghi&ecirc;m trọng</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể d&ugrave;ng Gardasil c&ugrave;ng thời điểm với c&aacute;c vắc xin kh&aacute;c như: vắc xin vi&ecirc;m gan B t&aacute;i tổ hợp, vắc xin li&ecirc;n hợp n&atilde;o m&ocirc; cầu nh&oacute;m A, C, D; vắc xin bạch hầu- ho g&agrave;- uốn v&aacute;n nhưng phải ti&ecirc;m ở c&aacute;c vị tr&iacute; kh&aacute;c nhau.</li>
<li style="box-sizing: border-box; text-align: justify;">Đ&atilde; c&oacute; những thử nghiệm l&acirc;m s&agrave;ng khi d&ugrave;ng Gardasil c&ugrave;ng với c&aacute;c thuốc: kh&aacute;ng sinh, nội tiết ngừa thai, c&aacute;c steroid; kết quả cho thấy c&aacute;c thuốc tr&ecirc;n kh&ocirc;ng l&agrave;m ảnh hưởng đến đ&aacute;p ứng miễn dịch đối với Gardasil</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin được bảo quản ở nhiệt độ từ 2-8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C, kh&ocirc;ng được đ&ocirc;ng băng v&agrave; tr&aacute;nh &aacute;nh s&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Khi đưa ra khỏi tủ bảo quản n&ecirc;n sử dụng vắc xin ngay nhưng cũng c&oacute; thể để ngo&agrave;i nhiệt độ ph&ograve;ng &lt; 25<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C trong thời gian 3 ng&agrave;y m&agrave; kh&ocirc;ng bị ảnh hưởng đến chất lượng vắc xin. Sau 3 ng&agrave;y vắc xin cần được loại bỏ.</li>
</ul>', '2021-07-01 04:00:09', '2021-07-03 19:42:11', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (18, 'Cervarix', 16, 'Ngưng sử dụng tại VNVC', 126, '/storage/vaccine_photo/VjQmtFJDuxj2RJjGW39R.jpg', null, '<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">VẮC XIN PH&Ograve;NG UNG THƯ CỔ TỬ CUNG CERVARIX (BỈ)</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">&nbsp;Vắc xin Cervarix l&agrave; vắc xin ph&ograve;ng ngừa bệnh ung thư cổ tử cung cho phụ nữ, được d&ugrave;ng cho c&aacute;c đối tượng trong độ tuổi từ 10 &ndash; 25 tuổi. Cervarix l&agrave; vắc xin chuy&ecirc;n biệt trong ph&ograve;ng ngừa ung thư cổ tử cung, do c&ocirc;ng ty Glaxo Smith Kline nghi&ecirc;n cứu v&agrave; ph&aacute;t triển (C&ocirc;ng ty lớn nhất thế giới trong lĩnh vực nghi&ecirc;n cứu v&agrave; sản xuất vắc xin).</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Cervarix thường gọi dưới t&ecirc;n gọi &rdquo; vắc xin ung thư cổ tử cung của Bỉ&rdquo;. Hiện tại tr&ecirc;n thị trường c&oacute; 2 loại vắc xin ph&ograve;ng ngừa ung thư cổ tử cung l&agrave; Cervarix (Bỉ) v&agrave; Gardasil (Mỹ), đều đ&atilde; được nhập khẩu về Việt Nam v&agrave; được sử dụng tại c&aacute;c ph&ograve;ng ti&ecirc;m vắc xin dịch vụ.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">&nbsp;T&ecirc;n thương mại: Cervarix</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">C&ocirc;ng ty sản xuất: Glaxo Smith Kline - Bỉ</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">TH&Agrave;NH PHẦN</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Một liều vắc xin Cervarix 0,5ml c&oacute; chứa:</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Protein L1 của Papillomavirus tu&yacute;p 16 ở người: 20 microgram.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Protein L1 của Papillomavirus tu&yacute;p 18 ở người: 20 microgram.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">T&aacute; dược: 3-O-desacyl-4&rsquo;-monophosphoryl lipid A (MPL): 50 microgram; Hydroxide nh&ocirc;m, hydrate h&oacute;a: 0,5 milligram Al<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">3+</span>.</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">QUY C&Aacute;CH Đ&Oacute;NG G&Oacute;I</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Hộp 1 bơm kim ti&ecirc;m đ&oacute;ng sẵn 1 liều vắc xin 0,5 ml dạng huyền dịch để ti&ecirc;m.</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">CHỈ ĐỊNH</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Vắc xin Cervarix&nbsp; được chỉ định d&ugrave;ng cho nữ giới từ 10 &ndash; 25 tuổi, để tạo miễn dịch chủ động ph&ograve;ng tr&aacute;nh bệnh ung thư cổ tử cung cho phụ nữ (ung thư biểu m&ocirc; vảy v&agrave; ung thư biểu m&ocirc; tuyến cổ tử cung). Bằng c&aacute;ch chống lại nhiễm mới v&agrave; nhiễm dai dẳng g&acirc;y ra bởi Papillomavirus ở người. (99,7% c&aacute;c ca ung thư cổ tử cung l&agrave; do Papilloma virus g&acirc;y ra).</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">LỊCH TI&Ecirc;M PH&Ograve;NG</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Ti&ecirc;m 3 mũi cơ bản theo lịch 0, 1, 6 th&aacute;ng: Mũi đầu ti&ecirc;m thời điểm bất kỳ; mũi 2 c&aacute;ch mũi 1 tối thiểu 1 th&aacute;ng; mũi 3 c&aacute;ch mũi 2 tối thiểu 5 th&aacute;ng.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Nếu v&igrave; l&yacute; do g&igrave; đ&oacute; kh&ocirc;ng thể ti&ecirc;m được theo đ&uacute;ng lịch, th&igrave; c&oacute; thể &aacute;p dụng ph&aacute;c đồ linh động: Mũi 2 c&oacute; thể ti&ecirc;m c&aacute;ch mũi 1 khoảng từ 1 &ndash; 2,5 th&aacute;ng; Mũi 3 c&oacute; thể ti&ecirc;m c&aacute;ch mũi 1 trong khoảng từ 6 -12 th&aacute;ng.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Hiện tại chưa c&oacute; khuyến c&aacute;o phải ti&ecirc;m nhắc lại.</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">C&Aacute;CH D&Ugrave;NG</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Vắc xin Cervarix được chỉ định ti&ecirc;m bắp c&ugrave;ng cơ delta.</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">CHỐNG CHỈ ĐỊNH</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Kh&ocirc;ng ti&ecirc;m Cervarix cho c&aacute;c đối tượng qu&aacute; mẫn với bất kỳ th&agrave;nh phần n&agrave;o trong vắc xin.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m bắp Cervarix cho những người giảm tiểu cầu hoặc rối loạn đ&ocirc;ng m&aacute;u do hiện tượng chảy m&aacute;u c&oacute; thể xuất hiện sau khi ti&ecirc;m bắp ở những đối tượng n&agrave;y.</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">THẬN TRỌNG</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Phải kh&aacute;m s&agrave;ng lọc v&agrave; hỏi tiền sử về sức khỏe, t&igrave;nh trạng dị ứng ti&ecirc;m chủng trước đ&oacute; trước khi ti&ecirc;m vắc xin.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Kh&ocirc;ng ti&ecirc;m Cervarix ở những người đang sốt cao cấp t&iacute;nh. Tuy nhi&ecirc;n nhiễm khuẩn nhẹ th&igrave; vẫn c&oacute; thể chủng ngừa.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m Cervarix đường tĩnh mạch, trong da hoặc dưới da.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Cũng giống như tất cả c&aacute;c loại vắc xin kh&aacute;c. Kh&ocirc;ng phải 100% tất cả c&aacute;c đối tượng sau khi ti&ecirc;m vắc xin đều c&oacute; đ&aacute;p ứng miễn dịch.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Cervarix kh&ocirc;ng c&oacute; khả năng bảo vệ đối với tất cả c&aacute;c tu&yacute;p HPV (Human Papiloma Virus).</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Nếu tại thời điểm ti&ecirc;m bệnh nh&acirc;n đ&atilde; bị c&aacute;c tổn thương tiền ung thư CIN II, CIN III. Th&igrave; việc ti&ecirc;m vắc xin Cervarix kh&ocirc;ng ngăn chặn được sự tiến triển của c&aacute;c tổn thương li&ecirc;n quan đến HPV.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Ti&ecirc;m chủng Cervarix chỉ l&agrave; biện ph&aacute;p ph&ograve;ng bệnh, kh&ocirc;ng thể thay thế được việc kh&aacute;m s&agrave;ng lọc ung thư cổ tử cung theo định kỳ (Thời gian kh&aacute;m s&agrave;ng lọc UTCTC thường l&agrave; mỗi 6 th&aacute;ng kh&aacute;m lại 1 lần).</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Chưa c&oacute; dữ liệu l&acirc;m s&agrave;ng về việc d&ugrave;ng Cervarix cho c&aacute;c đối tượng suy giảm miễn dịch (Bệnh nh&acirc;n HIV hoặc bệnh nh&acirc;n đang điều trị c&aacute;c thuốc ức chế miễn dịch). Đối với những đối tượng n&agrave;y, đ&aacute;p ứng miễn dịch c&oacute; thể kh&ocirc;ng đạt được như mong muốn.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Cũng giống như tất cả c&aacute;c loại vắc xin kh&aacute;c, n&ecirc;n c&oacute; sẵn adrenalin v&agrave; c&aacute;c phương tiện điều trị th&iacute;ch hợp để ph&ograve;ng ngừa sock phản vệ sau ti&ecirc;m chủng.</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">PHỤ NỮ C&Oacute; THAI V&Agrave; CHO CON B&Uacute;</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">C&aacute;c nghi&ecirc;n cứu đặc hiệu về việc sử dụng Cervarix tr&ecirc;n phụ nữ c&oacute; thai kh&ocirc;ng được thực hiện. Do vậy, n&ecirc;n tr&igrave; ho&atilde;n việc ti&ecirc;m ph&ograve;ng vắc xin cho đến khi kết th&uacute;c thai kỳ.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Ảnh hưởng đối với trẻ đang b&uacute; mẹ khi mẹ khi ti&ecirc;m Cervarix chưa được đ&aacute;nh gi&aacute; trong c&aacute;c nghi&ecirc;n cứu l&acirc;m s&agrave;ng. V&igrave; vậy chỉ sử dụng Cervarix cho phụ nữ đang cho con b&uacute; trong những trường hợp thật sự cần thiết</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">T&Aacute;C DỤNG KH&Ocirc;NG MONG MUỐN</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Đ&atilde; c&oacute; nghi&ecirc;n cứu tr&ecirc;n l&acirc;m s&agrave;ng thực hiện tr&ecirc;n 12.672 phụ nữ từ 10-25 tuổi. C&aacute;c t&aacute;c dụng kh&ocirc;ng mong muốn phổ biến nhất sau ti&ecirc;m chủng Cervarix l&agrave; đau tại chỗ ti&ecirc;m (78%). Phần lớn c&aacute;c t&aacute;c dụng kh&ocirc;ng mong muốn n&agrave;y xảy ra ở mức độ nhẹ đến trung b&igrave;nh v&agrave; kh&ocirc;ng k&eacute;o d&agrave;i. Tần suất được b&aacute;o c&aacute;o như sau:</p>
<h4 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #010101; font-size: 16px; text-align: justify;">Rất phổ biến ( &ge;1/10)</h4>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Đau đầu; đau cơ; đau, đỏ, sưng chỗ ti&ecirc;m; mệt mỏi.</p>
<h4 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #010101; font-size: 16px; text-align: justify;">Phổ biến ( &ge;1/100 đến &lt;1/10)</h4>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Buồn n&ocirc;n, n&ocirc;n, ti&ecirc;u chảy v&agrave; đau bụng; ngứa, ph&aacute;t ban, m&agrave;y đay; đau khớp; sốt (&ge; 38oC).</p>
<h4 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #010101; font-size: 16px; text-align: justify;">Kh&ocirc;ng phổ biến ( &ge;1/1000 đến &lt;1/100)</h4>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Nhiễm khuẩn đường h&ocirc; hấp tr&ecirc;n; sưng hạch bạch huyết; hoa mắt, ch&oacute;ng mặt; phản ứng tại chỗ kh&aacute;c bao gồm chai cứng, t&ecirc;.</p>
<h4 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #010101; font-size: 16px; text-align: justify;">Hiếm gặp</h4>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Phản ứng dị ứng (bao gồm c&aacute;c phản ứng phản vệ v&agrave; &aacute; phản vệ), ph&ugrave; mạch. C&oacute; thể xuất hiện hiện tượng ngất xỉu sau hoặc thậm ch&iacute; trước khi ti&ecirc;m do phản ứng t&acirc;m l&yacute; đối với mũi ti&ecirc;m.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Th&ocirc;ng b&aacute;o cho b&aacute;c sỹ c&aacute;c t&aacute;c dụng kh&ocirc;ng mong muốn gặp phải khi d&ugrave;ng thuốc.</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">TƯƠNG T&Aacute;C THUỐC</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Sử dụng đồng thời với c&aacute;c vaccine kh&aacute;c.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Cervarix c&oacute; thể ti&ecirc;m đồng thời với c&aacute;c vắc xin: vắc xin kh&aacute;ng nguy&ecirc;n bạch hầu-uốn v&aacute;n-ho g&agrave; v&ocirc; b&agrave;o (DTPa); vắc xin bại liệt bất hoạt (IPV); vắc xin phối hợp DTPa-IPV; vắc xin vi&ecirc;m gan A bất hoạt , vắc xin vi&ecirc;m gan B; vắc xin phối hợp vi&ecirc;m gan A + B.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Nếu ti&ecirc;m Cervarix c&ugrave;ng l&uacute;c với c&aacute;c vắc xin dạng ti&ecirc;m kh&aacute;c, phải sử dụng bơm kim ti&ecirc;m ri&ecirc;ng v&agrave; ti&ecirc;m v&agrave;o c&aacute;c vị tr&iacute; kh&aacute;c nhau.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Sử dụng đồng thời với c&aacute;c biện ph&aacute;p tr&aacute;nh thai nội tiết tố</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Kh&ocirc;ng c&oacute; bằng chứng n&agrave;o cho thấy c&aacute;c thuốc tr&aacute;nh thai nội tiết tố c&oacute; ảnh hưởng đến hiệu quả của Cervarix.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Sử dụng đồng thời với c&aacute;c thuốc ức chế miễn dịch to&agrave;n th&acirc;n.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Cũng giống với c&aacute;c vắc xin kh&aacute;c, đ&aacute;p ứng miễn dịch c&oacute; thể kh&ocirc;ng đạt được hiệu quả tối ưu ở những bệnh nh&acirc;n đang sử dụng c&aacute;c thuốc ức chế miễn dịch.</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">HIỆU QUẢ BẢO VỆ</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Cervarix trong th&agrave;nh phần vắc xin chỉ c&oacute; 2 tu&yacute;p l&agrave; 16 v&agrave; 18. Tuy nhi&ecirc;n do c&oacute; chất bổ trợ ASO4 độc quyền gi&uacute;p k&eacute;o d&agrave;i thời gian bảo vệ hơn so với c&aacute;c vắc xin kh&aacute;c sử dụng muối nh&ocirc;m th&ocirc;ng thường.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Ngo&agrave;i 2 tu&yacute;p 16 v&agrave; 18 trong th&agrave;nh phần, Cervarix c&ograve;n c&oacute; khả năng bảo vệ ch&eacute;o c&aacute;c tu&yacute;p kh&aacute;c như: 31, 33, 35, 39, 45, 51, 52, 56, 58, 59, 66, 68.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Hiệu quả bảo vệ chung ph&ograve;ng chống ung thư cổ tử cung trong nghi&ecirc;n cứu PATRICIA được thực hiện tr&ecirc;n 18.500 phụ nữ ở độ tuổi từ 15 -25 tuổi, cho thấy hiệu quả bảo vệ chung, ph&ograve;ng chống ung thư cổ tử cung của Cervarix l&agrave; 93%, bất kể do c&aacute;c tu&yacute;p HPV n&agrave;o.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Thời gian bảo vệ của vắc xin Cervarix hiện vẫn đang được tiến h&agrave;nh theo d&otilde;i. Thời gian bảo vệ được cập nhật cho đến hiện tại trong một số nghi&ecirc;n cứu l&agrave; 8,4 năm. Tuy nhi&ecirc;n theo dự đo&aacute;n dựa theo nồng độ kh&aacute;ng thể th&igrave; thời gian n&agrave;y c&oacute; thể k&eacute;o d&agrave;i l&ecirc;n tới hơn 20 năm.</p>
<h3 style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; font-family: Roboto-Bold; font-weight: 500; line-height: 1.2; color: #333333; font-size: 16px; border-left: 2px solid #a41c2f; background: #eeeeee; text-transform: uppercase; padding: 10px 10px 10px 20px; text-align: justify;">BẢO QUẢN</h3>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Vận chuyển v&agrave; bảo quản trong tủ lạnh ở nhiệt độ từ 2&ordm;C-8&ordm;C.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Kh&ocirc;ng để đ&ocirc;ng đ&aacute; vắc xin. Loại bỏ khi bị đ&ocirc;ng đ&aacute;.</p>
<p style="box-sizing: border-box; margin-top: 0px; margin-bottom: 0.5rem; line-height: 25px; color: #444b49; font-family: Roboto-Regular, Arial, sans-serif; font-size: 16px; text-align: justify;">Giữ nguy&ecirc;n vắc xin trong hộp để tr&aacute;nh &aacute;nh s&aacute;ng.</p>', '2021-07-01 04:00:09', '2021-07-03 19:44:56', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (19, 'PHÒNG BỆNH THỦY ĐẬU', 0, null, null, null, null, null, '2021-07-01 04:00:09', '2021-07-01 04:00:09', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (20, 'Varivax', 19, 'Đang sử dụng', 126, '/storage/vaccine_photo/uyFno3tuMublAXt1eY18.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">hủy đậu l&agrave; bệnh truyền nhiễm cấp t&iacute;nh, c&oacute; thể g&acirc;y ra nhiều biến chứng nguy hiểm:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">C&oacute; đến 20% tỷ lệ trẻ tử vong do vi&ecirc;m n&atilde;o biến chứng thủy đậu, nếu may mắn cứu sống, người bệnh cũng c&oacute; nguy cơ bại n&atilde;o, nằm liệt giường.</li>
<li style="box-sizing: border-box; text-align: justify;">Khoảng 30% trẻ sơ sinh tử vong do mắc thủy đậu l&acirc;y từ mẹ.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ dưới 3 th&aacute;ng tuổi hoặc trẻ c&oacute; hệ miễn dịch yếu, khi mắc bệnh thủy đậu sẽ rất nguy hiểm, khả năng tử vong cao v&agrave; điều trị tốn k&eacute;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ mang thai, đặc biệt trong khoảng 13-20 tuần, mắc thủy đậu c&oacute; thể dẫn đến sảy thai hoặc để lại dị tật cho thai nhi (dị dạng ở sọ, đa dị tật ở tim, trẻ sơ sinh mắc chứng đầu nhỏ&hellip;).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Ti&ecirc;m vắc xin l&agrave; c&aacute;ch duy nhất, hiệu quả v&agrave; l&acirc;u d&agrave;i nhất để ngăn ngừa, giảm tỷ lệ mắc bệnh, giảm tỷ lệ biến chứng v&agrave; nhập viện nặng do thuỷ đậu.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin Varilrix (Bỉ)</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Varilrix (Bỉ) l&agrave; vắc xin sống, giảm độc lực, được sử dụng để ph&ograve;ng ngừa bệnh thủy đậu cho người từ 9 th&aacute;ng tuổi v&agrave; những người lớn chưa c&oacute; miễn dịch ph&ograve;ng bệnh.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin thủy đậu Varilrix được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi h&atilde;ng dược h&agrave;ng đầu thế giới &ndash; GlaxoSmithKline (GSK).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Varilrix l&agrave; vắc xin sống giảm độc lực ph&ograve;ng bệnh thủy đậu do vi khuẩn Varicella Zoster (VZV) g&acirc;y ra.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đối tượng c&oacute; thể sử dụng vắc xin</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Trẻ em từ khi 9 th&aacute;ng tuổi v&agrave; người lớn chưa c&oacute; miễn dịch ph&ograve;ng chống bệnh thủy đậu.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; text-decoration-line: underline;"><em style="box-sizing: border-box;">Trẻ em từ 9 th&aacute;ng tuổi đến 12 tuổi: Lịch ti&ecirc;m 2 mũi</em></span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: L&agrave; mũi ti&ecirc;m lần đầu ti&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: Sau mũi 1 &iacute;t nhất 3 th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ từ 13 tuổi v&agrave; người lớn: Lịch ti&ecirc;m 2 mũi:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: l&agrave; mũi ti&ecirc;m lần đầu ti&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: sau mũi 1 &iacute;t nhất l&agrave; 1 th&aacute;ng (kh&ocirc;ng ti&ecirc;m trước 4 tuần trong bất cứ ho&agrave;n cảnh n&agrave;o).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Varilrix được chỉ định ti&ecirc;m dưới da ở v&ugrave;ng cơ delta hoặc v&ugrave;ng m&aacute; ngo&agrave;i đ&ugrave;i với liều 0.5ml.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định /Ho&atilde;n ti&ecirc;m:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; Ho&atilde;n ti&ecirc;m vắc xin Varilrix với những người đang sốt cao cấp t&iacute;nh.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; Chống chỉ định ti&ecirc;m cho những người suy giảm miễn dịch dịch thể hoặc tế n&agrave;o nghi&ecirc;m trọng như:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Người suy giảm miễn dịch ti&ecirc;n ph&aacute;t hoặc c&oacute; số lượng tế b&agrave;o lympho &iacute;t hơn 1.200/mm3.</li>
<li style="box-sizing: border-box; text-align: justify;">Người thiếu hụt khả năng miễn dịch tế b&agrave;o như: bạch hầu, ung thư bạch huyết, loạn tạo m&aacute;u, nhiễm HIV.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh nh&acirc;n đang điều trị ức chế miễn dịch (bao gồm việc sử dụng liều cao corticosteroid).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; Chống chỉ định ti&ecirc;m Varilrix cho người qu&aacute; mẫn cảm với neomycin hoặc bất cứ th&agrave;nh phần kh&aacute;c c&oacute; trong vắc xin.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; Kh&ocirc;ng ti&ecirc;m vắc xin cho những người c&oacute; dấu hiệu qu&aacute; mẫn sau liều ti&ecirc;m vắc xin thủy đậu trước đ&oacute;. Kh&ocirc;ng d&ugrave;ng cho phụ nữ mang thai, tốt nhất n&ecirc;n ho&agrave;n th&agrave;nh ph&aacute;c đồ ti&ecirc;m chủng 3 th&aacute;ng trước khi mang thai.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Người được ti&ecirc;m chủng c&oacute; thể bị ngất xỉu sau hoặc thậm ch&iacute; trước khi ti&ecirc;m. Do đ&oacute;, n&ecirc;n lựa chọn địa điểm th&iacute;ch hợp khi ti&ecirc;m để tr&aacute;nh bị thương do ngất xỉu.</li>
<li style="box-sizing: border-box; text-align: justify;">Phải để cho cồn v&agrave; c&aacute;c chất s&aacute;t khuẩn kh&aacute;c bay hơi hết trước khi ti&ecirc;m vắc xin do ch&uacute;ng c&oacute; thể l&agrave;m bất hoạt c&aacute;c virus giảm độc lực c&oacute; trong vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể đạt được t&aacute;c dụng bảo vệ ph&ograve;ng thủy đậu (tuy c&ograve;n hạn chế) bằng việc ti&ecirc;m chủng trong v&ograve;ng 72 giờ sau khi tiếp x&uacute;c với thủy đậu tự nhi&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m Varilrix v&agrave;o tĩnh mạch hoặc trong da.</li>
<li style="box-sizing: border-box; text-align: justify;">Varilrix c&oacute; thể ti&ecirc;m c&ugrave;ng l&uacute;c với bất kỳ vắc xin n&agrave;o kh&aacute;c. C&aacute;c vắc xin dạng ti&ecirc;m kh&aacute;c nhau n&ecirc;n ti&ecirc;m ở những vị tr&iacute; kh&aacute;c nhau.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c vắc xin bất hoạt c&oacute; thể được d&ugrave;ng v&agrave;o bất kỳ thời gian n&agrave;o khi d&ugrave;ng Varilrix.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Varilrix c&oacute; thể ti&ecirc;m c&ugrave;ng với c&aacute;c vắc xin sống dạng ti&ecirc;m kh&aacute;c trong c&ugrave;ng buổi ti&ecirc;m chủng. Nếu kh&ocirc;ng thể ti&ecirc;m c&ugrave;ng buổi ti&ecirc;m chủng th&igrave; c&aacute;c loại n&agrave;y phải ti&ecirc;m c&aacute;ch nhau &iacute;t nhất 1 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh nh&acirc;n c&oacute; nguy cơ cao: Kh&ocirc;ng sử dụng Varilrix c&ugrave;ng l&uacute;c với vắc xin sống giảm độc lực kh&aacute;c. C&aacute;c vắc xin bất hoạt c&oacute; thể được d&ugrave;ng v&agrave;o bất kỳ thời gian n&agrave;o khi d&ugrave;ng Varilrix miễn l&agrave; kh&ocirc;ng c&oacute; chống chỉ định đặc biệt n&agrave;o. Tuy nhi&ecirc;n, c&aacute;c vắc xin kh&aacute;c nhau n&ecirc;n ti&ecirc;m ở những vị tr&iacute; kh&aacute;c nhau.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Nếu cần thử phản ứng lao th&igrave; n&ecirc;n tiến h&agrave;nh trước hoặc c&ugrave;ng l&uacute;c với việc ti&ecirc;m chủng v&igrave; c&oacute; thể g&acirc;y ức chế tr&ecirc;n da. Sự ức chế n&agrave;y c&oacute; thể k&eacute;o d&agrave;i l&ecirc;n tới 6 tuần v&agrave; kh&ocirc;ng n&ecirc;n x&eacute;t nghiệm phản ứng lao trong thời gian sau khi ti&ecirc;m chủng để tr&aacute;nh t&iacute;nh &acirc;m giả.</li>
<li style="box-sizing: border-box; text-align: justify;">Ở những người đ&atilde; d&ugrave;ng globulin miễn dịch hay truyền m&aacute;u, n&ecirc;n ho&atilde;n việc ti&ecirc;m vắc xin Varilrix &iacute;t nhất 3 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">N&ecirc;n tr&aacute;nh d&ugrave;ng salicylate trong 6 tuần sau ti&ecirc;m vắc xin Varilrix do đ&atilde; c&oacute; nghi&ecirc;n cứu về hội chứng Reye sau khi d&ugrave;ng salicylate trong thời gian nhiễm virus thủy đậu tự nhi&ecirc;n.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng tại chỗ ti&ecirc;m: Nổi ban đỏ, chai cứng, sưng đau, tăng nhạy cảm tại chỗ ti&ecirc;m. Tỷ lệ đau, đỏ, sưng c&oacute; xu hướng cao hơn sau khi ti&ecirc;m liều thứ 2 so với sau khi ti&ecirc;m liều thứ nhất. C&aacute;c phản ứng to&agrave;n th&acirc;n kh&aacute;c: Sốt, đau đầu, buồn ngủ, giảm cảm gi&aacute;c th&egrave;m ăn, n&ocirc;n mửa, ti&ecirc;u chảy.</li>
<li style="box-sizing: border-box; text-align: justify;">Cũng như c&aacute;c loại vắc xin thủy đậu kh&aacute;c, vắc xin Varilrix c&oacute; thể g&acirc;y ra ph&aacute;t ban dạng thủy đậu ở người ti&ecirc;m vắc xin nhưng những biểu hiện n&agrave;y thường rất nhẹ so với người kh&ocirc;ng được ti&ecirc;m ph&ograve;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Bảo quản ở nhiệt độ lạnh (từ 2 &ndash; 8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C) v&agrave; tr&aacute;nh &aacute;nh s&aacute;ng. Vắc xin kh&ocirc;ng bị ảnh hưởng khi để đ&ocirc;ng đ&aacute;. Kh&ocirc;ng l&agrave;m đ&ocirc;ng băng dung m&ocirc;i.</li>
</ul>', '2021-07-01 04:01:55', '2021-07-03 20:34:33', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (21, 'Varicella', 19, 'Đang sử dụng', 286, '/storage/vaccine_photo/fB9WR1sErY8N13Jc4wiZ.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Thủy đậu l&agrave; bệnh truyền nhiễm cấp t&iacute;nh, c&oacute; thể g&acirc;y ra nhiều biến chứng nguy hiểm:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">C&oacute; đến 20% tỷ lệ trẻ tử vong do vi&ecirc;m n&atilde;o biến chứng thủy đậu, nếu may mắn cứu sống, người bệnh cũng c&oacute; nguy cơ bại n&atilde;o, nằm liệt giường.</li>
<li style="box-sizing: border-box; text-align: justify;">30% trẻ sơ sinh tử vong do mắc thủy đậu l&acirc;y từ mẹ. Trẻ dưới 3 th&aacute;ng tuổi hoặc trẻ c&oacute; hệ miễn dịch yếu, khi mắc bệnh thủy đậu sẽ rất nguy hiểm, khả năng tử vong cao v&agrave; điều trị tốn k&eacute;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ mang thai, đặc biệt trong khoảng 13-20 tuần, mắc thủy đậu c&oacute; thể dẫn đến sảy thai hoặc để lại dị tật cho thai nhi (dị dạng ở sọ, đa dị tật ở tim, trẻ sơ sinh mắc chứng đầu nhỏ&hellip;).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Ti&ecirc;m vắc xin l&agrave; c&aacute;ch duy nhất, hiệu quả v&agrave; l&acirc;u d&agrave;i để ngăn ngừa bệnh thủy đậu. C&aacute;c chuy&ecirc;n gia cho biết, c&oacute; từ 88-98% người ti&ecirc;m vắc xin thủy đậu đ&atilde; tạo được khả năng miễn dịch tuyệt đối với bệnh.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin Varicella (H&agrave;n Quốc)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin ph&ograve;ng thủy đậu Varicella l&agrave; vắc xin dạng đ&ocirc;ng kh&ocirc; của virus thủy đậu (varicella) sống giảm độc lực, được chỉ định ph&ograve;ng thủy đậu cho c&aacute;c đối tượng từ 12 th&aacute;ng tuổi trở l&ecirc;n.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Nghi&ecirc;n cứu v&agrave; sản xuất: Green Cross &ndash; H&agrave;n Quốc.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin được chỉ định ph&ograve;ng thủy đậu cho c&aacute;c đối tượng từ 12 th&aacute;ng tuổi trở l&ecirc;n. Ngo&agrave;i ra vắc xin khuyến kh&iacute;ch được ti&ecirc;m cho c&aacute;c đối tượng chưa mắc thủy đậu v&agrave; c&oacute; th&ecirc;m c&aacute;c yếu tố như:</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; nguy cơ cao mắc bệnh thủy đậu (V&ugrave;ng nguy cơ cao, đang c&oacute; dịch lưu h&agrave;nh&hellip;).</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh nh&acirc;n c&oacute; bệnh bạch cầu lympho cấp t&iacute;nh: Bệnh nh&acirc;n chưa tiếp x&uacute;c với bất kỳ một th&agrave;nh phần n&agrave;o của vắc xin Varicella &iacute;t nhất trong v&ograve;ng 3 th&aacute;ng gần đ&acirc;y; C&oacute; số lượng tế b&agrave;o lympho lớn hơn 500/mm3; C&oacute; kết quả thử nghiệm qu&aacute; mẫn muộn dương t&iacute;nh (VD: với dẫn xuất protein tinh khiết (PPD), dinitrochlorobenzene (DNCB) v&agrave; phytohaemagglutinine (PHA, 5mcg/0,1ml)).</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh nh&acirc;n c&oacute; khối u &aacute;c t&iacute;nh thể rắn đ&atilde; sử dụng phương ph&aacute;p phẫu thuật hoặc h&oacute;a trị liệu để ngăn chặn sự ph&aacute;t triển của khối u.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh l&yacute; thận hư, hoặc hen phế quản nặng điều trị bằng ACTH hoặc corticosteroid.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c đối tượng tiếp x&uacute;c chặt chẽ với đối tượng nguy cơ cao phải ti&ecirc;m ph&ograve;ng vắc xin (Cha mẹ, anh chị em ruột, người chăm s&oacute;c y tế cho bệnh nh&acirc;n thủy đậu).</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c đối tượng cảm nhiễm sống trong c&ugrave;ng một cộng đồng kh&eacute;p k&iacute;n (như buồng bệnh hoặc k&yacute; t&uacute;c x&aacute;).</li>
<li style="box-sizing: border-box; text-align: justify;">Sinh vi&ecirc;n y khoa, y t&aacute;, b&aacute;c sĩ, c&aacute;n bộ y tế&hellip;v&agrave; những phụ nữ muốn được bảo vệ trước khi c&oacute; thai.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">&ndash; Trẻ từ 12 th&aacute;ng đến 12 tuổi: 2 mũi</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box;">Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n trong độ tuổi</li>
<li style="box-sizing: border-box;">Mũi 2: C&aacute;ch mũi 1 l&agrave; 3 th&aacute;ng hoặc hẹn mũi 2 l&uacute;c 4-6 tuổi</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">&ndash; Trẻ từ 13 tuổi trở l&ecirc;n v&agrave; người lớn: 2 mũi c&aacute;ch nhau &iacute;t nhất 1 th&aacute;ng</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">&ndash; Phụ nữ đang c&oacute; dự định mang thai n&ecirc;n ho&agrave;n tất lịch ti&ecirc;m thủy đậu trước khi c&oacute; thai &iacute;t nhất 3 th&aacute;ng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">C&aacute;ch d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin phải được sử dụng ngay kh&ocirc;ng qu&aacute; 30 ph&uacute;t sau khi ho&agrave;n nguy&ecirc;n với nước hồi chỉnh cung cấp.</li>
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m dưới da. Liều đơn 0,5ml</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Qu&aacute; mẫn với bất kỳ th&agrave;nh phần n&agrave;o của vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng d&ugrave;ng vắc xin cho đối tượng đang sốt hoặc suy dinh dưỡng.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh tim mạch, rối loạn chức năng gan, thận.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; tiền sử qu&aacute; mẫn với kanamycin v&agrave; Erythromycin.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; tiền sử co giật trong v&ograve;ng 1 năm trước khi ti&ecirc;m vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Suy giảm miễn dịch tế b&agrave;o.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thai hoặc 2 th&aacute;ng trước khi định c&oacute; thai.</li>
<li style="box-sizing: border-box; text-align: justify;">Đ&atilde; ti&ecirc;m ph&ograve;ng vắc xin sống kh&aacute;c trong v&ograve;ng 1 th&aacute;ng gần đ&acirc;y (Sởi, quai bị, rubella, lao, bại liệt).</li>
<li style="box-sizing: border-box; text-align: justify;">Suy giảm miễn dịch ti&ecirc;n ph&aacute;t hoặc mắc phải như AIDS hoặc c&aacute;c biểu hiện l&acirc;m s&agrave;ng của nhiễm virus g&acirc;y suy giảm miễn dịch ở người.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ em dưới 12 th&aacute;ng tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh nh&acirc;n mắc bệnh bạch cầu tủy cấp, bệnh bạch cầu tế b&agrave;o lympho T hoặc u lympho &aacute;c t&iacute;nh.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh nh&acirc;n bị ức chế mạnh hệ thống miễn dịch do xạ trị hoặc giai đoạn tấn c&ocirc;ng trong điều trị bệnh bạch cầu.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng v&agrave; những điều cần lưu &yacute;:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Điều trị liệu ph&aacute;p ph&ugrave; hợp bằng epinephrine ngay khi phản ứng qu&aacute; mẫn xảy ra.</li>
<li style="box-sizing: border-box; text-align: justify;">Khoảng thời gian để c&oacute; hiệu quả bảo vệ sau khi ti&ecirc;m vắc xin Varicella chưa được biết r&otilde;. Tuy nhi&ecirc;n sau khi ti&ecirc;m vắc xin cần tr&aacute;nh tiếp x&uacute;c với người c&oacute; nguy cơ mắc bệnh cao trong v&ograve;ng 6 tuần. C&aacute;c đối tượng c&oacute; nguy cơ cao đ&oacute; l&agrave; c&aacute;c đối tượng suy giảm miễn dịch, phụ nữ c&oacute; thai kh&ocirc;ng c&oacute; tiền sử mắc bệnh hoặc x&eacute;t nghiệm kh&ocirc;ng thấy sự nhiễm bệnh trước đ&oacute;.</li>
<li style="box-sizing: border-box; text-align: justify;">Cần thận trọng đối với c&aacute;c đối tượng phụ nữ cho con b&uacute; d&ugrave; chưa r&otilde; virus thủy đậu c&oacute; thể b&agrave;i tiết qua sữa mẹ hay kh&ocirc;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Trong trường hợp đối tượng điều trị bằng 6-mercaptopurine, phải ngừng thuốc &iacute;t nhất 1 tuần trước khi ti&ecirc;m vắc xin v&agrave; sử dụng lại &iacute;t nhất 1 tuần sau đ&oacute;.</li>
<li style="box-sizing: border-box; text-align: justify;">Trong trường hợp khẩn cấp (phải tạo miễn dịch chủ động bằng c&aacute;ch ti&ecirc;m immunoglobulin Varicella &ndash; zoster), ti&ecirc;m ph&ograve;ng vắc xin cần được tiến h&agrave;nh trừ khi c&oacute; triệu chứng suy giảm miễn dịch. Trong trường hợp khẩn cấp n&agrave;y vắc xin cần phải được ti&ecirc;m trong v&ograve;ng 72 giờ sau khi phơi nhiễm với virus Varicella.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ở bệnh nh&acirc;n c&oacute; nguy cơ cao, sau 14 -30 ng&agrave;y sau khi ti&ecirc;m vắc xin c&oacute; thể xuất hiện c&aacute;c nốt ph&aacute;t ban dạng nốt sần hoặc phỏng nước k&egrave;m theo sốt nhẹ. Gần 20% bệnh nh&acirc;n bệnh bạch cầu lympho cấp t&iacute;nh c&oacute; phản ứng phụ n&agrave;y.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c biểu hiện của bệnh Herpes zoster cũng c&oacute; thể xuất hiện ở bệnh nh&acirc;n c&oacute; nguy cơ cao.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng thường gặp như: Sưng đau, tấy đỏ, nổi ban, ngứa, tụ m&aacute;u, nổi mảng cứng chỗ ti&ecirc;m; sốt cao tr&ecirc;n 39 độ C; ph&aacute;t ban dạng thủy đậu (to&agrave;n th&acirc;n hoặc chỗ ti&ecirc;m).</li>
<li style="box-sizing: border-box; text-align: justify;">Phải th&ocirc;ng b&aacute;o ngay cho b&aacute;c sĩ những t&aacute;c dụng kh&ocirc;ng mong muốn gặp phải sau khi ti&ecirc;m vắc xin.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m vắc xin &iacute;t nhất 5 th&aacute;ng sau khi truyền m&aacute;u hoặc huyết tương v&agrave; ti&ecirc;m bất kỳ một immunoglobulin n&agrave;o hoặc immunoglobulin với Varicella Zoster (VZIG).</li>
<li style="box-sizing: border-box; text-align: justify;">Sau khi ti&ecirc;m vắc xin trong v&ograve;ng 2 th&aacute;ng kh&ocirc;ng được sử dụng immunoglobulin kể cả VZIG trừ khi c&aacute;c chế phẩm n&agrave;y c&oacute; lợi hơn ti&ecirc;m vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Trong v&ograve;ng 6 tuần sau khi ti&ecirc;m vắc xin kh&ocirc;ng được sử dụng salicylate do hội chứng Reye đ&atilde; xảy ra ở những bệnh nh&acirc;n mắc thủy đậu một c&aacute;ch tự nhi&ecirc;n được điều trị bằng salicylate.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Bảo quản ở 2-8&ordm;C.</li>
<li style="box-sizing: border-box; text-align: justify;">Tr&aacute;nh &aacute;nh s&aacute;ng trực tiếp.</li>
</ul>', '2021-07-01 04:01:55', '2021-07-04 22:12:04', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (22, 'PHÒNG BỆNH CÚM', 0, null, null, null, null, null, '2021-07-01 04:01:55', '2021-07-01 04:01:55', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (23, 'GC Flu', 22, 'Đang sử dụng', 286, '/storage/vaccine_photo/ejRXgecIVQZHz5puJ4YL.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bệnh c&uacute;m m&ugrave;a l&agrave; bệnh truyền nhiễm cấp t&iacute;nh với biểu hiện sốt, đau đầu, đau cơ, mệt mỏi, sổ mũi, đau họng v&agrave; ho.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">T&aacute;c nh&acirc;n g&acirc;y bệnh chủ yếu do c&aacute;c chủng vi r&uacute;t c&uacute;m A(H3N2), c&uacute;m A(H1N1), c&uacute;m B v&agrave; c&uacute;m C. Bệnh c&oacute; khả năng l&acirc;y nhiễm rất cao, qua đường h&ocirc; hấp, qua c&aacute;c giọt nhỏ nước bọt hay dịch tiết mũi họng do hắt hơi, ho khạc.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Th&ocirc;ng thường bệnh diễn biến nhẹ v&agrave; hồi phục trong v&ograve;ng 2-7 ng&agrave;y, nhưng đối với trẻ em, người lớn tuổi, người c&oacute; bệnh m&atilde;n t&iacute;nh về tim phổi, thận, bệnh chuyển h&oacute;a, thiếu m&aacute;u hoặc suy giảm miễn dịch&hellip;, bệnh c&oacute; thể diễn biến nặng hơn, dễ bị biến chứng v&agrave; c&oacute; thể dẫn đến tử vong.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Hiện nay, ti&ecirc;m vắc xin h&agrave;ng năm l&agrave; biện ph&aacute;p hiệu quả nhất để ph&ograve;ng ngừa c&uacute;m.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về vắc xin GC Flu (H&agrave;n Quốc) ph&ograve;ng bệnh c&uacute;m</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin GC Flu của Green Cross Corporation (H&agrave;n Quốc) l&agrave; loại vắc xin ph&ograve;ng c&aacute;c bệnh c&uacute;m m&ugrave;a, được sử dụng rộng r&atilde;i để ph&ograve;ng ngừa c&uacute;m ở người lớn v&agrave; trẻ em tr&ecirc;n 3 tuổi.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ph&ograve;ng ngừa c&uacute;m cho trẻ em tr&ecirc;n 3 tuổi v&agrave; người lớn</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Liều lượng v&agrave; c&aacute;ch d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Người lớn v&agrave; trẻ em tr&ecirc;n 3 tuổi: Ti&ecirc;m bắp với thể t&iacute;ch 0.5ml.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Trẻ cần ti&ecirc;m vắc xin c&uacute;m GC Flu ngay từ 36 th&aacute;ng tuổi để ph&ograve;ng bệnh hiệu quả, lịch ti&ecirc;m vắc xin c&uacute;m GC Flu được khuyến c&aacute;o như sau:</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ từ 36 th&aacute;ng đến dưới 9 tuổi chưa từng ti&ecirc;m vắc xin c&uacute;m:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m 2 mũi c&aacute;ch nhau tối thiểu 1 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Sau đ&oacute; ti&ecirc;m nhắc lại 1 mũi hằng năm.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ tr&ecirc;n 9 tuổi v&agrave; người lớn:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m 1 mũi 0.5ml</li>
<li style="box-sizing: border-box; text-align: justify;">Sau đ&oacute; ti&ecirc;m nhắc lại h&agrave;ng năm.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Phụ nữ c&oacute; kế hoạch mang thai n&ecirc;n ti&ecirc;m vắc xin ph&ograve;ng c&uacute;m trước khi c&oacute; thai</span></em></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Người c&oacute; tiền sử sốc phản vệ hoặc dị ứng với th&agrave;nh phần của vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Người dị ứng với trứng g&agrave;, thịt g&agrave;, mọi sản phẩm từ thịt g&agrave;.</li>
<li style="box-sizing: border-box; text-align: justify;">Người c&oacute; triệu chứng động kinh trong v&ograve;ng 1 năm trước khi ti&ecirc;m chủng.</li>
<li style="box-sizing: border-box; text-align: justify;">Người bị rối loạn thần kinh.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng tại chỗ: nổi mẩn đỏ, sưng đau tại v&ugrave;ng ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">To&agrave;n th&acirc;n : sốt nhẹ, r&eacute;t run, đau đầu, mệt mỏi, buồn n&ocirc;n&hellip;</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c trường hợp rất hiếm gặp như: vi&ecirc;m n&atilde;o, dị ứng hay sốc phản vệ.</li>
<li style="box-sizing: border-box; text-align: justify;">Rối loạn hệ thần kinh tạm thời c&oacute; thể xảy ra, t&ecirc; liệt, đau d&acirc;y thần kinh&hellip;</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Chưa c&oacute; nghi&ecirc;n cứu đ&aacute;nh gi&aacute; việc ti&ecirc;m đồng thời vắc xin c&uacute;m với c&aacute;c vắc xin kh&aacute;c, trong trường hợp bắt buộc phải ti&ecirc;m n&ecirc;n ti&ecirc;m ở những vị tr&iacute; kh&aacute;c nhau</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c thuốc sau c&oacute; thể g&acirc;y tương t&aacute;c với GC FLU: Thuốc kiểm so&aacute;t bệnh động kinh (Phenytoin, carbamazepine, Phenobarbital); Theophyline; Warfarin; Immune globulin.</li>
<li style="box-sizing: border-box; text-align: justify;">Thuốc ức chế miễn dịch (corticosteroid, cyclosporine, thuốc chống ung thư (bao gồm cả xạ trị&hellip;). Vắc xin n&agrave;y c&oacute; thể d&ugrave;ng ở phụ nữ đang cho con b&uacute;.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lưu &yacute; khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Trước khi sử dụng cần kiểm tra h&igrave;nh thức của dung dịch nếu c&oacute; hạt hoặc đổi m&agrave;u, ngừng sử dụng.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin thường được ti&ecirc;m v&agrave;o vị tr&iacute; b&ecirc;n của bắp tay v&agrave; vị tr&iacute; ti&ecirc;m cần được khử tr&ugrave;ng bằng cồn hoặc cồn i-ốt.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng trộn lẫn với c&aacute;c loại vắc xin kh&aacute;c trong c&ugrave;ng một xilanh.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng sử dụng khi vắc xin bị đ&ocirc;ng đ&aacute; Vắc xin cần được lắc kỹ, đồng nhất trước khi sử dụng.</li>
<li style="box-sizing: border-box; text-align: justify;">Sản phẩm n&ecirc;n được sử dụng ngay sau khi mở.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Bảo quản ở nhiệt độ 2 &ndash; 8&ordm;C, kh&ocirc;ng để đ&ocirc;ng đ&aacute;, tr&aacute;nh tiếp x&uacute;c &aacute;nh s&aacute;ng.</li>
</ul>', '2021-07-01 04:05:16', '2021-07-04 22:07:56', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (24, 'Influvac 0.5ml', 22, 'Đang sử dụng', 286, '/storage/vaccine_photo/wlJBIO9BRKdBUGkdSwyd.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Lịch sử nh&acirc;n loại đ&atilde; chứng kiến nhiều đại dịch c&uacute;m giết chết h&agrave;ng triệu người. Thống k&ecirc; của Tổ chức Y tế thế giới cho thấy, mỗi năm c&oacute; từ 5 &ndash; 10% người lớn v&agrave; 15 &ndash; 42% trẻ em tr&ecirc;n to&agrave;n thế giới mắc bệnh c&uacute;m, trong đ&oacute; c&oacute; khoảng 250.000 &ndash; 500.000 người tử vong do căn bệnh n&agrave;y.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Ai cũng c&oacute; nguy cơ mắc bệnh c&uacute;m, kể cả người khỏe mạnh. Đặc biệt, bệnh c&uacute;m v&ocirc; c&ugrave;ng nguy hiểm với người gi&agrave;, trẻ em, người mắc bệnh mạn t&iacute;nh&hellip; Bệnh c&oacute; thể g&acirc;y bội nhiễm, vi&ecirc;m phổi nặng, suy hấp, suy đa tạng dẫn tới tử vong.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Hiện nay, ti&ecirc;m vắc xin c&uacute;m h&agrave;ng năm l&agrave; biện ph&aacute;p hiệu quả nhất để ph&ograve;ng ngừa c&uacute;m.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin c&uacute;m Influvac 0.5ml</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin Influvac 0.5ml được chỉ định để ph&ograve;ng ngừa c&uacute;m (đặc biệt ở người c&oacute; nguy cơ rủi ro tăng do biến chứng kết hợp như người tr&ecirc;n 65 tuổi, (b) Người lớn &amp; trẻ em: rối loạn mạn t&iacute;nh hệ h&ocirc; hấp/tim mạch kể cả hen, bệnh chuyển h&oacute;a mạn t&iacute;nh (như tiểu đường), suy thận mạn t&iacute;nh, suy giảm miễn dịch (do bệnh, d&ugrave;ng thuốc ức chế miễn dịch hoặc xạ trị), (c) Trẻ 6 th&aacute;ng-18 tuổi điều trị l&acirc;u d&agrave;i bằng thuốc c&oacute; chứa acid acetylsalicylic c&oacute; nguy cơ ph&aacute;t triển hội chứng Reye sau nhiễm khuẩn c&uacute;m.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Liều d&ugrave;ng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin c&uacute;m Influvac được khuyến c&aacute;o ti&ecirc;m cho trẻ từ 6 th&aacute;ng tuổi v&agrave; người lớn với lịch ti&ecirc;m như sau:</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ từ 6 th&aacute;ng tuổi đến dưới 9 tuổi chưa từng ti&ecirc;m vắc xin c&uacute;m:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m 2 mũi c&aacute;ch nhau tối thiểu 1 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Sau đ&oacute; ti&ecirc;m nhắc lại 1 mũi hằng năm.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">trong đ&oacute;: Trẻ từ 6 đến 35 th&aacute;ng tuổi: 0.25 ml; Trẻ &ge; 36 th&aacute;ng tuổi v&agrave; dưới 9 tuổi: 0.5ml</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ tr&ecirc;n 9 tuổi v&agrave; người lớn:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m 1 mũi 0.5ml</li>
<li style="box-sizing: border-box; text-align: justify;">Sau đ&oacute; ti&ecirc;m nhắc lại h&agrave;ng năm.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">&ndash; Phụ nữ c&oacute; kế hoạch mang thai n&ecirc;n ti&ecirc;m vắc xin ph&ograve;ng c&uacute;m trước khi c&oacute; thai hoặc 3 th&aacute;ng giữa thai kỳ.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp hoặc ti&ecirc;m s&acirc;u dưới da.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Mẫn cảm với th&agrave;nh phần vắc xin hoặc bất cứ th&agrave;nh phần n&agrave;o c&oacute; thể chỉ l&agrave; rất nhỏ như trứng (ovalbumin, protein g&agrave;), formaldehyde, cetyltrimethylamoni, bromide, polysorbat 80, hoặc gentamicin.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c bệnh nh&acirc;n c&oacute; triệu chứng sốt hoặc nhiễm tr&ugrave;ng cấp t&iacute;nh sẽ phải ho&atilde;n việc ti&ecirc;m chủng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Cảnh b&aacute;o:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Cũng như với tất cả c&aacute;c vắc xin ti&ecirc;m, điều trị nội khoa th&iacute;ch hợp v&agrave; việc gi&aacute;m s&aacute;t phải lu&ocirc;n lu&ocirc;n sẵn s&agrave;ng trong trường hợp xảy ra phản ứng qu&aacute; mẫn sau khi ti&ecirc;m vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m tĩnh mạch Influvac trong bất kỳ trường hợp n&agrave;o.</li>
<li style="box-sizing: border-box; text-align: justify;">Đ&aacute;p ứng kh&aacute;ng thể ở c&aacute;c bệnh nh&acirc;n/trẻ em c&oacute; ức chế miễn dịch nội sinh hoặc do điều trị c&oacute; thể l&agrave; kh&ocirc;ng đủ.</li>
<li style="box-sizing: border-box; text-align: justify;">Sự can thiệp của thử nghiệm huyết thanh học: Xem &ldquo;Tương t&aacute;c thuốc&rdquo;.</li>
<li style="box-sizing: border-box; text-align: justify;">Influvac kh&ocirc;ng c&oacute; hoặc c&oacute; ảnh hưởng kh&ocirc;ng đ&aacute;ng kể tới khả năng l&aacute;i xe v&agrave; vận h&agrave;nh m&aacute;y m&oacute;c.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m tĩnh mạch. Đ&aacute;p ứng kh&aacute;ng thể c&oacute; thể bị giảm ở bệnh nh&acirc;n c&oacute; ức chế miễn dịch nội sinh hoặc do điều trị.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Phản ứng phụ:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng tại chỗ: đỏ, đau, sưng, cứng vị tr&iacute; ti&ecirc;m</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng to&agrave;n th&acirc;n: sốt, kh&oacute; chịu, đầu đầu, đổ mồ h&ocirc;i, chứng đau cơ, đ&acirc;u khớp</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng bất lợi n&agrave;y thường mất đi trong v&ograve;ng 1-2 ng&agrave;y m&agrave; kh&ocirc;ng cần điều trị.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Influvac c&oacute; thể được ti&ecirc;m c&ugrave;ng l&uacute;c với c&aacute;c vắc xin kh&aacute;c. Việc ti&ecirc;m chủng phải được thực hiện tr&ecirc;n c&aacute;c chi kh&aacute;c nhau.</li>
<li style="box-sizing: border-box; text-align: justify;">Miễn dịch c&oacute; thể giảm nếu bệnh nh&acirc;n đang điều trị ức chế miễn dịch.</li>
<li style="box-sizing: border-box; text-align: justify;">Sau&nbsp; khi ti&ecirc;m vắc xin c&uacute;m: X&eacute;t nghiệm huyết thanh học (ELISA) c&oacute; thể bị dương t&iacute;nh giả trong x&eacute;t nghiệm ph&aacute;t hiện kh&aacute;ng thể chống lại HIV1, vi&ecirc;m gan C v&agrave; đặc biệt l&agrave; HTVL1. c&aacute;c phản ứng dương t&iacute;nh giả nhất thời c&oacute; thể l&agrave; do đ&aacute;p ứng IgM của vắc xin c&uacute;m</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Influvac phải được bảo quản tại 2&deg;C đến 8&deg;C (trong tủ lạnh).</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được đ&ocirc;ng băng.</li>
<li style="box-sizing: border-box; text-align: justify;">Bảo quản trong bao b&igrave; gốc. Tr&aacute;nh &aacute;nh s&aacute;ng.</li>
</ul>', '2021-07-01 04:05:16', '2021-07-03 20:44:06', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (25, 'Ivacflu-S 0,5ml', 22, 'Đang sử dụng', 286, '/storage/vaccine_photo/vgZSBTowG2Wv4KkOWeS2.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Bệnh c&uacute;m m&ugrave;a l&agrave; bệnh truyền nhiễm cấp t&iacute;nh với biểu hiện sốt, đau đầu, đau cơ, mệt mỏi, sổ mũi, đau họng v&agrave; ho.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">T&aacute;c nh&acirc;n g&acirc;y bệnh chủ yếu do c&aacute;c chủng vi r&uacute;t c&uacute;m A(H3N2), c&uacute;m A(H1N1), c&uacute;m B v&agrave; c&uacute;m C. Bệnh c&oacute; khả năng l&acirc;y nhiễm rất cao, qua đường h&ocirc; hấp, qua c&aacute;c giọt nhỏ nước bọt hay dịch tiết mũi họng do hắt hơi, ho khạc.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Th&ocirc;ng thường bệnh diễn biến nhẹ v&agrave; hồi phục trong v&ograve;ng 2-7 ng&agrave;y, nhưng đối với trẻ em, người lớn tuổi, người c&oacute; bệnh m&atilde;n t&iacute;nh về tim phổi, thận, bệnh chuyển h&oacute;a, thiếu m&aacute;u hoặc suy giảm miễn dịch&hellip;, bệnh c&oacute; thể diễn biến nặng hơn, dễ bị biến chứng v&agrave; c&oacute; thể dẫn đến tử vong.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Hiện nay, ti&ecirc;m vắc xin h&agrave;ng năm l&agrave; biện ph&aacute;p hiệu quả nhất để ph&ograve;ng ngừa c&uacute;m.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về vắc xin Ivacflu-S 0,5ml (Việt Nam) ph&ograve;ng bệnh c&uacute;m</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin Ivacflu-S 0,5ml (Việt Nam) l&agrave; loại vắc xin chuy&ecirc;n d&ugrave;ng để ph&ograve;ng c&aacute;c bệnh c&uacute;m m&ugrave;a, được sử dụng rộng r&atilde;i để ph&ograve;ng ngừa c&uacute;m ở người lớn từ 18 đến 60 tuổi.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Viện Vắc xin v&agrave; Sinh phẩm Y tế IVAC &ndash; Việt Nam</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">IVACFLU-S được chỉ định ph&ograve;ng ngừa c&uacute;m m&ugrave;a cho người lớn từ 18 tuổi đến 60 tuổi. Thời điểm ti&ecirc;m ngừa tốt nhất l&agrave; trước m&ugrave;a dịch (cụ thể theo khuyến c&aacute;o của b&aacute;c sĩ hoặc cơ sở y tế theo từng miền).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">C&aacute;ch d&ugrave;ng v&agrave; liều d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin được sử dụng qua đường ti&ecirc;m bắp. Vị tr&iacute; ti&ecirc;m: Cơ delta (bắp c&aacute;nh tay). Kh&ocirc;ng được ti&ecirc;m IVACFLU-S v&agrave;o mạch m&aacute;u.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin chưa được nghi&ecirc;n cứu ở đối tượng trẻ em, kh&ocirc;ng khuyến c&aacute;o sử dụng cho trẻ em.</li>
<li style="box-sizing: border-box; text-align: justify;">Người lớn (từ 18 tuổi đến 60 tuổi): Ti&ecirc;m 1 liều 0,5 ml vắc xin.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">C&oacute; tiền sử sốc phản vệ với vắc xin IVACFLU-S.</li>
<li style="box-sizing: border-box; text-align: justify;">Tiền sử mẫn cảm với bất cứ chủng virus c&uacute;m n&agrave;o trong th&agrave;nh phần vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Tiền sử mẫn cảm với cao su (của n&uacute;t lọ đựng vắc xin) hoặc c&aacute;c th&agrave;nh phần pha chế vắc xin như dung dịch PBS.</li>
<li style="box-sizing: border-box; text-align: justify;">Người c&oacute; hội chứng Guilain-Barre, c&oacute; rối loạn thần kinh.</li>
<li style="box-sizing: border-box; text-align: justify;">Người bị động kinh đang tiến triển hoặc c&oacute; tiền sử co giật.</li>
<li style="box-sizing: border-box; text-align: justify;">Người c&oacute; cơ địa mẫn cảm nặng với c&aacute;c vắc xin kh&aacute;c (đ&atilde; từng bị sốc phản vệ khi ti&ecirc;m vắc xin)</li>
<li style="box-sizing: border-box; text-align: justify;">Ho&atilde;n ti&ecirc;m chủng nếu người ti&ecirc;m c&oacute; t&igrave;nh trạng bệnh l&yacute; m&agrave; c&aacute;n bộ ti&ecirc;m chủng nhận thấy kh&ocirc;ng an to&agrave;n khi ti&ecirc;m vắc xin (sốt tr&ecirc;n 38<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C; bệnh nhiễm tr&ugrave;ng cấp t&iacute;nh&hellip;) hoặc kh&ocirc;ng đảm bảo hiệu quả của vắc xin (đang d&ugrave;ng thuốc ức chế miễn dịch tr&ecirc;n 14 ng&agrave;y, mắc lao thể hoạt động &hellip;).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Cảnh b&aacute;o v&agrave; thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin IVACFLU-S n&ecirc;n để đạt bằng nhiệt độ ph&ograve;ng ngay trước khi sử dụng. N&ecirc;n lắc kỹ trước khi sử dụng. Cần gi&aacute;m s&aacute;t chặt phản ứng qu&aacute; mẫn sau khi ti&ecirc;m vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Đ&aacute;p ứng miễn dịch c&oacute; thể kh&ocirc;ng đủ mức bảo vệ trong trường hợp suy giảm miễn dịch bẩm sinh hoặc mắc phải.<br style="box-sizing: border-box;" />Phụ nữ tuổi sinh đẻ tốt nhất n&ecirc;n ti&ecirc;m vắc xin c&uacute;m m&ugrave;a trước khi chuẩn bị mang thai.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng chống chỉ định đối với to&agrave;n bộ đối tượng c&oacute; bệnh m&atilde;n t&iacute;nh, đặc biệt n&ecirc;n ti&ecirc;m vắc xin IVACFLU-S cho đối tượng l&agrave;m việc ở c&aacute;c cơ sở x&atilde; hội đ&ocirc;ng người, nằm viện l&acirc;u ng&agrave;y. Trong những trường hợp n&agrave;y cần theo chỉ định của b&aacute;c sĩ.</li>
<li style="box-sizing: border-box; text-align: justify;">Người bị bệnh đường h&ocirc; hấp m&atilde;n t&iacute;nh như hội chứng COPD n&ecirc;n ti&ecirc;m vắc xin c&uacute;m m&ugrave;a h&agrave;ng năm theo chỉ định của b&aacute;c sĩ</li>
<li style="box-sizing: border-box; text-align: justify;">Nh&acirc;n vi&ecirc;n y tế v&agrave; người cao tuổi n&ecirc;n ti&ecirc;m vắc xin trước m&ugrave;a c&uacute;m h&agrave;ng năm.</li>
<li style="box-sizing: border-box; text-align: justify;">Đối tượng đi du lịch đến c&aacute;c nước v&ugrave;ng dịch n&ecirc;n ti&ecirc;m một liều &iacute;t nhất 7 ng&agrave;y trước khi đi. ( Nếu chưa ti&ecirc;m vắc xin c&uacute;m trong v&ograve;ng 1 năm trước đ&oacute;).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Sử dụng cho phụ nữ c&oacute; thai v&agrave; cho con b&uacute;:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng c&oacute; dữ liệu về sử dụng thuốc tr&ecirc;n phụ nữ c&oacute; thai, chỉ n&ecirc;n d&ugrave;ng thuốc nếu lợi &iacute;ch vượt trội so với nguy cơ. Đ&acirc;y l&agrave; vắc xin bất hoạt n&ecirc;n c&oacute; thể d&ugrave;ng cho phụ nữ c&oacute; thai, khi d&ugrave;ng phải c&oacute; &yacute; kiến b&aacute;c sĩ. Kh&ocirc;ng chống chỉ định ti&ecirc;m vắc xin c&uacute;m trong thời kỳ cho con b&uacute;, tuy nhi&ecirc;n n&ecirc;n hỏi &yacute; kiến b&aacute;c sĩ trước khi ti&ecirc;m.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn của thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">C&oacute; một số phản ứng tại chỗ như đau tại vị tr&iacute; ti&ecirc;m khi chạm phải, đau tại vị tr&iacute; ti&ecirc;m, rất thường xảy ra trong những ng&agrave;y đầu sau khi ti&ecirc;m, c&aacute;c triệu chứng tại chỗ kh&aacute;c như sưng, mảng cứng v&agrave; quầng đỏ c&oacute; thể thường xảy ra. C&aacute;c phản ứng to&agrave;n th&acirc;n như mệt mỏi, kh&oacute; chịu rất thường hay gặp. Triệu chứng ớn lạnh, buồn n&ocirc;n v&agrave; sốt nhẹ thường hay xảy ra. N&ocirc;n v&agrave; cảm gi&aacute;c sốt c&oacute; thể gặp nhưng &iacute;t xảy ra. Tất cả c&aacute;c phản ứng kh&ocirc;ng mong muốn đều ở mức độ nhẹ v&agrave; thường tự khỏi sau 1 &ndash; 2 ng&agrave;y, kh&ocirc;ng cần điều trị.</li>
<li style="box-sizing: border-box; text-align: justify;">Rối loạn hệ thần kinh &ndash; vận động tạm thời như đau cơ, đau đầu, đau khớp l&agrave; c&aacute;c biểu hiện thường hay bắt gặp. Cũng giống như c&aacute;c triệu chứng kh&ocirc;ng mong muốn kh&aacute;c c&aacute;c biểu hiện tr&ecirc;n thường nhẹ v&agrave; tự qua khỏi sau 1-2 ng&agrave;y, kh&ocirc;ng cần điều trị.</li>
<li style="box-sizing: border-box; text-align: justify;">Chưa ghi nhận c&aacute;c biểu hiện rối loạn thần kinh như t&ecirc; liệt, đau d&acirc;y thần kinh hay vi&ecirc;m đa rễ thần kinh ngoại bi&ecirc;n (hội chứng Guillian-Barre), nhưng cần ch&uacute; &yacute; v&igrave; trong y văn đ&atilde; c&oacute; b&aacute;o c&aacute;o, mặc d&ugrave; rất hiếm xảy ra. Khi c&oacute; c&aacute;c triệu chứng n&ecirc;u tr&ecirc;n cần th&ocirc;ng b&aacute;o cho b&aacute;c sĩ tại điểm ti&ecirc;m ph&ograve;ng v&agrave; cần thực hiện điều trị y tế th&iacute;ch hợp.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể gặp dị ứng hay phản ứng phản vệ nhưng hiếm gặp.Trong những trường hợp n&agrave;y cần được xử tr&iacute; cấp cứu kịp thời theo quy định về an to&agrave;n ti&ecirc;m chủng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Nhiệt độ bảo quản vắc xin từ + 2<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C đến + 8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C, tr&aacute;nh đ&ocirc;ng băng. Bảo quản vắc xin nguy&ecirc;n trong hộp để tr&aacute;nh &aacute;nh s&aacute;ng.</li>
</ul>', '2021-07-01 04:05:16', '2021-07-03 20:44:56', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (26, 'Rotavin M1', 9, 'Đang sử dụng', 126, '/storage/vaccine_photo/2jNQZ0IvUCU6sbFd2Gzb.png', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Ti&ecirc;u chảy cấp do virus Rota l&agrave; bệnh cấp t&iacute;nh do virus g&acirc;y n&ecirc;n. Bệnh thường gặp ở trẻ nhỏ với biểu hiện: n&ocirc;n &oacute;i, đi ngo&agrave;i ph&acirc;n lỏng, đau bụng, dẫn tới mất nước, rối loạn điện giải g&acirc;y trụy mạch v&agrave; tử vong nhanh.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Theo thống k&ecirc;, h&agrave;ng năm tr&ecirc;n thế giới c&oacute; khoảng 1,5 tỷ lượt trẻ dưới 5 tuổi mắc ti&ecirc;u chảy v&agrave; 4 triệu trẻ chết v&igrave; bệnh n&agrave;y, trong đ&oacute; 80% xảy ra ở trẻ dưới 2 tuổi.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Tại Việt Nam, c&oacute; đến 56% trẻ nhập viện do vi&ecirc;m dạ d&agrave;y ruột l&agrave; do nhiễm virus Rota, trong đ&oacute;, số trẻ chết do vi r&uacute;t Rota chiếm từ 4-8% trong tổng số trẻ dưới 5 tuổi bị chết v&igrave; mọi nguy&ecirc;n nh&acirc;n.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Uống vắc xin l&agrave; c&aacute;ch duy nhất gi&uacute;p trẻ ph&ograve;ng ngừa căn bệnh ti&ecirc;u chảy cấp g&acirc;y ra do Rotavirus. C&aacute;c chuy&ecirc;n gia khuyến c&aacute;o, trẻ n&ecirc;n uống vắc xin Rotavin (Việt Nam) sớm để ph&ograve;ng bệnh hiệu quả.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về vắc xin Rotavin M1 (Việt Nam)</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Rotavin-M1 l&agrave; vắc xin sống giảm độc lực. Vắc xin c&oacute; dung dịch m&agrave;u hồng, được sản xuất tr&ecirc;n d&acirc;y chuyền c&ocirc;ng nghệ hiện đại, đạt c&aacute;c ti&ecirc;u chuẩn của Tổ chức Y tế Thế giới về vắc xin uống.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Nghi&ecirc;n cứu v&agrave; sản xuất: Polyvac &ndash; Việt Nam</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Rotavin-M1 được chỉ định để ph&ograve;ng vi&ecirc;m dạ d&agrave;y ruột g&acirc;y ti&ecirc;u chảy cấp do Rotavirus.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch uống:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Vắc xin Rotavin-M1 gồm 2 liều:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Liều thứ 1: Bắt đầu khi trẻ được 6 tuần tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Liều thứ 2: Sau liều đầu ti&ecirc;n từ 1-2 th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Lưu &yacute;: N&ecirc;n cho trẻ ho&agrave;n th&agrave;nh uống vắc xin Rotavin-M1 trước 6 th&aacute;ng tuổi.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường uống:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Chỉ được d&ugrave;ng đường uống, kh&ocirc;ng được ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Liều uống: 2ml/liều.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Quy c&aacute;ch đ&oacute;ng g&oacute;i:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mỗi lọ vắc xin gồm 1 liều (2ml) được đ&oacute;ng trong 1 hộp nhỏ v&agrave; k&egrave;m theo hướng dẫn sử dụng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">C&aacute;ch d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Để tan băng vắc xin, lắc đều, sau đ&oacute; để tr&ecirc;n phiến đ&aacute; trước khi cho uống.</li>
<li style="box-sizing: border-box; text-align: justify;">D&ugrave;ng xi-lanh v&ocirc; tr&ugrave;ng h&uacute;t 2ml vắc xin, bỏ kim ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Cho trẻ dựa lưng v&agrave;o l&ograve;ng mẹ, đưa xi-lanh c&oacute; vắc xin v&agrave;o ph&iacute;a trong m&aacute;, từ từ cho trẻ uống hết lượng vắc xin.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng d&ugrave;ng Rotavin-M1 cho trẻ qu&aacute; mẫn sau khi uống liều vắc xin đầu ti&ecirc;n, hoặc qu&aacute; mẫn với bất cứ th&agrave;nh phần n&agrave;o của thuốc.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng sử dụng vắc xin Rotavin-M1 nếu trẻ c&oacute; bệnh l&yacute; nặng, cấp t&iacute;nh, sốt cao, trẻ đang bị ti&ecirc;u chảy hoặc n&ocirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng sử dụng Rotavin-M1 cho trẻ bị dị tật bẩm sinh đường ti&ecirc;u h&oacute;a, bị lồng ruột hay đang bị suy giảm miễn dịch nặng.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin kh&ocirc;ng dự kiến sử dụng ở người lớn. Kh&ocirc;ng c&oacute; chỉ định sử dụng vắc xin ở phụ nữ c&oacute; thai v&agrave; cho con b&uacute;.</li>
<li style="box-sizing: border-box; text-align: justify;">Việc cho con b&uacute; kh&ocirc;ng l&agrave;m giảm hiệu quả của vắc xin, n&ecirc;n vẫn c&oacute; thể cho con b&uacute; sau khi trẻ đ&atilde; sử dụng vắc xin.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Cần thận trọng đối với những trường hợp c&oacute; tiền sử dị ứng, co giật.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng n&ecirc;n cho uống vắc xin trong trường hợp đang sốt hay đang trong qu&aacute; tr&igrave;nh điều trị c&oacute; ảnh hưởng đến hệ miễn dịch (như d&ugrave;ng thuốc, truyền m&aacute;u hay c&aacute;c chế phẩm từ m&aacute;u&hellip;). Chỉ cho trẻ uống vắc xin sau khi đ&atilde; hết sốt &iacute;t nhất 3 ng&agrave;y v&agrave; sau khi kết th&uacute;c điều trị tối thiểu 4 tuần.</li>
<li style="box-sizing: border-box; text-align: justify;">Nếu trẻ trớ ra phần lớn lượng vắc xin ngay sau khi uống th&igrave; n&ecirc;n cho trẻ uống ngay liều vắc xin thay thế. Ch&uacute; &yacute; kh&ocirc;ng n&ecirc;n cho trẻ b&uacute; mẹ trước v&agrave; sau khi cho uống vắc xin 30 ph&uacute;t.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c triệu chứng thường gặp: N&ocirc;n, sốt, ti&ecirc;u chảy, đau bụng, ho, quấy kh&oacute;c&hellip; nhưng rất &iacute;t.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể sử dụng Rotavin-M1 c&ugrave;ng l&uacute;c với c&aacute;c vắc xin kh&aacute;c như: vắc xin bại liệt (IPV, OPV), vắc xin phối hợp 6 trong 1, 5 trong 1, vắc xin vi&ecirc;m gan B, vắc xin ph&ograve;ng c&aacute;c bệnh do Hib&hellip;</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Điều kiện bảo quản v&agrave; hạn d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Hạn sử dụng 24 th&aacute;ng kể từ ng&agrave;y sản xuất trong điều kiện nhiệt độ bảo quản -20 (+/-5 độ C).</li>
<li style="box-sizing: border-box; text-align: justify;">Hạn d&ugrave;ng 2 th&aacute;ng kể từ khi tan băng trong điều kiện bảo quản 2-8 độ C.</li>
</ul>', '2021-07-01 04:05:16', '2021-07-03 20:35:12', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (27, 'PHÒNG BỆNH VIÊM NÃO NHẬT BẢN', 0, null, null, null, null, null, '2021-07-03 19:47:58', '2021-07-03 19:47:58', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (28, 'Jevax', 27, 'Đang sử dụng', 245, '/storage/vaccine_photo/R5EGCRTzGol8Vt2dF9Km.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vi&ecirc;m n&atilde;o Nhật Bản l&agrave; căn bệnh nguy hiểm, rất kh&oacute; ph&aacute;t hiện do triệu chứng ban đầu rất giống với c&aacute;c bệnh vi&ecirc;m nhiễm kh&aacute;c.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Bệnh vi&ecirc;m n&atilde;o Nhật Bản c&oacute; thể g&acirc;y tử vong chỉ trong 24h. Nếu được điều trị kịp thời, bệnh cũng c&oacute; thể để lại nhiều di chứng nghi&ecirc;m trọng như như di chứng vận động, thần kinh, h&ocirc; hấp, c&oacute; trẻ phải sống nhờ m&aacute;y m&oacute;c suốt đời&hellip; trở th&agrave;nh g&aacute;nh nặng cho gia đ&igrave;nh v&agrave; x&atilde; hội.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Để ph&ograve;ng bệnh vi&ecirc;m n&atilde;o Nhật Bản, ti&ecirc;m vắc xin l&agrave; biện ph&aacute;p quan trọng, hiệu quả v&agrave; khả thi nhất hiện nay.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin Jevax (Việt Nam)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin ph&ograve;ng bệnh vi&ecirc;m n&atilde;o Nhật Bản Jevax được sản xuất tại Việt Nam, chỉ định để dự ph&ograve;ng bệnh vi&ecirc;m n&atilde;o Nhật Bản cho mọi đối tượng người lớn v&agrave; trẻ em từ 12 th&aacute;ng tuổi trở l&ecirc;n.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vabiotech &ndash; Việt Nam</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Jevax l&agrave; vắc xin được chỉ định để ph&ograve;ng vi&ecirc;m n&atilde;o Nhật bản cho mọi đối tượng v&agrave; cho trẻ em từ 12 th&aacute;ng tuổi trở l&ecirc;n</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m chủng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Lịch cơ bản:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: l&agrave; liều đầu ti&ecirc;n khi ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: sau mũi 1 khoảng 2 tuần.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: sau mũi 2 l&agrave; một năm.</li>
<li style="box-sizing: border-box; text-align: justify;">Sau 3 năm ti&ecirc;m nhắc lại một liều để duy tr&igrave; miễn dịch hoặc những người c&oacute; thể trạng miễn dịch tốt th&igrave; ti&ecirc;m nhắc lại trước khi c&oacute; dịch vi&ecirc;m n&atilde;o xảy ra.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Liều ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Trẻ em từ &ge;12 th&aacute;ng đến &le;36 th&aacute;ng (nhỏ hơn hoặc bằng 3 tuổi): d&ugrave;ng liều 0.5ml.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ em từ &ge;36 th&aacute;ng (lớn hơn 3 tuổi v&agrave; người lớn): d&ugrave;ng liều 1ml.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m dưới da</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Người nhạy cảm với bất kỳ th&agrave;nh phần n&agrave;o trong vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Người bị bệnh tim, gan, thận.</li>
<li style="box-sizing: border-box; text-align: justify;">Mệt mỏi, sốt cao hoặc nhiễm tr&ugrave;ng tiến triển.</li>
<li style="box-sizing: border-box; text-align: justify;">Người đang mắc bệnh tiểu đường hoặc suy dinh dưỡng.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh ung thư m&aacute;u v&agrave; c&aacute;c bệnh &aacute;c t&iacute;nh n&oacute;i chung.</li>
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ c&oacute; thai.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh qu&aacute; mẫn.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Thường gặp l&agrave; sưng, đau v&agrave; nổi ban đỏ tại nơi ti&ecirc;m. Những phản ứng n&agrave;y thường sẽ tự hết sau 1-2 ng&agrave;y.</li>
<li style="box-sizing: border-box; text-align: justify;">Phản ứng to&agrave;n th&acirc;n thường gặp như sốt, đau đầu, buồn n&ocirc;n, ch&oacute;ng mặt c&oacute; thể xảy ra với một số người</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Jevax được bảo quản ở nhiệt độ từ 2<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C đến 8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C v&agrave; kh&ocirc;ng được đ&ocirc;ng băng</li>
</ul>', '2021-07-03 19:48:19', '2021-07-03 19:49:31', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (29, 'Johnson & Johnson''s Janssen', 1, 'Chưa được sử dụng tại Việt Nam', null, '/storage/vaccine_photo/43h4gbtrbRdIx4JYqdO8.jpg', null, null, '2021-07-03 19:52:30', '2021-07-04 22:07:43', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (30, 'PHÒNG BỆNH VIÊM GAN A+B', 0, null, null, null, null, null, '2021-07-03 19:57:13', '2021-07-03 19:57:13', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (34, 'Twinrix', 30, 'Đang sử dụng', null, '/storage/vaccine_photo/7N0jgoLuQk33IsqDZVGB.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Việt Nam đang thuộc nh&oacute;m nước mắc ung thư gan cao nhất thế giới. Trung b&igrave;nh cứ 100.000 người Việt th&igrave; c&oacute; 23,2 người bị ung thư gan, ở cả hai giới, nguy cơ ở đ&agrave;n &ocirc;ng gấp ba lần phụ nữ. Bệnh kh&oacute; ph&aacute;t hiện sớm n&ecirc;n tỷ lệ chữa khỏi thấp.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vi&ecirc;m gan A (Hepatitis A) l&agrave; một bệnh truyền nhiễm cấp t&iacute;nh tại gan, g&acirc;y ra bởi virus vi&ecirc;m gan A, bệnh c&oacute; thể g&acirc;y biến chứng dẫn đến suy tim. Trong khi đ&oacute;, vi&ecirc;m gan B được xem l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y ung thư h&agrave;ng đầu, xếp thứ 2 chỉ sau thuốc l&aacute;.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Hiện nay, bệnh vi&ecirc;m gan A, B đ&atilde; c&oacute; thể ph&ograve;ng ngừa được bằng vắc xin. L&agrave; vắc xin duy nhất tr&ecirc;n thế giới ph&ograve;ng được bệnh vi&ecirc;m gan A v&agrave; vi&ecirc;m gan B, vắc xin Twinrix (Bỉ) với c&ocirc;ng dụng &ldquo;2 trong 1&rdquo; mang đến nhiều c&ocirc;ng dụng vượt trội với hiệu quả bảo vệ cao v&agrave; l&acirc;u d&agrave;i.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin Twinrix (Bỉ)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Glaxo Smith Kline (Bỉ)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Twinrix l&agrave; vắc xin được chỉ định để ph&ograve;ng hai bệnh vi&ecirc;m gan A v&agrave; vi&ecirc;m gan B ở trẻ em tr&ecirc;n 1 tuổi v&agrave; người lớn chưa c&oacute; miễn dịch.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m chủng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; Lịch cơ bản đối với người lớn v&agrave; trẻ &ge; 16 tuổi:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: l&agrave; liều đầu ti&ecirc;n khi ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: sau mũi 1 khoảng 1 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: sau mũi 2 khoảng 5 th&aacute;ng</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; Lịch cơ bản đối với trẻ em từ 1 tuổi đến 15 tuổi:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: l&agrave; liều đầu ti&ecirc;n khi ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: sau mũi 1 khoảng 6 th&aacute;ng đến 12 th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; Khi đ&atilde; bắt đầu liệu tr&igrave;nh với Twinrix th&igrave; phải d&ugrave;ng c&ugrave;ng một loại vắc xin cho đến hết. Liều củng cố c&oacute; thể d&ugrave;ng loại ri&ecirc;ng lẻ.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Người nhạy cảm với bất kỳ th&agrave;nh phần n&agrave;o trong vắc xin hoặc c&oacute; biểu hiện qu&aacute; mẫn với vắc xin ph&ograve;ng vi&ecirc;m gan B v&agrave; vi&ecirc;m gan A đơn lẻ.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Người đang sốt cao, cấp t&iacute;nh.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin kh&ocirc;ng ph&ograve;ng được bệnh vi&ecirc;m gan do c&aacute;c t&aacute;c nh&acirc;n kh&aacute;c như vi&ecirc;m gan C v&agrave; vi&ecirc;m gan E.</li>
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ c&oacute; thai v&agrave; cho con b&uacute;: kh&ocirc;ng c&oacute; chống chỉ định nhưng phải c&oacute; sự c&acirc;n nhắc của chuy&ecirc;n gia y tế.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng tại chỗ ti&ecirc;m: sưng, đau, đỏ da.</li>
<li style="box-sizing: border-box; text-align: justify;">Phản ứng to&agrave;n th&acirc;n: mệt mỏi, đau đầu, c&oacute; thể sốt.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; cảm gi&aacute;c buồn n&ocirc;n.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin được bảo quản ở nhiệt độ từ 2<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C đến 8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C v&agrave; kh&ocirc;ng được đ&ocirc;ng băng</li>
</ul>', '2021-07-03 20:02:30', '2021-07-04 22:12:18', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (35, 'PHÒNG BỆNH VIÊM GAN A', 0, null, null, null, null, null, '2021-07-03 20:03:28', '2021-07-03 20:03:28', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (36, 'Avaxim 80U', 35, 'Đang sử dụng', null, '/storage/vaccine_photo/JkskyXaD6z1DyjXq67o2.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Năm 1973, c&aacute;c nh&agrave; khoa học đ&atilde; quan s&aacute;t được m&ocirc;̣t loại si&ecirc;u vi g&acirc;y bệnh t&ecirc;n l&agrave; si&ecirc;u vi vi&ecirc;m gan A. Si&ecirc;u vi vi&ecirc;m gan A c&oacute; khả năng sống tr&ecirc;n những vật dụng kh&ocirc; r&aacute;o ở nhiệt độ ph&ograve;ng trong nhiều tuần và bị hủy trong 5 ph&uacute;t ở nhiệt độ s&ocirc;i. Si&ecirc;u vi n&agrave;y c&oacute; thể tồn tại trong s&ograve;, ốc, đất c&aacute;t, nước biển, nước thải&hellip; nhiều ng&agrave;y đến nhiều tuần m&agrave; vẫn giữ nguy&ecirc;n t&iacute;nh l&acirc;y nhiễm của nó.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vi&ecirc;m gan si&ecirc;u vi A l&acirc;y nhiễm qua đường ăn uống, nguồn nước, m&ocirc;i trường sống n&ecirc;n nguy cơ l&acirc;y bệnh rất dễ d&agrave;ng. Các nguy&ecirc;n nh&acirc;n thường gặp: l&acirc;y nhiễm từ người sang người qua đường ti&ecirc;u h&oacute;a, ăn hải sản kh&ocirc;ng nấu ch&iacute;n, vệ sinh c&aacute; nh&acirc;n kh&ocirc;ng tốt, ăn chung b&aacute;t đũa, sống đ&ocirc;ng đ&uacute;c chật hẹp, m&ocirc;i trường sống &ocirc; nhiễm, nguồn nước sinh hoạt kh&ocirc;ng đảm bảo, tiếp x&uacute;c với người bị bệnh vi&ecirc;m gan si&ecirc;u vi A&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bệnh vi&ecirc;m gan si&ecirc;u vi A thường kh&ocirc;ng g&acirc;y vi&ecirc;m gan mạn t&iacute;nh, do đ&oacute; &iacute;t nguy cơ diễn tiến th&agrave;nh xơ gan, ung thư gan. Tuy nhi&ecirc;n, vẫn c&oacute; một số &iacute;t trường hợp diễn biến sang thể &aacute;c t&iacute;nh g&acirc;y tử vong cho bệnh nh&acirc;n. Ở người gi&agrave; v&agrave; người bị c&aacute;c bệnh nền như bệnh gan, suy tim ứ huyết, tiểu đường v&agrave; thiếu m&aacute;u, thời gian hồi phục c&oacute; thể k&eacute;o d&agrave;i hơn v&agrave; diễn tiến bệnh c&oacute; thể nặng hơn.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Cho đến nay, vẫn chưa c&oacute; phương ph&aacute;p điều trị đặc hiệu bệnh vi&ecirc;m gan A, v&igrave; thế để tr&aacute;nh l&acirc;y nhiễm bệnh gan v&agrave; tổn thương gan, việc ti&ecirc;m ngừa vắc xin vi&ecirc;m gan A l&agrave; bước đơn giản v&agrave; hiệu quả nhất.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin AVAXIM 80U (Ph&aacute;p)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin AVAXIM 80U ph&ograve;ng ngừa vi&ecirc;m gan si&ecirc;u vi A được khuyến c&aacute;o chủng ngừa cho trẻ từ 12 th&aacute;ng đến dưới 15 tuổi với ph&aacute;c đồ 2 mũi.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Sanofi Pasteur (Ph&aacute;p)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Avaxim 80U được d&ugrave;ng để ph&ograve;ng l&acirc;y nhiễm virus vi&ecirc;m gan A&nbsp; cho trẻ từ 12 th&aacute;ng đến tr&ograve;n 15 tuổi.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m chủng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Trẻ em tr&ecirc;n 12 th&aacute;ng đến 15 tuổi: Một liều Avaxim 80U/0.5ml. Liều nhắc lại sau 6-18 th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp v&agrave;o cơ delta tr&ecirc;n c&aacute;nh tay.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box;"><span style="box-sizing: border-box;">Ho&atilde;n việc ti&ecirc;m chủng nếu c&oacute; sốt hay nhiễm tr&ugrave;ng cấp t&iacute;nh. Bệnh m&atilde;n t&iacute;nh trong giai đoạn tiến triển.</span></li>
<li style="box-sizing: border-box;"><span style="box-sizing: border-box;">kh&ocirc;ng ti&ecirc;m cho người bị dị ứng với hoạt chất, với bất kỳ th&agrave;nh phần t&aacute; dược n&agrave;o c&oacute; trong vắc xin, với neomycin, với polysorbate hoặc nếu trước đ&acirc;y đ&atilde; từng bị mẩn m&atilde;n sau khi ti&ecirc;m vắc xin n&agrave;y.</span></li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi&nbsp; sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Trẻ bị suy giảm miễn dịch</li>
<li style="box-sizing: border-box; text-align: justify;">T&aacute;c dụng ph&ograve;ng bệnh sẽ mất đi nếu ti&ecirc;m vắc xin trong thời kỳ ủ bệnh</li>
<li style="box-sizing: border-box; text-align: justify;">Trong một số trường hợp đặc biệt, những người bị giảm tiểu cầu hay c&oacute; nguy cơ bị chảy m&aacute;u, th&igrave; c&oacute; thể ti&ecirc;m bằng đường ti&ecirc;m dưới da.</li>
<li style="box-sizing: border-box; text-align: justify;">Giống như khi ti&ecirc;m c&aacute;c vắc xin kh&aacute;c, lu&ocirc;n phải theo d&otilde;i v&agrave; ph&ograve;ng ngừa phản ứng c&oacute; hại, c&oacute; sẵn thuốc cấp cứu chống phản ứng phản vệ sau khi&nbsp; ti&ecirc;m vắc xin.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Đ&aacute;p ứng miễn dịch c&oacute; thể giảm trong trường hợp đang d&ugrave;ng thuốc ức chế miễn dịch.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin c&oacute; thể ti&ecirc;m c&ugrave;ng l&uacute;c, tại c&aacute;c vị tr&iacute; kh&aacute;c nhau với c&aacute;c vắc xin kh&aacute;c như bạch hầu, ho g&agrave;, uốn v&aacute;n, Hib, bại liệt.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng tại chỗ ti&ecirc;m: Đau tại vết ti&ecirc;m, đ&ocirc;i khi c&oacute; quầng đỏ, sưng, nốt cứng</li>
<li style="box-sizing: border-box; text-align: justify;">Phản ứng to&agrave;n th&acirc;n: Sốt nhẹ, đau đầu, đau cơ, mệt mỏi, rối loạn ti&ecirc;u h&oacute;a, ph&aacute;t ban&hellip;</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c t&aacute;c dụng kh&ocirc;ng mong muốn n&agrave;y đều ở mức nhẹ hoặc vừa phải v&agrave; sẽ tự khỏi.<span style="box-sizing: border-box; font-weight: bold;"><br style="box-sizing: border-box;" /></span></li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin được bảo quản ở nhiệt độ từ +2<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C đến +8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C, tr&aacute;nh &aacute;nh s&aacute;ng, kh&ocirc;ng được đ&ocirc;ng băng.</li>
</ul>', '2021-07-03 20:04:33', '2021-07-04 22:09:09', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (37, 'PHÒNG BỆNH VIÊM GAN B', 0, null, null, null, null, null, '2021-07-03 20:05:07', '2021-07-03 20:05:07', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (38, 'Engerix B', 37, 'Đang sử dụng', null, '/storage/vaccine_photo/Ru88bbzrnPGHON7Xb72W.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">10 triệu người được ghi nhận nhiễm virus vi&ecirc;m gan B. 8 triệu người đang trong t&igrave;nh trạng vi&ecirc;m gan xơ gan hay ung thư gan. 22.000 người tử vong v&igrave; ung thư gan mỗi năm. Đ&acirc;y l&agrave; con số b&aacute;o động về t&igrave;nh trạng nhiễm bệnh vi&ecirc;m gan B ở nước ta, nguy&ecirc;n nh&acirc;n ch&iacute;nh g&acirc;y n&ecirc;n Ung thư gan &ndash; căn bệnh c&oacute; tỷ lệ mắc mới v&agrave; g&acirc;y tử vong h&agrave;ng đầu tại Việt Nam. Theo thống k&ecirc;, trung b&igrave;nh cứ 100.000 người Việt th&igrave; c&oacute; 23,2 người bị ung thư gan.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vi&ecirc;m gan B (hay c&ograve;n gọi l&agrave; vi&ecirc;m gan si&ecirc;u vi B) l&agrave; t&igrave;nh trạng tế b&agrave;o gan bị vi&ecirc;m nhiễm hoặc hoại tử cấp t&iacute;nh hay mạn t&iacute;nh do mắc phải virus vi&ecirc;m gan B. Người Việt Nam c&oacute; tỷ lệ nhiễm vi&ecirc;m gan B rất cao, nếu kh&ocirc;ng được ph&ograve;ng ngừa v&agrave; điều trị bệnh, 25% trong tổng số bệnh nh&acirc;n mắc vi&ecirc;m gan si&ecirc;u vi B sẽ ph&aacute;t sinh những vấn đề nghi&ecirc;m trọng về gan, kể cả ung thư gan.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vi&ecirc;m gan B l&agrave; bệnh dễ l&acirc;y nhiễm v&agrave; để lại hậu quả nặng nề, tuy nhi&ecirc;n bệnh ho&agrave;n to&agrave;n c&oacute; thể ph&ograve;ng ngừa được nhờ vắc xin ph&ograve;ng vi&ecirc;m gan B. Trẻ sơ sinh được khuyến c&aacute;o n&ecirc;n ti&ecirc;m vắc xin vi&ecirc;m gan B trong 24h đầu sau sinh, nếu trẻ được sinh ra từ mẹ nhiễm vi&ecirc;m gan B cần ti&ecirc;m th&ecirc;m 1 mũi huyết thanh ph&ograve;ng vi&ecirc;m gan B.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin Engerix B (Bỉ)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin Engerix B 10mcg/0.5ml của Bỉ ph&ograve;ng ngừa vi&ecirc;m gan B được d&ugrave;ng để chủng ngừa cho trẻ sơ sinh đến tr&ograve;n 19 tuổi.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin Engerix B 20mcg/0.5ml của Bỉ ph&ograve;ng ngừa vi&ecirc;m gan B được d&ugrave;ng để chủng ngừa cho người từ 20 tuổi trở l&ecirc;n.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Glaxo Smith Kline (Bỉ)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Engerix-B ph&ograve;ng bệnh vi&ecirc;m gan B cho tất cả c&aacute;c đối tượng c&oacute; nguy cơ tiếp x&uacute;c với virus vi&ecirc;m gan B ở tất cả c&aacute;c lứa tuổi: trẻ sơ sinh, trẻ nhỏ, thanh thiếu ni&ecirc;n, người lớn thuộc nh&oacute;m nguy cơ cao.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin liều 20 mcg: một liều 20 mcg HBsAg (trong 1 ml &ndash; huyền dịch) được d&ugrave;ng cho đối tượng từ 20 tuổi trở l&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin liều 10 mcg: liều 10 mcg HBsAg(trong 0,5 ml huyền dịch) được d&ugrave;ng cho trẻ em từ sơ sinh đến 19 tuổi (bao gồm cả tuổi 19).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Lịch ti&ecirc;m chủng cơ bản: c&oacute; thể lựa chọn 1 trong 2 c&aacute;ch ti&ecirc;m sau:</span></em></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">C&aacute;ch 1: hiệu gi&aacute; kh&aacute;ng thể đạt nhanh.</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: lần đầu đến ti&ecirc;m. (Với trẻ sơ sinh l&agrave; th&aacute;ng đầu ti&ecirc;n sau khi sinh)</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: sau mũi 1 một th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: sau mũi 2 một th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 4 sau mũi 1 mười hai th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">C&aacute;ch 2: hiệu gi&aacute; kh&aacute;ng thể đạt cao sau 3 mũi ti&ecirc;m.</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: lần đầu đến ti&ecirc;m. (Với trẻ sơ sinh l&agrave; th&aacute;ng đầu ti&ecirc;n sau khi sinh)</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: sau mũi 1 một th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: sau mũi 1 s&aacute;u th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Lịch chủng ngừa nhanh: d&agrave;nh cho c&aacute;c đối tượng cần hiệu quả bảo vệ nhanh (bị đ&acirc;m phải kim ti&ecirc;m nghi ngờ nhiễm virus vi&ecirc;m gan B; hoặc chuẩn bị đi v&agrave;o v&ugrave;ng c&oacute; tỷ lệ mắc bệnh vi&ecirc;m gan B cao&hellip;)</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: lần đầu đến ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: sau mũi 1 bảy ng&agrave;y.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: sau mũi 2 hai mốt ng&agrave;y (21 ng&agrave;y).</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 4: sau mũi 1 mười hai th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Engerix B được chỉ định ti&ecirc;m bắp (v&ugrave;ng cơ delta). Ngoại lệ với những bệnh nh&acirc;n bị rối loạn chảy m&aacute;u hay giảm tiểu cầu c&oacute; thể ti&ecirc;m dưới da</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng n&ecirc;n d&ugrave;ng Engerix-B cho những đối tượng được biết l&agrave; qu&aacute; mẫn cảm với một trong c&aacute;c th&agrave;nh phần của vaccin, hoặc những đối tượng c&oacute; biểu hiện mẫn cảm với vắc xin ở lần ti&ecirc;m trước.</li>
<li style="box-sizing: border-box; text-align: justify;">Nhiễm HIV kh&ocirc;ng được xem l&agrave; chống chỉ định đối với việc chủng ngừa vi&ecirc;m gan B.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">N&ecirc;n ho&atilde;n ti&ecirc;m vắc xin vi&ecirc;m gan B cho c&aacute;c đối tượng đang bị sốt hoặc nhiễm tr&ugrave;ng cấp t&iacute;nh. Tuy nhi&ecirc;n những trường hợp nhiễm tr&ugrave;ng nhẹ th&igrave; kh&ocirc;ng c&oacute; chống chỉ định ti&ecirc;m ph&ograve;ng vắc xin vi&ecirc;m gan B. Do thời kỳ ủ bệnh của vi&ecirc;m gan B d&agrave;i, c&oacute; thể v&agrave;o l&uacute;c chủng ngừa bệnh nh&acirc;n đ&atilde; bị nhiễm virus m&agrave; kh&ocirc;ng biết; trong những trường hợp như vậy, vắc xin c&oacute; thể kh&ocirc;ng ngăn ngừa được sự l&acirc;y nhiễm vi&ecirc;m gan B.</li>
<li style="box-sizing: border-box; text-align: justify;">Sự đ&aacute;p ứng miễn dịch của vắc xin vi&ecirc;m gan B c&ograve;n phụ thuộc v&agrave;o c&aacute;c yếu tố: tuổi cao (như người tr&ecirc;n 40 tuổi), nam giới tr&ecirc;n 40 tuổi, bệnh b&eacute;o ph&igrave;, bệnh tiểu đường, th&oacute;i quen h&uacute;t thuốc l&aacute;, đường ti&ecirc;m kh&ocirc;ng th&iacute;ch hợp (m&ocirc;ng, trong da), người nhiễm HIV,&hellip; Những đối tượng n&agrave;y thường c&oacute; đ&aacute;p ứng miễn dịch k&eacute;m hơn, v&igrave; vậy n&ecirc;n c&acirc;n nhắc liều bổ sung.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box;">Phản ứng tại chỗ ti&ecirc;m: đau, đỏ,, chai cứng chỗ ti&ecirc;m.</li>
<li style="box-sizing: border-box;">Phản ứng to&agrave;n th&acirc;n: mệt mỏi, sốt,, nhức đầu, ch&oacute;ng mặt, buồn n&ocirc;n, ti&ecirc;u chảy, đau bụng&hellip;</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể d&ugrave;ng đồng thời vắc xin Engerix B với huyết thanh kh&aacute;ng vi&ecirc;m gan B nhưng phải ti&ecirc;m v&agrave;o 2 vị tr&iacute; kh&aacute;c nhau.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể ti&ecirc;m Engerix B với c&aacute;c vắc xin bạch hầu- ho g&agrave;- uốn v&aacute;n hoặc sởi- quai bị- rubella v&agrave; vắc xin ph&ograve;ng lao; tuy nhi&ecirc;n mỗi vắc xin phải ti&ecirc;m v&agrave;o c&aacute;c vị tr&iacute; kh&aacute;c nhau.</li>
<li style="box-sizing: border-box; text-align: justify;">Khả năng thay thế: vắc xin Engerix B c&oacute; thể thay thế c&aacute;c vắc xin vi&ecirc;m gan B kh&aacute;c c&oacute; nguồn gốc từ huyết tương hoặc bằng c&ocirc;ng nghệ di truyền</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Engerix B được bảo quản ở nhiệt độ từ +2<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C đến +8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C v&agrave; kh&ocirc;ng để đ&ocirc;ng băng.</li>
</ul>', '2021-07-03 20:05:19', '2021-07-04 22:11:12', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (39, 'PHÒNG BỆNH LAO', 0, null, null, null, null, null, '2021-07-03 20:06:16', '2021-07-03 20:06:16', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (40, 'BCG', 39, 'Đang sử dụng', 286, '/storage/vaccine_photo/9US4ZMRMyTsi0Segzu02.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Mỗi năm tr&ecirc;n thế giới c&oacute; hơn 1 triệu người tử vong do bệnh Lao. Căn bệnh n&agrave;y được xếp v&agrave;o top 10 căn bệnh truyền nhiễm nguy hiểm v&agrave; g&acirc;y tử vong nhiều nhất hiện nay.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bệnh Lao phổi thường gặp nhất với những triệu chứng như ho li&ecirc;n tục k&eacute;o d&agrave;i kh&ocirc;ng khỏi, sốt cao, đau ngực, kh&oacute; thở&hellip; Bệnh nh&acirc;n nếu như kh&ocirc;ng được ph&aacute;t hiện v&agrave; điều trị điều trị kịp thời dễ gặp c&aacute;c biến chứng đe dọa trực tiếp đến sức khỏe cũng như l&agrave; t&iacute;nh mạng như: ho ra m&aacute;u, tr&agrave;n kh&iacute; m&agrave;ng phổi, suy h&ocirc; hấp, gi&atilde;n phế quản, u nấm phổi&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bệnh ho&agrave;n to&agrave;n c&oacute; thể ph&ograve;ng ngừa bằng vắc xin BCG, l&agrave; loại vắc xin ph&ograve;ng bệnh Lao được sản xuất tại Việt Nam. Trẻ sơ sinh trong v&ograve;ng 1 th&aacute;ng đầu sau sinh cần được ti&ecirc;m vắc xin BCG c&agrave;ng sớm c&agrave;ng tốt.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin BCG (Việt Nam) ph&ograve;ng bệnh lao</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin BCG đặc biệt hiệu quả trong việc ph&ograve;ng ngừa c&aacute;c h&igrave;nh th&aacute;i lao nguy hiểm, trong đ&oacute; c&oacute; lao vi&ecirc;m m&agrave;ng n&atilde;o với độ bảo vệ l&ecirc;n tới 70%. Vắc xin thường được khuyến c&aacute;o cho trẻ sơ sinh v&agrave; trẻ nhỏ, chỉ cần ti&ecirc;m 1 liều duy nhất l&agrave; đ&atilde; c&oacute; thể bảo vệ sức khỏe trọn đời, kh&ocirc;ng cần ti&ecirc;m nhắc lại.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Sản xuất tại: Việt Nam</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">BCG được chỉ định ph&ograve;ng bệnh lao ở trẻ em</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m chủng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin BCG được chỉ định ti&ecirc;m cho trẻ trong v&ograve;ng 30 ng&agrave;y sau khi sinh v&agrave; c&agrave;ng sớm c&agrave;ng tốt.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin BCG chỉ ti&ecirc;m một liều duy nhất, kh&ocirc;ng cần ti&ecirc;m nhắc lại.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m trong da ch&iacute;nh x&aacute;c, ở mặt ngo&agrave;i ph&iacute;a tr&ecirc;n c&aacute;nh tay hoặc vai tr&aacute;i.</li>
<li style="box-sizing: border-box; text-align: justify;">Nh&acirc;n vi&ecirc;n y tế cần sử dụng bơm kim ti&ecirc;m ri&ecirc;ng để ti&ecirc;m vắc xin BCG</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m vắc xin ph&ograve;ng lao cho trẻ c&oacute; dấu hiệu hoặc triệu chứng bệnh AIDS.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phần lớn trẻ em đều c&oacute; phản ứng tại vết ti&ecirc;m, th&ocirc;ng thường ngay sau khi ti&ecirc;m BCG sẽ xuất hiện một nốt nhỏ tại chỗ ti&ecirc;m v&agrave; biến mất sau khoảng 30 ph&uacute;t. Sau khoảng 2 tuần sẽ xuất hiện một vết lo&eacute;t đỏ c&oacute; k&iacute;ch thước bằng đầu b&uacute;t ch&igrave;. Sau đ&oacute; 2 tuần vết lo&eacute;t tự l&agrave;nh tạo n&ecirc;n một vết sẹo nhỏ c&oacute; đường k&iacute;nh 5mm.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng c&oacute; thể gặp như &aacute;p xe, nổi hạch n&aacute;ch thường do sử dụng bơm ti&ecirc;m kh&ocirc;ng v&ocirc; tr&ugrave;ng, ti&ecirc;m qu&aacute; nhiều hay ti&ecirc;m kh&ocirc;ng đ&uacute;ng kỹ thuật.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng nặng khi ti&ecirc;m BCG thường rất &iacute;t gặp, chỉ c&oacute; 1/1.000.000 người bị nhiễm lao sau khi ti&ecirc;m BCG, hay xảy ra ở những trường hợp nhiễm HIV hoặc suy giảm miễn dịch mắc phải.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin BCG được bảo quản ở nhiệt độ 2-8 độ C (vắc xin kh&ocirc;ng bị hỏng bởi đ&ocirc;ng băng nhưng dung m&ocirc;i th&igrave; kh&ocirc;ng được đ&ocirc;ng băng). Sau khi ho&agrave;n nguy&ecirc;n dung dịch ti&ecirc;m cần được bảo quản ở nhiệt độ 2-8 độ C trong v&ograve;ng 6 giờ. Phần c&ograve;n lại của lọ vắc xin sau mỗi buổi ti&ecirc;m chủng hoặc sau 6 giờ cần phải hủy bỏ.</li>
</ul>', '2021-07-03 20:06:31', '2021-07-04 22:08:52', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (41, 'PHÒNG BỆNH SỞI – QUAI BỊ – RUBELLA', 0, null, null, null, null, null, '2021-07-03 20:07:08', '2021-07-03 20:07:08', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (42, 'MMR II', 41, 'Đang sử dụng', 286, '/storage/vaccine_photo/G8tu56WuXyQFC1ITfZEJ.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Sởi, quai bị, rubella đều l&agrave; dẫn căn bệnh rất dễ l&acirc;y qua đường h&ocirc; hấp nếu chưa c&oacute; kh&aacute;ng thể ph&ograve;ng bệnh. Bệnh l&acirc;y lan nhanh v&agrave; để lại nhiều biến chứng nguy hiểm, ảnh hưởng đến t&iacute;nh mạng. Phụ nữ mang thai bị nhiễm bệnh c&oacute; nguy cơ g&acirc;y dị tật bẩm sinh cho thai nhi, sinh non hoặc thai chết lưu.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bệnh sởi g&acirc;y ra ph&aacute;t ban to&agrave;n th&acirc;n, ho khan k&eacute;o d&agrave;i, sốt, chảy nước mũi, sưng m&iacute; mắt&hellip; Bệnh c&oacute; thể dẫn đến vi&ecirc;m phổi, nhiễm tr&ugrave;ng tai, động kinh, tổn thương n&atilde;o, thậm ch&iacute; tử vong.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bệnh quai bị c&oacute; triệu chứng điển h&igrave;nh l&agrave; sưng đau v&ugrave;ng mang tai, đau đầu, đau nhức cơ, sốt, kh&oacute; nhai&hellip; Quai bị c&oacute; thể dẫn đến điếc, vi&ecirc;m m&agrave;ng n&atilde;o, sưng đau tinh ho&agrave;n hoặc buồng trứng dẫn đến v&ocirc; sinh.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bệnh Rubella g&acirc;y ph&aacute;t ban to&agrave;n th&acirc;n, vi&ecirc;m khớp v&agrave; sốt nhẹ. Phụ nữ mang thai nhiễm rubella th&igrave; trẻ sinh ra c&oacute; nguy cơ cao mắc Rubella bẩm sinh l&agrave;m chậm ph&aacute;t triển tr&iacute; tuệ, v&agrave; dễ bị dị tật bẩm sinh.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Hiện nay, đ&atilde; c&oacute; vắc xin kết hợp gi&uacute;p ph&ograve;ng c&ugrave;ng l&uacute;c 3 bệnh sởi, quai bị, rubella chỉ trong 1 mũi ti&ecirc;m, phổ biến nhất l&agrave; vắc xin MMR II.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin MMR II (Mỹ) ph&ograve;ng bệnh Sởi &ndash; Quai bị &ndash; Rubella</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin phối hợp sởi, quai bị v&agrave; rubella sống l&agrave; t&aacute;c nh&acirc;n g&acirc;y miễn dịch chủ động d&ugrave;ng để ngăn ngừa nhiễm virus bệnh sởi, quai bị v&agrave; rubella. Thuốc hoạt động bằng c&aacute;ch gi&uacute;p cơ thể tạo kh&aacute;ng thể chống lại virus. Đ&acirc;y l&agrave; vắc xin sống, giảm độc lực.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Merck Sharp and Dohme (Mỹ)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin MMR-II được chỉ định để tạo miễn dịch ph&ograve;ng 3 bệnh: sởi, quai bị, rubella cho trẻ từ 12 th&aacute;ng tuổi trở l&ecirc;n</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m chủng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m chủng mũi đầu ti&ecirc;n cho trẻ v&agrave;o khoảng từ 12 -15 th&aacute;ng tuổi hoặc muộn hơn để tr&aacute;nh tương t&aacute;c với kh&aacute;ng thể của mẹ truyền sang con.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi ti&ecirc;m nhắc lại n&ecirc;n được chủng ngừa v&agrave;o l&uacute;c 4-6 tuổi (độ tuổi đi học), hoặc sớm hơn nếu c&oacute; dịch xảy ra. Mũi ti&ecirc;m nhắc lại c&ograve;n c&oacute; t&aacute;c dụng tạo ra biến đổi thể dịch cho những trẻ chưa đ&aacute;p ứng với mũi ti&ecirc;m lần trước.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Ph&aacute;c đồ ti&ecirc;m vắc xin MMR II của Mỹ ph&ograve;ng sởi, quai bị, rubella c&oacute; sự kh&aacute;c nhau giữa c&aacute;c độ tuổi:</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Với trẻ từ 12 th&aacute;ng tuổi đến 7 tuổi:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1 l&agrave; lần ti&ecirc;m đầu ti&ecirc;n</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2 khi trẻ 4-6 tuổi hoặc sớm hơn nếu c&oacute; dịch xảy ra. Mũi 2 c&aacute;ch mũi 1 &iacute;t nhất 1 th&aacute;ng</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Với trẻ từ 7 tuổi trở l&ecirc;n v&agrave; người lớn:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1 l&agrave; lần ti&ecirc;m đầu ti&ecirc;n</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2 c&aacute;ch mũi 1 &iacute;t nhất 1 th&aacute;ng</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Ri&ecirc;ng với phụ nữ, n&ecirc;n ho&agrave;n tất ph&aacute;c đồ ti&ecirc;m chủng vắc xin ph&ograve;ng sởi, quai bị, rubella trước khi mang thai &iacute;t nhất 3 th&aacute;ng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m dưới da, kh&ocirc;ng được ti&ecirc;m tĩnh mạch</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Người dị ứng với bất kỳ th&agrave;nh phần n&agrave;o trong vắc xin, kể cả gelatin.</li>
<li style="box-sizing: border-box; text-align: justify;">Người đang mang thai, phải tr&aacute;nh mang thai 3 th&aacute;ng sau khi ti&ecirc;m vắc xin&nbsp; cho phụ nữ.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; tiền sử dị ứng với neomycin.</li>
<li style="box-sizing: border-box; text-align: justify;">Đang c&oacute; bệnh l&yacute; sốt hoặc vi&ecirc;m đường h&ocirc; hấp.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh lao đang tiến triển m&agrave; chưa được điều trị hoặc người đang điều trị bằng thuốc ức chế miễn dịch.</li>
<li style="box-sizing: border-box; text-align: justify;">Người c&oacute; rối loạn về m&aacute;u, bệnh bạch cầu hay u hạch bạch huyết; hoặc ở người c&oacute; những khối u t&acirc;n sinh &aacute;c t&iacute;nh ảnh hưởng tới tủy xương hoặc hệ bạch huyết.</li>
<li style="box-sizing: border-box; text-align: justify;">Người bị bệnh suy giảm miễn dịch ti&ecirc;n ph&aacute;t hoặc thứ ph&aacute;t, bao gồm cả người mắc bệnh AIDS v&agrave; người c&oacute; biểu hiện l&acirc;m s&agrave;ng về nhiễm virus g&acirc;y suy giảm miễn dịch; c&aacute;c bệnh g&acirc;y giảm hoặc v&ocirc; gama- globulin m&aacute;u.</li>
<li style="box-sizing: border-box; text-align: justify;">Người c&oacute; tiền sử trong gia đ&igrave;nh suy giảm miễn dịch bẩm sinh hoặc di truyền cho đến khi chứng minh được họ c&oacute; khả năng đ&aacute;p ứng miễn dịch với vaccine.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Lu&ocirc;n c&oacute; sẵn dụng cụ v&agrave; ph&aacute;c đồ cấp cứu sốc phản vệ khi ti&ecirc;m chủng vắc xin</li>
<li style="box-sizing: border-box; text-align: justify;">Cẩn trọng với người c&oacute; tiền sử co giật v&agrave; c&oacute; tiền sử tổn thương n&atilde;o. Th&ocirc;ng b&aacute;o với b&aacute;c sĩ nếu t&igrave;nh trạng sốt xảy ra sau khi ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Qu&aacute; mẫn với trứng: người c&oacute; phản ứng dị ứng với trứng cần đặc biệt thận trọng khi ti&ecirc;m vắc xin n&agrave;y bởi v&igrave; vắc xin sống ph&ograve;ng sởi v&agrave; quai bị được nu&ocirc;i cấy tr&ecirc;n ph&ocirc;i g&agrave;.</li>
<li style="box-sizing: border-box; text-align: justify;">Giảm tiểu cầu: sau khi ti&ecirc;m vắc xin ở những người c&oacute; bệnh giảm tiểu cầu th&igrave; mức độ giảm tiểu cầu sẽ trầm trọng hơn. Mức độ giảm tiểu cầu sẽ lũy tiến khi ti&ecirc;m nhắc lại lần sau.</li>
<li style="box-sizing: border-box; text-align: justify;">Đối với những trẻ c&oacute; nhiễm virus HIV nhưng kh&ocirc;ng c&oacute; biểu hiện l&acirc;m s&agrave;ng của suy giảm miễn dịch th&igrave; vẫn c&oacute; thể ti&ecirc;m ph&ograve;ng bằng MMR-II nhưng phải theo d&otilde;i hiệu quả g&acirc;y đ&aacute;p ứng miễn dịch đối với c&aacute;c bệnh đ&atilde; ti&ecirc;m ph&ograve;ng. Bởi v&igrave; khả năng đ&aacute;p ứng miễn dịch c&oacute; thể kh&ocirc;ng bằng so với c&aacute;c trẻ b&igrave;nh thường kh&aacute;c.</li>
<li style="box-sizing: border-box; text-align: justify;">Đ&atilde; c&oacute; nghi&ecirc;n cứu cho thấy vắc xin sởi-quai bị- rubella c&oacute; thể l&agrave;m ức chế tạm thời t&iacute;nh nhạy cảm của da với tuberculin, v&igrave; vậy phải thực hiện test tuberculin trước hoặc đồng thời với việc ti&ecirc;m vắc xin</li>
<li style="box-sizing: border-box; text-align: justify;">Chưa c&oacute; nghi&ecirc;n cứu về việc t&aacute;c động của vắc xin l&ecirc;n trẻ em đang bị nhiễm lao m&agrave; chưa được điều trị.</li>
<li style="box-sizing: border-box; text-align: justify;">Cũng như c&aacute;c vắc xin kh&aacute;c, MMR-II kh&ocirc;ng g&acirc;y được đ&aacute;p ứng thể dịch 100% tr&ecirc;n người đ&atilde; được ti&ecirc;m chủng.</li>
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ đang cho con b&uacute;: cần thận trọng khi ti&ecirc;m ph&ograve;ng, chưa c&oacute; th&ocirc;ng tin về việc vắc xin sởi, quai bị c&oacute; b&agrave;i tiết qua sữa mẹ hay kh&ocirc;ng. Đ&atilde; c&oacute; th&ocirc;ng tin về việc vắc xin rubella b&agrave;i tiết qua sữa mẹ v&agrave; c&oacute; biểu hiện l&acirc;m s&agrave;ng nhiễm virus rubella tr&ecirc;n trẻ b&uacute; mẹ.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Cảm gi&aacute;c r&aacute;t bỏng hoặc đau nh&oacute;i tại v&ugrave;ng ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">&Iacute;t gặp: sốt (từ 38<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C trở l&ecirc;n, tr&ecirc;n da c&oacute; v&ugrave;ng ban đỏ nhưng thường nhẹ.</li>
<li style="box-sizing: border-box; text-align: justify;">Hiếm gặp: Phản ứng nhẹ tại chỗ, như ban đỏ, chai hoặc căng cứng, đau họng, kh&oacute; chịu, sởi kh&ocirc;ng điển h&igrave;nh, ngất, dễ bị k&iacute;ch th&iacute;ch. Vi&ecirc;m tuyến mang tai, buồn n&ocirc;n, n&ocirc;n v&agrave; ti&ecirc;u chảy.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng qu&aacute; mẫn tr&ecirc;n da như nổi m&agrave;y đay, co thắt kh&iacute; phế quản c&oacute; thể xảy ra ngay cả ở những người kh&ocirc;ng c&oacute; tiền sử dị ứng.</li>
<li style="box-sizing: border-box; text-align: justify;">Đau cơ, khớp: thường tho&aacute;ng qua v&agrave; kh&ocirc;ng bị mạn t&iacute;nh. Hay xảy ra ở phụ nữ ở lứa tuổi trưởng th&agrave;nh.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phải ti&ecirc;m vắc xin MMR-II trước hoặc sau một th&aacute;ng khi d&ugrave;ng những loại vắc xin virus sống kh&aacute;c.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể ti&ecirc;m c&ugrave;ng thời điểm với vắc xin Varicella v&agrave; vắc xin Hib nhưng phải ở c&aacute;c vị tr&iacute; kh&aacute;c nhau.</li>
<li style="box-sizing: border-box; text-align: justify;">Chưa c&oacute; nhiều dữ liệu nghi&ecirc;n cứu l&acirc;m s&agrave;ng về việc ti&ecirc;m chủng MMR-II c&ugrave;ng với vắc xin bạch hầu- ho g&agrave;- uốn v&aacute;n v&agrave; vắc xin bại liệt OPV v&igrave; thế kh&ocirc;ng n&ecirc;n sử dụng đồng thời.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m chủng &iacute;t nhất 3 th&aacute;ng sau khi ti&ecirc;m immunoglobulin hoặc truyền m&aacute;u hay huyết tương v&igrave; sẽ l&agrave;m giảm đ&aacute;p ứng miễn dịch.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Trước khi ho&agrave;n nguy&ecirc;n, vắc xin cần được bảo quản ở nhiệt độ 2-80C v&agrave; tr&aacute;nh &aacute;nh s&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Sau khi ho&agrave;n nguy&ecirc;n n&ecirc;n sử dụng ngay vắc xin, c&oacute; thể sử dụng được vắc xin đ&atilde; ho&agrave;n nguy&ecirc;n nếu được bảo quản ở nhiệt độ từ 2-8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C, tr&aacute;nh &aacute;nh s&aacute;ng trong v&ograve;ng 4 giờ. Sau 4 giờ phải hủy bỏ vắc xin theo quy định</li>
</ul>', '2021-07-03 20:07:18', '2021-07-04 22:09:26', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (43, 'PHÒNG BỆNH THƯƠNG HÀN', 0, null, null, null, null, null, '2021-07-03 20:08:05', '2021-07-03 20:08:05', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (44, 'Typhim Vi', 43, 'Đang sử dụng', 286, '/storage/vaccine_photo/7TC6WDw8XDUsqDCf3rOi.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Thương h&agrave;n l&agrave; bệnh truyền nhiễm cấp t&iacute;nh do vi khuẩn Salmonella typhi g&acirc;y ra. Bệnh dễ d&agrave;ng l&acirc;y nhiễm qua đường ti&ecirc;u h&oacute;a v&agrave; ph&aacute;t triển nhanh th&agrave;nh dịch. Cả trẻ em v&agrave; người lớn đều c&oacute; thể trở th&agrave;nh đối tượng mắc bệnh nếu kh&ocirc;ng được ph&ograve;ng bệnh đ&uacute;ng c&aacute;ch.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">L&agrave; một căn bệnh kh&ocirc;ng được nhiều người quan t&acirc;m, tuy nhi&ecirc;n thương h&agrave;n lại rất nguy hiểm với c&aacute;c triệu chứng sốt, đau đầu, rối loạn ti&ecirc;u h&oacute;a, chảy m&aacute;u cam&hellip; thậm ch&iacute; nếu bệnh trở nặng c&oacute; thể dẫn đến tử vong với c&aacute;c biến chứng nguy hiểm như: thủng ruột non, xuất huyết ti&ecirc;u h&oacute;a, vi&ecirc;m t&uacute;i mật, vi&ecirc;m n&atilde;o&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin Typhim Vi (Ph&aacute;p)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin ph&ograve;ng bệnh thương h&agrave;n c&oacute; t&ecirc;n Typhim Vi được sản xuất tại Ph&aacute;p, được chỉ định ti&ecirc;m cho trẻ từ 2 tuổi trở l&ecirc;n v&agrave; người lớn. Chỉ cần ti&ecirc;m 1 liều duy nhất c&oacute; t&aacute;c dụng bảo vệ trong 3 năm, ti&ecirc;m nhắc nếu c&oacute; nguy cơ nhiễm bệnh.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Sanofi Pasteur (Ph&aacute;p)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Typhim Vi được chỉ định để dự ph&ograve;ng bệnh thương h&agrave;n cho người lớn v&agrave; trẻ em tr&ecirc;n 2 tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin thương h&agrave;n cũng được chỉ định cho người đi du lịch, d&acirc;n di cư hoặc nh&acirc;n vi&ecirc;n y tế, qu&acirc;n nh&acirc;n đi đến c&aacute;c v&ugrave;ng c&oacute; dịch.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m chủng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Một liều duy nhất v&agrave; c&oacute; t&aacute;c dụng bảo vệ trong 3 năm</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp hoặc ti&ecirc;m dưới da</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mẫn cảm với c&aacute;c th&agrave;nh phần trong vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m bắp cho người bị rối loạn đ&ocirc;ng m&aacute;u hoặc giảm tiểu cầu.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ em dưới 2 tuổi v&igrave; đ&aacute;p ứng miễn dịch thấp.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ho&atilde;n sử dụng vắc xin khi đang c&oacute; biểu hiện sốt hoặc nhiễm tr&ugrave;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Phải chắc chắn rằng kh&ocirc;ng được ti&ecirc;m vắc xin v&agrave;o trong l&ograve;ng mạch m&aacute;u.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Typhim Vi chỉ ph&ograve;ng được bệnh sốt thương h&agrave;n do Samonella typhi c&ograve;n kh&ocirc;ng ph&ograve;ng được bệnh thương h&agrave;n do Samonella paratyphi tu&yacute;p A v&agrave; B g&acirc;y ra.</li>
<li style="box-sizing: border-box; text-align: justify;">Lu&ocirc;n c&oacute; sẵn ph&aacute;c đồ chống sốc phản vệ khi ti&ecirc;m ph&ograve;ng vắc xin mặc d&ugrave; rất hiếm gặp c&aacute;c phản ứng dị ứng n&agrave;y xảy ra khi ti&ecirc;m Typhim Vi.</li>
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ c&oacute; thai v&agrave; cho con b&uacute;: Kh&ocirc;ng c&oacute; chống chỉ định cho phụ nữ c&oacute; thai, tuy nhi&ecirc;n việc d&ugrave;ng thuốc trong thời gian thai kỳ phải c&oacute; chỉ định của B&aacute;c sĩ.</li>
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ cho con b&uacute;: vẫn c&oacute; thể ti&ecirc;m ph&ograve;ng vắc xin.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng tại chỗ: sưng, đau, c&oacute; quầng đỏ tại vết ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Phản ứng to&agrave;n th&acirc;n: sốt, mệt mỏi, đau đầu, đau cơ, buồn n&ocirc;n v&agrave; đau bụng.</li>
<li style="box-sizing: border-box; text-align: justify;">Rất hiếm c&oacute; trường hợp ph&aacute;t ban tr&ecirc;n da hoặc qu&aacute; mẫn.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Typhim Vi c&oacute; thể ti&ecirc;m c&ugrave;ng c&aacute;c vắc xin kh&aacute;c như: vi&ecirc;m gan A, bạch hầu, ho g&agrave;, bại liệt, uốn v&aacute;n, dại, vi&ecirc;m gan B, vi&ecirc;m m&agrave;ng n&atilde;o B+C trong c&ugrave;ng một đợt ti&ecirc;m chủng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin cần được bảo quản ở nhiệt độ từ 2<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C &ndash; 8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C v&agrave; kh&ocirc;ng được đ&ocirc;ng băng.</li>
</ul>', '2021-07-03 20:08:15', '2021-07-04 22:21:44', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (45, 'EUVAX B', 37, 'Đang sử dụng', null, '/storage/vaccine_photo/TEliwNymdhZeGKqo0VUC.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vi&ecirc;m gan B l&agrave; bệnh truyền nhiễm g&acirc;y ra bởi virus vi&ecirc;m gan B (Hepatitis virus B), được mệnh danh l&agrave; &ldquo;kẻ giết người thầm lặng&rdquo;. Người nhiễm virus vi&ecirc;m gan B chịu ảnh hưởng lớn đến hoạt động của gan, c&oacute; thể g&acirc;y nhiễm tr&ugrave;ng gan thậm ch&iacute; đe dọa đến t&iacute;nh mạng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Hiện nay, virus vi&ecirc;m gan B vẫn l&agrave; mối đe dọa lớn đến sức khỏe to&agrave;n cầu, l&agrave; nguy&ecirc;n nh&acirc;n dẫn đến suy gan, xơ gan, ung thư gan. Virus vi&ecirc;m gan B c&oacute; thể tồn tại trong m&aacute;u v&agrave; c&aacute;c chất dịch kh&aacute;c của người bệnh g&acirc;y ra t&igrave;nh trạng vi&ecirc;m gan B cấp v&agrave; mạn t&iacute;nh. Khi đ&oacute;, người bệnh buộc phải chấp nhận sống chung với virus vi&ecirc;m gan B suốt đời.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Theo thống k&ecirc; của Tổ chức Y tế Thế giới (WHO), c&oacute; khoảng 400 triệu người tr&ecirc;n thế giới mắc vi&ecirc;m gan B mạn t&iacute;nh. Ở Việt Nam hiện nay số người nhiễm virus vi&ecirc;m gan b chiếm khoảng 20% d&acirc;n số.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin Vắc xin EUVAX B</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin EUVAX B của H&agrave;n Quốc ph&ograve;ng ngừa vi&ecirc;m gan B được d&ugrave;ng để chủng ngừa cho người từ 16 tuổi trở l&ecirc;n.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Sanofi Pasteur (Ph&aacute;p), sản xuất tại H&agrave;n Quốc.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Tạo miễn dịch chống lại sự nhiễm c&aacute;c ph&acirc;n nh&oacute;m (subtype) virus vi&ecirc;m gan si&ecirc;u vi B được biết.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Euvax B chỉ d&ugrave;ng đường ti&ecirc;m bắp, kh&ocirc;ng n&ecirc;n ti&ecirc;m ở v&ugrave;ng m&ocirc;ng, v&agrave; kh&ocirc;ng được ti&ecirc;m tĩnh mạch.</li>
<li style="box-sizing: border-box; text-align: justify;">Phải lắc kỹ trước khi d&ugrave;ng, bởi v&igrave; trong qu&aacute; tr&igrave;nh bảo quản vắc xin c&oacute; thể trở th&agrave;nh dạng chất lắng trắng mịn với dịch nổi b&ecirc;n tr&ecirc;n trong suốt kh&ocirc;ng m&agrave;u.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Đối tượng tr&ecirc;n 16 tuổi c&oacute; thể lựa chọn một trong c&aacute;c ph&aacute;c đồ ti&ecirc;m sau:</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; text-decoration-line: underline;"><em style="box-sizing: border-box;">C&aacute;ch 1:</em></span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: lần đầu đến ti&ecirc;m</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: một th&aacute;ng sau mũi 1</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: s&aacute;u th&aacute;ng sau mũi 1</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">C&aacute;ch 2:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: lần đầu đến ti&ecirc;m</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: 1 th&aacute;ng sau mũi 1</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: 2 th&aacute;ng sau mũi 1</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 4: 12 th&aacute;ng sau mũi 1</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Cảnh b&aacute;o:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phải ho&atilde;n d&ugrave;ng Euvax B ở những bệnh nh&acirc;n c&oacute; bệnh c&oacute; sốt cấp t&iacute;nh, nặng.</li>
<li style="box-sizing: border-box; text-align: justify;">Ở bệnh nh&acirc;n mắc bệnh xơ cứng rải r&aacute;c, bất cứ một k&iacute;ch th&iacute;ch hệ thống miễn dịch n&agrave;o cũng c&oacute; thể l&agrave;m trầm trọng th&ecirc;m c&aacute;c triệu chứng. Tuy nhi&ecirc;n, ở c&aacute;c bệnh nh&acirc;n n&agrave;y cần phải c&acirc;n nhắc giữa lợi &iacute;ch của việc chủng ngừa vi&ecirc;m gan si&ecirc;u vi B với nguy cơ của việc l&agrave;m nặng th&ecirc;m bệnh xơ cứng rải r&aacute;c (xem T&aacute;c dụng ngoại &yacute;).</li>
<li style="box-sizing: border-box; text-align: justify;">Cần lưu &yacute; rằng việc chủng ngừa kh&ocirc;ng đạt được hiệu quả bảo vệ ở bệnh nh&acirc;n vi&ecirc;m gan si&ecirc;u vi B giai đoạn tiềm ẩn hay tiến triển.</li>
<li style="box-sizing: border-box; text-align: justify;">Cũng như khi d&ugrave;ng c&aacute;c vắc xin dạng ti&ecirc;m kh&aacute;c, cần phải chuẩn bị sẵn s&agrave;ng c&aacute;c thuốc v&agrave; phương tiện điều trị trong trường hợp sốc phản vệ sau khi ti&ecirc;m vắc xin (hiếm gặp).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mẫn cảm với bất kỳ th&agrave;nh phần n&agrave;o của Euvax B.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Phụ nữ mang thai v&agrave; cho con b&uacute;:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Người ta chưa đ&aacute;nh gi&aacute; được ảnh hưởng của HBsAg l&ecirc;n sự ph&aacute;t triển ph&ocirc;i thai. Tuy nhi&ecirc;n, cũng như c&aacute;c vắc xin virus bất hoạt kh&aacute;c, nguy cơ đối với ph&ocirc;i thai hầu như kh&ocirc;ng đ&aacute;ng kể. Vắc xin Euvax B chỉ n&ecirc;n d&ugrave;ng trong thai kỳ khi thật sự cần thiết.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c nghi&ecirc;n cứu l&acirc;m s&agrave;ng kh&ocirc;ng lượng gi&aacute; việc ti&ecirc;m Euvax B cho mẹ c&oacute; ảnh hưởng đến trẻ b&uacute; sữa mẹ kh&ocirc;ng. Kh&ocirc;ng x&aacute;c định c&oacute; chống chỉ định.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng ngoại &yacute;:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng tại chỗ như quầng đỏ, đau, sưng hay hơi n&oacute;ng hiếm khi xuất hiện; c&aacute;c triệu chứng n&agrave;y khỏi trong v&ograve;ng 2 ng&agrave;y.</li>
<li style="box-sizing: border-box; text-align: justify;">Hiếm: phản ứng to&agrave;n th&acirc;n như kh&oacute; chịu, suy nhược, đau đầu, n&ocirc;n, buồn n&ocirc;n, ch&oacute;ng mặt, đau cơ, đau khớp; ban đỏ ở da v&agrave; tăng men gan (transaminase) tho&aacute;ng qua.</li>
<li style="box-sizing: border-box; text-align: justify;">Rất hiếm: C&aacute;c b&aacute;o c&aacute;o kh&ocirc;ng thể x&aacute;c định được nguy&ecirc;n nh&acirc;n g&acirc;y n&ecirc;n di chứng v&agrave; ảnh hưởng về vi&ecirc;m đa d&acirc;y thần kinh, vi&ecirc;m thần kinh mắt, liệt mặt, l&agrave;m bệnh xơ cứng rải r&aacute;c nặng l&ecirc;n, v&agrave; hội chứng Guilain-Barr&eacute;.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin phải được bảo quản ở nhiệt độ +2&deg;C đến +8&deg;C (trong tủ lạnh).</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được để đ&ocirc;ng băng.</li>
</ul>', '2021-07-03 20:10:11', '2021-07-04 22:22:17', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (46, 'PHÒNG CÁC BỆNH DO PHẾ CẦU KHUẨN', 0, null, null, null, null, null, '2021-07-03 20:10:53', '2021-07-03 20:10:53', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (47, 'Synflorix', 46, 'Đang sử dụng', null, '/storage/vaccine_photo/vAL8Brb92eUiuRv457Ap.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Mỗi năm, c&aacute;c bệnh g&acirc;y ra do khuẩn phế cầu đang đe dọa t&iacute;nh mạng h&agrave;ng tỷ người tr&ecirc;n thế giới:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Gần 1 triệu ca tử vong h&agrave;ng năm ở trẻ dưới 5 tuổi, vi&ecirc;m phổi đang l&agrave; mối đe dọa lớn tr&ecirc;n to&agrave;n thế giới.</li>
<li style="box-sizing: border-box; text-align: justify;">83% trẻ dưới 3 tuổi bị vi&ecirc;m m&agrave;ng n&atilde;o. 50% trong số đ&oacute; tử vong. 30 &ndash; 50% phải chịu đựng những di chứng l&acirc;u d&agrave;i như: điếc, m&ugrave;, liệt, động kinh, chậm ph&aacute;t triển tr&iacute; tuệ&hellip;</li>
<li style="box-sizing: border-box; text-align: justify;">Hơn 350 triệu ca bệnh vi&ecirc;m tai giữa cấp ở trẻ em dưới 5 tuổi, hơn 1/3 trong số đ&oacute; bị nhiễm tr&ugrave;ng lặp đi lặp lại, phải phẫu thuật.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Theo Tổ chức Y tế Thế giới (WHO), vắc xin Synflorix ra đời đ&atilde; gi&uacute;p ngăn chặn 6-7,5 triệu trường hợp mắc bệnh vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o, vi&ecirc;m tai giữa do S.Pneumoniae kh&ocirc;ng định tu&yacute;p, cứu sống khoảng 290.000 trẻ dưới 5 tuổi tr&ecirc;n to&agrave;n cầu.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Ti&ecirc;m vắc xin Synflorix cho trẻ ngay từ 6 th&aacute;ng tuổi l&agrave; c&aacute;ch đơn giản v&agrave; hiệu quả nhất để bảo vệ trẻ khỏi c&aacute;c căn bệnh nguy hiểm g&acirc;y ra do khuẩn phế cầu.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về vắc xin Synflorix (Bỉ)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin ngừa phế cầu Synflorix c&oacute; t&aacute;c dụng tạo miễn dịch chủ động cho trẻ em v&agrave; trẻ nhỏ từ 6 tuần tuổi đến 5 tuổi, với mục đ&iacute;ch ph&ograve;ng ngừa c&aacute;c bệnh g&acirc;y ra bởi phế cầu (Streptococcus pneumoniae) như: Hội chứng nhiễm tr&ugrave;ng, vi&ecirc;m m&agrave;ng n&atilde;o, vi&ecirc;m phổi, nhiễm khuẩn huyết v&agrave; vi&ecirc;m tai giữa cấp t&iacute;nh.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Glaxo SmithKline (Bỉ)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Synflorix được chỉ định cho trẻ nhỏ từ 6 tuần đến 5 tuổi để ph&ograve;ng ngừa c&aacute;c bệnh do phế cầu Streptococcus pneumoniae tu&yacute;p huyết thanh 1, 4, 5, 6B, 7F, 9V, 14, 18C, 19F v&agrave; 23F (như hội chứng nhiễm tr&ugrave;ng, vi&ecirc;m m&agrave;ng n&atilde;o, vi&ecirc;m phổi, nhiễm khuẩn huyết v&agrave; vi&ecirc;m tai giữa cấp) v&agrave; ngừa vi&ecirc;m tai giữa cấp g&acirc;y bởi Haemophilus influenzae kh&ocirc;ng định tu&yacute;p.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m chủng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ từ 6 tuần đến 6 th&aacute;ng tuổi: * Liệu tr&igrave;nh ti&ecirc;m 3 liều cơ bản</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: v&agrave;o 2 th&aacute;ng tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: v&agrave;o 3 th&aacute;ng tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: v&agrave;o 4 th&aacute;ng tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi nhắc lại: sau 6 th&aacute;ng kể từ mũi thứ 3.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Hoặc:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: v&agrave;o 2 th&aacute;ng tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: v&agrave;o 4 th&aacute;ng tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: v&agrave;o 6 th&aacute;ng tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi nhắc lại: sau 6 th&aacute;ng kể từ mũi 3.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">(Liều đầu ti&ecirc;n của liệu tr&igrave;nh n&agrave;y c&oacute; thể bắt đầu từ 6 tuần tuổi. Khoảng c&aacute;ch giữa 3 liều đầu ti&ecirc;n tối thiểu l&agrave; 1 th&aacute;ng; liều nhắc lại sau 6 th&aacute;ng kể từ mũi thứ 3).</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Trẻ sinh non (&ge; 27 tuần): c&oacute; thể &aacute;p dụng liệu tr&igrave;nh n&agrave;y.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">* Liệu tr&igrave;nh ti&ecirc;m 2 liều (&aacute;p dụng khi sử dụng Synflorix trong Chương tr&igrave;nh TCMR):</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: v&agrave;o 2 th&aacute;ng tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: v&agrave;o 4 th&aacute;ng tuổi.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">(Liều đầu ti&ecirc;n của liệu tr&igrave;nh n&agrave;y c&oacute; thể bắt đầu từ 6 tuần tuổi. Mũi ti&ecirc;m nhắc lại sau 6 th&aacute;ng kể từ mũi đầu ti&ecirc;n).</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ từ 7- 11 th&aacute;ng (chưa từng được ti&ecirc;m ph&ograve;ng vắc xin trước đ&oacute;)</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: c&aacute;ch mũi 1 l&agrave; 1 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi nhắc lại: v&agrave;o năm tuổi thứ 2 v&agrave; c&aacute;ch mũi 2 &iacute;t nhất l&agrave; 2 th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ từ 12-23 th&aacute;ng (chưa từng được ti&ecirc;m ph&ograve;ng vắc xin trước đ&oacute;)</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: c&aacute;ch mũi 1 l&agrave; 2 th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ từ 24 th&aacute;ng đến 5 tuổi (chưa từng được ti&ecirc;m ph&ograve;ng vắc xin trước đ&oacute;)</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: c&aacute;ch mũi 1 l&agrave; 2 th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp ở mặt trước &ndash; b&ecirc;n đ&ugrave;i của trẻ nhỏ v&agrave; ti&ecirc;m ở cơ delta c&aacute;nh tay của trẻ lớn. Kh&ocirc;ng được ti&ecirc;m tĩnh mạch hoặc ti&ecirc;m trong da đối với vắc xin Synflorix.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Synflorix kh&ocirc;ng được ti&ecirc;m cho c&aacute;c đối tượng qu&aacute; mẫn với bất kỳ th&agrave;nh phần n&agrave;o trong vắc xin.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">N&ecirc;n ho&atilde;n việc ti&ecirc;m vắc xin nếu đang sốt hoặc nhiễm tr&ugrave;ng cấp t&iacute;nh.</li>
<li style="box-sizing: border-box; text-align: justify;">Synflorix chỉ c&oacute; t&aacute;c dụng ph&ograve;ng ngừa đối với c&aacute;c vi khuẩn c&oacute; tu&yacute;p huyết thanh đ&atilde; được chỉ ra trong th&agrave;nh phần của vắc xin. V&igrave; vậy vắc xin polysaccharid 23 tu&yacute;p phế cầu (Pneumo 23) cần được chỉ định khi trẻ &ge; 2 tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Liệu tr&igrave;nh sử dụng Synflorix được th&iacute;ch hợp để chỉ định cho trẻ dưới 2 tuổi.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng tại chỗ thường gặp với tỷ lệ cao hơn ở trẻ &gt;12 th&aacute;ng tuổi so với trẻ nhỏ hơn.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c t&aacute;c dụng kh&ocirc;ng mong muốn thường trầm trọng hơn khi ti&ecirc;m c&ugrave;ng với vắc xin ho g&agrave; to&agrave;n tế b&agrave;o.</li>
<li style="box-sizing: border-box; text-align: justify;">Thường gặp: sốt &gt;38 độ C, đau sưng đỏ hoặc chai cứng ở chỗ ti&ecirc;m. Tinh thần trẻ c&oacute; thể bị k&iacute;ch th&iacute;ch, c&oacute; cảm gi&aacute;c ch&aacute;n ăn.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Synflorix c&oacute; thể ti&ecirc;m đồng thời với c&aacute;c vắc xin sau đ&acirc;y nhưng phải ti&ecirc;m v&agrave;o c&aacute;c vị tr&iacute; kh&aacute;c nhau: vắc xin bạch hầu, ho g&agrave; v&ocirc; b&agrave;o, vi&ecirc;m gan B, vắc xin bại liệt bất hoạt, vắc xin ph&ograve;ng c&aacute;c bệnh do Hib, vắc xin vi&ecirc;m gan B, vắc xin sởi &ndash; quai bị &ndash; rubella, vắc xin thủy đậu, vắc xin n&atilde;o m&ocirc; cầu, vắc xin rotavirus.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin được bảo quản ở nhiệt độ từ 2 độ C &ndash; 8 độ C, kh&ocirc;ng được để đ&ocirc;ng băng</li>
</ul>', '2021-07-03 20:11:06', '2021-07-04 22:12:53', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (48, 'Prevenar 13', 46, 'Đang sử dụng', 286, '/storage/vaccine_photo/dzlcXnt9CbEcHpluI7Zj.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Mỗi năm, c&aacute;c bệnh g&acirc;y ra do khuẩn phế cầu đang đe dọa t&iacute;nh mạng h&agrave;ng tỷ người tr&ecirc;n thế giới:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Gần 1 triệu ca tử vong h&agrave;ng năm ở trẻ dưới 5 tuổi, vi&ecirc;m phổi đang l&agrave; mối đe dọa lớn tr&ecirc;n to&agrave;n thế giới.</li>
<li style="box-sizing: border-box; text-align: justify;">83% trẻ dưới 3 tuổi bị vi&ecirc;m m&agrave;ng n&atilde;o. 50% trong số đ&oacute; tử vong. 30 &ndash; 50% phải chịu đựng những di chứng l&acirc;u d&agrave;i như: điếc, m&ugrave;, liệt, động kinh, chậm ph&aacute;t triển tr&iacute; tuệ&hellip;</li>
<li style="box-sizing: border-box; text-align: justify;">Hơn 350 triệu ca bệnh vi&ecirc;m tai giữa cấp ở trẻ em dưới 5 tuổi, hơn 1/3 trong số đ&oacute; bị nhiễm tr&ugrave;ng lặp đi lặp lại, phải phẫu thuật.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">B&ecirc;n cạnh vắc xin phế cầu Synflorix d&agrave;nh cho trẻ dưới 5 tuổi, hiện nay, người trưởng th&agrave;nh, người cao tuổi, người mắc c&aacute;c bệnh l&yacute; mạn t&iacute;nh&hellip; đ&atilde; c&oacute; vắc xin Prevenar 13 ph&ograve;ng c&aacute;c bệnh phế cầu khuẩn x&acirc;m lấn g&acirc;y nguy hiểm cho trẻ em v&agrave; người lớn như vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o, vi&ecirc;m tai giữa cấp t&iacute;nh, nhiễm khuẩn huyết (nhiễm tr&ugrave;ng m&aacute;u).</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về vắc xin Prevenar 13 (Anh)</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin phế cầu Prevenar 13 l&agrave; vắc xin ph&ograve;ng bệnh do phế cầu cộng hợp 13 tu&yacute;p, đ&oacute;ng vai tr&ograve; quan trọng trong việc tạo miễn dịch chủ động, ph&ograve;ng ngừa bệnh x&acirc;m lấn, vi&ecirc;m tai giữa v&agrave; vi&ecirc;m phổi cấp t&iacute;nh g&acirc;y ra bởi phế cầu khuẩn cho nhiều nh&oacute;m tuổi kh&aacute;c nhau.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Prevenar 13 được nghi&ecirc;n cứu v&agrave; ph&aacute;t triển bởi tập đo&agrave;n h&agrave;ng đầu thế giới về dược phẩm v&agrave; chế phẩm sinh học &ndash; Pfizer (Mỹ). Sản xuất tại Anh.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Prevenar 13 ph&ograve;ng c&aacute;c bệnh phế cầu khuẩn x&acirc;m lấn g&acirc;y nguy hiểm cho trẻ em v&agrave; người lớn như vi&ecirc;m phổi, vi&ecirc;m m&agrave;ng n&atilde;o, vi&ecirc;m tai giữa cấp t&iacute;nh, nhiễm khuẩn huyết (nhiễm tr&ugrave;ng m&aacute;u)&hellip; do phế cầu khuẩn Streptococcus Pneumoniae g&acirc;y ra.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đối tượng c&oacute; thể sử dụng vắc xin:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Trẻ em, c&oacute; thể sử dụng từ khi 6 tuần tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Người trưởng th&agrave;nh.</li>
<li style="box-sizing: border-box; text-align: justify;">Người cao tuổi v&agrave; người mắc c&aacute;c bệnh m&atilde;n t&iacute;nh như phổi tắc nghẽn mạn t&iacute;nh (COPD); lao phổi, tim mạch, tiểu đường&hellip;</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ em từ 6 tuần tuổi &ndash; 6 th&aacute;ng tuổi: Lịch ti&ecirc;m 3 liều cơ bản</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: Lần ti&ecirc;m đầu ti&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: C&aacute;ch mũi 1 tối thiểu 1 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: C&aacute;ch mũi 2 tối thiểu 1 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi nhắc lại: ti&ecirc;m khi trẻ 11 &ndash; 15 th&aacute;ng tuổi v&agrave; c&aacute;ch mũi 3 tối thiểu 2 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Lưu &yacute;: Mũi ti&ecirc;m đầu ti&ecirc;n của ph&aacute;c đồ ti&ecirc;m chủng c&oacute; thể bắt đầu từ 6 tuần tuổi.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ em từ 7 -11 th&aacute;ng tuổi (chưa từng được ti&ecirc;m ph&ograve;ng vắc xin trước đ&oacute;): Lịch ti&ecirc;m 2 liều cơ bản</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: Lần ti&ecirc;m đầu ti&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: C&aacute;ch mũi 1 tối thiểu 1 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi nhắc lại: Ti&ecirc;m khi trẻ tr&ecirc;n 1 tuổi.</li>
<li style="box-sizing: border-box; text-align: justify;">Lưu &yacute;: Mũi ti&ecirc;m nhắc lại c&aacute;ch mũi 2 tối thiểu 2 th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ em từ 12 &ndash; 23 th&aacute;ng tuổi (chưa từng được ti&ecirc;m ph&ograve;ng vắc xin trước đ&oacute;):</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: Lần ti&ecirc;m đầu ti&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: C&aacute;ch mũi 1 tối thiểu 2 th&aacute;ng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Trẻ em từ 24 th&aacute;ng tuổi &ndash; người lớn:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m 1 mũi duy nhất.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Prevenar 13 được chỉ định ti&ecirc;m bắp (v&ugrave;ng cơ delta) với liều 0.5ml</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Tr&aacute;nh d&ugrave;ng vắc xin Prevenar 13 trong thai kỳ.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m vắc xin&nbsp; Prevenar 13 với người qu&aacute; mẫn cảm với th&agrave;nh phần trong vắc xin hoặc với độc tố bạch hầu.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m vắc xin Prevenar 13 ở bệnh nh&acirc;n giảm tiểu cầu hoặc bất kỳ rối loạn đ&ocirc;ng m&aacute;u n&agrave;o.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Prevenar 13 kh&ocirc;ng được ti&ecirc;m nội mạch.</li>
<li style="box-sizing: border-box; text-align: justify;">N&ecirc;n ho&agrave;n tất ph&aacute;c đồ với c&ugrave;ng một loại vắc xin Synflorix hoặc Prevenar 13.</li>
<li style="box-sizing: border-box; text-align: justify;">Trong trường hợp bất khả kh&aacute;ng, c&oacute; thể chuyển đổi vắc xin Synflorix v&agrave; Prevenar 13 v&agrave;o bất cứ thời điểm n&agrave;o trong lịch tr&igrave;nh ti&ecirc;m chủng.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ em đến 5 tuổi (trước sinh nhật 6 tuổi) đ&atilde; ho&agrave;n tất đầy đủ ph&aacute;c đồ ti&ecirc;m Synflorix trước đ&oacute;: c&oacute; thể được khuyến c&aacute;o ti&ecirc;m 1 liều Prevenar 13 để k&iacute;ch th&iacute;ch sinh miễn dịch với 6 type huyết thanh bổ sung. Mũi ti&ecirc;m Prevenar 13 bổ sung được ti&ecirc;m c&aacute;ch mũi Synflorix cuối c&ugrave;ng 8 tuần.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng c&oacute; chống chỉ định về phối hợp vắc xin giữa Prevenar 13 với c&aacute;c vắc xin kh&aacute;c.</li>
<li style="box-sizing: border-box; text-align: justify;">Nếu phải ti&ecirc;m th&ecirc;m 1 loại vắc xin kh&aacute;c c&ugrave;ng l&uacute;c với Prevenar 13, vắc xin cần được ti&ecirc;m ở một vị tr&iacute; kh&aacute;c.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng tại chỗ ti&ecirc;m: Nổi ban đỏ, chai cứng, sưng đau, tăng nhạy cảm tại chỗ ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng to&agrave;n th&acirc;n kh&aacute;c: Sốt, đau đầu, buồn ngủ, giảm cảm gi&aacute;c th&egrave;m ăn, n&ocirc;n mửa, ti&ecirc;u chảy.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Bảo quản ở nhiệt độ lạnh (từ 2 &ndash; 8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C). Kh&ocirc;ng được đ&oacute;ng băng.</li>
</ul>', '2021-07-03 20:11:41', '2021-07-04 22:12:38', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (49, 'Havax 0,5ml', 35, 'Đang sử dụng', 286, '/storage/vaccine_photo/ClOJNxlbbFy5zZ8JkB2J.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vi&ecirc;m gan si&ecirc;u vi A (Hepatitis A) l&agrave; một bệnh truyền nhiễm c&oacute; mặt hầu hết khắp nơi tr&ecirc;n thế giới. Bệnh thường l&acirc;y qua đường ti&ecirc;u h&oacute;a, nguồn l&acirc;y chủ yếu từ người bệnh sang người l&agrave;nh qua đường ph&acirc;n, miệng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Ở Việt Nam, vi&ecirc;m gan A vẫn l&agrave; bệnh c&oacute; tỷ lệ mắc cao, sau khi x&acirc;m nhập v&agrave;o cơ thể người, vi r&uacute;t vi&ecirc;m gan A được nh&acirc;n l&ecirc;n, khoảng 30 ng&agrave;y sau sẽ ph&aacute;t bệnh, c&aacute;c triệu chứng của bệnh k&eacute;o d&agrave;i từ 2-6 th&aacute;ng, c&oacute; những trường hợp kh&ocirc;ng biểu hiện bất cứ triệu chứng n&agrave;o.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vi&ecirc;m gan si&ecirc;u vi A l&acirc;y nhiễm qua đường ăn uống, qua tiếp x&uacute;c với người bệnh, nguồn nước, m&ocirc;i trường sống n&ecirc;n nguy cơ l&acirc;y bệnh rất dễ d&agrave;ng. Bệnh c&oacute; thể l&acirc;y nhiễm từ người sang người qua đường ti&ecirc;u h&oacute;a, vệ sinh c&aacute; nh&acirc;n kh&ocirc;ng tốt, ăn chung b&aacute;t đũa, sống ở m&ocirc;i trường đ&ocirc;ng đ&uacute;c chật hẹp, &ocirc; nhiễm, nguồn nước sinh hoạt kh&ocirc;ng đảm bảo, tiếp x&uacute;c với người bị bệnh vi&ecirc;m gan si&ecirc;u vi A&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Một số bệnh nh&acirc;n bệnh c&oacute; thể tiến triển th&agrave;nh vi&ecirc;m gan cấp t&iacute;nh nặng, gọi l&agrave; thể tối cấp c&oacute; thể dẫn tới tử vong. Đặc biệt trong số những người đang nhiễm một loại vi r&uacute;t vi&ecirc;m gan m&atilde;n như vi&ecirc;m gan B hoặc vi&ecirc;m gan C, nếu nhiễm th&ecirc;m vi r&uacute;t vi&ecirc;m gan A, c&oacute; nguy cơ cao hơn tiến triển th&agrave;nh vi&ecirc;m gan thể tối cấp.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Cho đến nay, vẫn chưa c&oacute; phương ph&aacute;p điều trị đặc hiệu bệnh vi&ecirc;m gan A, v&igrave; thế để tr&aacute;nh l&acirc;y nhiễm bệnh gan v&agrave; tổn thương gan, việc ti&ecirc;m ngừa vắc xin vi&ecirc;m gan A l&agrave; bước đơn giản v&agrave; hiệu quả nhất.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin Havax 0,5ml (Việt Nam)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin Havax 0.5ML ph&ograve;ng ngừa vi&ecirc;m gan si&ecirc;u vi A được khuyến c&aacute;o chủng ngừa cho trẻ từ 24 th&aacute;ng đến dưới 18 tuổi, sử dụng cho những người c&oacute; nguy cơ phơi nhiễm với virus vi&ecirc;m gan A.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Nghi&ecirc;n cứu v&agrave; sản xuất bởi c&ocirc;ng ty TNHH MTV Vắc xin v&agrave; sinh phẩm số 1 (Vabiotech &ndash; Việt Nam).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">Havax 0,5 ml được d&ugrave;ng để ph&ograve;ng vi&ecirc;m gan A cho người lớn v&agrave; trẻ em từ 2 tuổi đến dưới 18 tuổi.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">Havax được d&ugrave;ng để ph&ograve;ng vi&ecirc;m gan A cho người lớn v&agrave; trẻ em từ 2 tuổi trở l&ecirc;n. Havax được sử dụng cho những người c&oacute; nguy cơ phơi nhiễm với virus vi&ecirc;m gan A:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Những người chưa ti&ecirc;m Vắc-xin Vi&ecirc;m gan A v&agrave; c&oacute; dự định đi du lịch đến những v&ugrave;ng dịch (những v&ugrave;ng thường c&oacute; virus vi&ecirc;m gan A).</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c đối tượng nghề nghiệp c&oacute; phơi nhiễm: hộ l&yacute;, y t&aacute;, c&aacute;n bộ vi&ecirc;n chức l&agrave;m c&ocirc;ng t&aacute;c chăm s&oacute;c phục vụ trẻ t&agrave;n tật, nh&acirc;n vi&ecirc;n xử l&yacute; nước, nước thải v&agrave; thực phẩm c&ocirc;ng nghiệp.</li>
<li style="box-sizing: border-box; text-align: justify;">Những người đặc biệt c&oacute; nguy cơ l&acirc;y nhiễm (bệnh nh&acirc;n ưa chảy m&aacute;u, truyền dịch nhiều lần, ti&ecirc;m tĩnh mạch,đồng t&iacute;nh).</li>
<li style="box-sizing: border-box; text-align: justify;">Havax kh&ocirc;ng bảo vệ chống lại được c&aacute;c bệnh gan do c&aacute;c t&aacute;c nh&acirc;n g&acirc;y bệnh v&agrave; c&aacute;c vir&uacute;t vi&ecirc;m gan kh&aacute;c g&acirc;y ra.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: lần ti&ecirc;m đầu ti&ecirc;n</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: 6 th&aacute;ng sau mũi 1</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">C&aacute;ch d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m v&agrave;o đường tĩnh mạch hoặc trong da, Havax phải được ti&ecirc;m bắp để c&oacute; được đ&aacute;p ứng miễn dịch cao nhất.</li>
<li style="box-sizing: border-box; text-align: justify;">Ở người lớn th&igrave; ti&ecirc;m vắc-xin v&agrave;o v&ugrave;ng cơ Delta song ở trẻ em n&ecirc;n ti&ecirc;m v&agrave;o v&ugrave;ng đ&ugrave;i ngo&agrave;i th&igrave; tốt hơn v&igrave; cơ Delta c&ograve;n nhỏ.</li>
<li style="box-sizing: border-box; text-align: justify;">Ngoại lệ c&oacute; thể ti&ecirc;m Vắc-xin v&agrave;o v&ugrave;ng dưới da cho những bệnh nh&acirc;n bị bệnh m&aacute;u kh&oacute; đ&ocirc;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Lắc kỹ lọ Vắc-xin trước khi ti&ecirc;m.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m Havax cho những người qu&aacute; nhạy cảm với bất cứ th&agrave;nh phần n&agrave;o của vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh tim, bệnh thận hoặc bệnh gan.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh tiểu đường hoặc suy dinh dưỡng.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh Ung thư m&aacute;u n&oacute;i ri&ecirc;ng v&agrave; c&aacute;c bệnh &aacute;c t&iacute;nh n&oacute;i chung.</li>
<li style="box-sizing: border-box; text-align: justify;">Bệnh qu&aacute; mẫn.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m tĩnh mạch trong bất cứ trường hợp n&agrave;o.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m cho trẻ em dưới 2 tuổi (24 th&aacute;ng tuổi).</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m cho c&aacute;c đối tượng mắc bệnh bẩm sinh.</li>
<li style="box-sizing: border-box; text-align: justify;">Ho&atilde;n ti&ecirc;m ở người đang Sốt cao hoặc phản ứng to&agrave;n th&acirc;n hoặc bệnh nhiễm tr&ugrave;ng đang tiến triển.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Cảnh b&aacute;o v&agrave; thận trọng đặc biệt:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Cũng giống như tất cả c&aacute;c loại vắc xin kh&aacute;c: lu&ocirc;n phải c&oacute; sẵn Epinephrine để đề ph&ograve;ng c&aacute;c trường hợp Sốc phản vệ sau khi ti&ecirc;m vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m Havax v&agrave;o m&ocirc;ng hoặc trong da v&igrave; đ&aacute;p ứng miễn dịch thấp</li>
<li style="box-sizing: border-box; text-align: justify;">Với đối tượng bệnh nh&acirc;n đang chạy thận nh&acirc;n tạo hoặc suy giảm miễn dịch, th&igrave; sau khi ti&ecirc;m c&oacute; thể h&agrave;m lượng kh&aacute;ng thể kh&ocirc;ng được cao như mong đợi. Với c&aacute;c đối tượng n&agrave;y c&oacute; thể phải ti&ecirc;m c&aacute;c liều bổ sung, nhắc lại.</li>
<li style="box-sizing: border-box; text-align: justify;">Thời kỳ ủ bệnh vi&ecirc;m gan A c&oacute; thể k&eacute;o d&agrave;i. V&igrave; vậy nếu ti&ecirc;m vắc xin đang trong giai đoạn ủ bệnh th&igrave; vắc xin c&oacute; thể sẽ kh&ocirc;ng bảo vệ được cho c&aacute;c đối tượng n&agrave;y.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng thường gặp: Sau, sưng, đỏ tại chỗ ti&ecirc;m. C&aacute;c triệu chứng n&agrave;y thường hết trong v&ograve;ng 2 ng&agrave;y sau khi ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">&Iacute;t gặp: sốt, đau đầu, buồn n&ocirc;n, mệt mỏi, ch&oacute;ng mặt. Tuy nhi&ecirc;n chưa c&oacute; đủ bằng chứng để chứng minh c&oacute; sự li&ecirc;n quan giữa ti&ecirc;m vắc xin v&agrave; c&aacute;c t&aacute;c dụng kh&ocirc;ng mong muốn n&agrave;y.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Bảo quản trong tủ lạnh (2&deg;C &ndash; 8&deg;C).</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng để đ&ocirc;ng đ&aacute;.</li>
<li style="box-sizing: border-box; text-align: justify;">Tr&aacute;nh &aacute;nh s&aacute;ng</li>
</ul>', '2021-07-03 20:12:29', '2021-07-04 22:08:35', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (50, 'PHÒNG BỆNH BẠCH HẦU – HO GÀ – UỐN VÁN – BẠI LIỆT', 0, null, null, null, null, null, '2021-07-03 20:13:33', '2021-07-03 20:13:33', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (51, 'Tetraxim', 50, 'Đang sử dụng', 286, '/storage/vaccine_photo/WVR1ez9iChz8nTafSclw.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bạch hầu, Ho g&agrave;, Uốn v&aacute;n, Bại liệt l&agrave; những căn bệnh truyền nhiễm rất nguy hiểm. Mọi đối tượng từ trẻ em đến người lớn, phụ nữ mang thai, người cao tuổi đều c&oacute; nguy cơ mắc c&aacute;c bệnh n&agrave;y. Ch&iacute;nh v&igrave; vậy, ti&ecirc;m vắc xin l&agrave; c&aacute;ch hiệu quả nhất để ph&ograve;ng bệnh cho bản th&acirc;n v&agrave; ngăn ngừa dịch bệnh trong cộng đồng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bệnh bạch hầu (Diphtheria) l&agrave; bệnh nhiễm khuẩn cấp t&iacute;nh c&oacute; giả mạc ở tuyến hạnh nh&acirc;n, hầu họng, thanh quản, mũi. Bệnh l&acirc;y truyền trực tiếp từ người sang người qua đường h&ocirc; hấp. Bệnh nh&acirc;n dễ bị nhiễm tr&ugrave;ng, nhiễm độc, g&acirc;y liệt cơ, vi&ecirc;m cơ tim, c&oacute; thể tử vong trong v&ograve;ng 6 ng&agrave;y.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Ho g&agrave; (Pertussis) l&agrave; bệnh nhiễm khuẩn cấp t&iacute;nh đường h&ocirc; hấp. Bệnh c&oacute; thể xảy ra ở mọi lứa tuổi, chủ yếu l&agrave; trẻ dưới 1 tuổi chưa ti&ecirc;m hoặc chưa ho&agrave;n th&agrave;nh việc ti&ecirc;m ph&ograve;ng vắc xin ho g&agrave;.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Uốn v&aacute;n (Tetanus) l&agrave; bệnh nhiễm tr&ugrave;ng cấp t&iacute;nh c&oacute; tỷ lệ tử vong rất cao từ 20 &ndash; 90% v&agrave; l&ecirc;n đến 95% ở trẻ sơ sinh. Phụ nữ mang thai v&agrave; trẻ nhỏ l&agrave; những đối tượng cần được ti&ecirc;m vắc xin ph&ograve;ng bệnh uốn v&aacute;n nhất.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bệnh bại liệt (Poliomyelitis) l&agrave; bệnh nhiễm vi r&uacute;t cấp t&iacute;nh l&acirc;y truyền theo đường ti&ecirc;u h&oacute;a do virus Polio g&acirc;y ra. Đ&acirc;y l&agrave; bệnh thường gặp ở trẻ em với c&aacute;c triệu chứng đau cơ, liệt tủy sống, suy h&ocirc; hấp&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin Tetraxim l&agrave; vắc xin tổng hợp của Ph&aacute;p gi&uacute;p ph&ograve;ng ngừa 4 bệnh Bạch hầu, Ho g&agrave;, Uốn V&aacute;n, Bại liệt d&agrave;nh cho trẻ từ 2 th&aacute;ng tuổi.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin vắc xin Tetraxim (Ph&aacute;p) ph&ograve;ng bệnh Bạch hầu &ndash; Ho g&agrave; &ndash; Uốn v&aacute;n &ndash; Bại liệt</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Vắc xin Tetraxim 0.5 ml l&agrave; vắc xin 4 trong 1 được chỉ định để ph&ograve;ng ngừa c&aacute;c bệnh Bạch hầu &ndash; Ho g&agrave; &ndash; Uốn v&aacute;n &ndash; Ho g&agrave; &ndash; Bại liệt ở trẻ từ 2 th&aacute;ng tuổi trở l&ecirc;n khi ti&ecirc;m chủng cơ bản hay nhắc lại trong năm tuổi thứ 2 hoặc ở trẻ em từ độ tuổi 5 &ndash; 11 tuổi v&agrave; 11 &ndash; 13 tuổi t&ugrave;y theo mỗi quốc gia.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Sanofi Pasteur &ndash; Ph&aacute;p.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Chỉ định bảo vệ ph&ograve;ng bệnh&nbsp; bạch hầu, uốn v&aacute;n, ho g&agrave; v&agrave; bại liệt ở trẻ em từ 2 th&aacute;ng đến 13 tuổi (t&ugrave;y theo khuyến c&aacute;o ch&iacute;nh thức của quốc gia).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp ở trẻ nhũ nhi v&agrave; ti&ecirc;m v&ugrave;ng cơ delta ở trẻ 5 &ndash; 13 tuổi.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Trẻ bị dị ứng với một trong c&aacute;c th&agrave;nh phần của vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Nếu trẻ bị bệnh n&atilde;o tiến triển (thương tổn ở n&atilde;o).</li>
<li style="box-sizing: border-box; text-align: justify;">Nếu trẻ từng bị bệnh n&atilde;o (tổn thương ở n&atilde;o) trong v&ograve;ng 7 ng&agrave;y sau khi ti&ecirc;m liều vắc xin ho g&agrave; (ho g&agrave; v&ocirc; b&agrave;o hay to&agrave;n tế b&agrave;o)</li>
<li style="box-sizing: border-box; text-align: justify;">Nếu trẻ bị sốt hay bị bệnh cấp t&iacute;nh (phải ho&atilde;n việc ti&ecirc;m ngừa lại).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m chủng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Lịch ti&ecirc;m chủng ở trẻ từ 2 th&aacute;ng tuổi đến 2 tuổi:</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box;">Mũi 1: khi trẻ 02 th&aacute;ng tuổi</li>
<li style="box-sizing: border-box;">Mũi 2: khi trẻ 03 th&aacute;ng tuổi</li>
<li style="box-sizing: border-box;">Mũi 3: khi trẻ 04 th&aacute;ng tuổi</li>
<li style="box-sizing: border-box;">Mũi 4: 01 năm sau mũi 3</li>
<li style="box-sizing: border-box;">Mũi 5: 03 năm sau mũi 4 (trẻ 4-6 tuổi)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;"><em style="box-sizing: border-box;"><span style="box-sizing: border-box; text-decoration-line: underline;">Lịch ti&ecirc;m chủng ở trẻ từ 2 tuổi trở l&ecirc;n m&agrave; chưa được ti&ecirc;m đủ 4 mũi cơ bản bạch hầu, ho g&agrave;, uốn v&aacute;n, bại liệt: lịch ti&ecirc;m 3 liều</span></em></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box;">Mũi 1: lần đầu ti&ecirc;n</li>
<li style="box-sizing: border-box;">Mũi 2: một th&aacute;ng sau mũi 1</li>
<li style="box-sizing: border-box;">Mũi 3: s&aacute;u th&aacute;ng sau mũi 2</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; Bảo đảm rằng kh&ocirc;ng ti&ecirc;m vắc xin v&agrave;o l&ograve;ng mạch m&aacute;u cũng như kh&ocirc;ng ti&ecirc;m trong da.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; Trẻ c&oacute; tiền sử dị ứng, đặc biệt l&agrave; phản ứng dị ứng sau khi ti&ecirc;m Tetraxim hoặc đang được điều trị bằng thuốc ức chế miễn dịch hoặc bị suy giảm miễn dịch.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; Đ&atilde; từng c&oacute; những triệu chứng sau đ&acirc;y m&agrave; được cho rằng c&oacute; li&ecirc;n quan đến vắc xin:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Sốt tr&ecirc;n 40 độ C trong v&ograve;ng 48 giờ m&agrave; kh&ocirc;ng c&oacute; nguy&ecirc;n nh&acirc;n x&aacute;c định n&agrave;o kh&aacute;c</li>
<li style="box-sizing: border-box; text-align: justify;">Trụy mạch hay t&igrave;nh trạng giống sốc với giai đoạn giảm trương lực, giảm đ&aacute;p ứng trong v&ograve;ng 48 giờ sau ti&ecirc;m ngừa.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&oacute;c th&eacute;t dai dẳng tr&ecirc;n 3 giờ, xảy ra trong v&ograve;ng 48 giờ sau ti&ecirc;m ngừa</li>
<li style="box-sizing: border-box; text-align: justify;">Co giật c&oacute; sốt hay kh&ocirc;ng c&oacute; sốt, xảy ra trong v&ograve;ng 3 ng&agrave;y sau khi ti&ecirc;m ngừa.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Khi ti&ecirc;m chủng c&aacute;c liều cơ bản hay ti&ecirc;m nhắc liều thứ nhất, TETRAXIM c&oacute; thể được d&ugrave;ng bằng c&aacute;ch ho&agrave;n nguy&ecirc;n với vắc xin cộng hợp Haemophilus influenzae t&yacute;p b (Act-HIB), hoặc ti&ecirc;m c&ugrave;ng l&uacute;c với vắc xin cộng hợp Haemophilus influenzae t&yacute;p b (Act-HIB) tại hai vị tr&iacute; ti&ecirc;m kh&aacute;c nhau.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; C&aacute;c phản ứng thường gặp:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng tại vị tr&iacute; ti&ecirc;m như nổi quầng đỏ, nốt cứng lớn hơn 2cm;</li>
<li style="box-sizing: border-box; text-align: justify;">Phản ứng to&agrave;n th&acirc;n: sốt, đ&ocirc;i khi &ge; 40&deg;C. Ti&ecirc;u chảy, n&ocirc;n mửa, ch&aacute;n ăn, buồn ngủ, bồn chồn, dễ bị k&iacute;ch th&iacute;ch; mất ngủ hay x&aacute;o trộn giấc ngủ; kh&oacute;c nh&egrave; bất thường, kh&oacute;c kh&oacute; dỗ k&eacute;o d&agrave;i.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">&ndash; C&aacute;c triệu chứng giống dị ứng như: ph&aacute;t ban, hồng ban v&agrave; m&agrave;y đay, ph&ugrave; mặt, sưng mặt v&agrave; cổ đột ngột.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Bảo quản ở nhiệt độ từ +2&deg;C đến +8&deg;C (trong tủ lạnh). Kh&ocirc;ng được để đ&ocirc;ng băng.</li>
</ul>', '2021-07-03 20:14:00', '2021-07-04 22:13:14', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (53, 'PHÒNG BỆNH DẠI', 0, null, null, null, null, null, '2021-07-03 20:15:58', '2021-07-03 20:15:58', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (54, 'Abhayrab', 53, 'Đang sử dụng', 286, '/storage/vaccine_photo/AK7SIJLodQaT7O6cgpY3.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bệnh dại l&agrave; bệnh truyền nhiễm cấp t&iacute;nh do virus Rabies g&acirc;y ra. Theo thống k&ecirc; từ Tổ chức Y tế Thế giới (WHO), mỗi năm c&oacute; đến 59.000 người tử vong do dại. Trong 9 th&aacute;ng đầu năm 2019, nước ta ghi nhận 54 ca tử vong do bệnh dại, bệnh c&oacute; xu hướng lan rộng khắp c&aacute;c tỉnh th&agrave;nh. Phần lớn mọi người vẫn c&ograve;n chủ quan trong ph&ograve;ng ngừa căn bệnh nguy hiểm n&agrave;y.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Những triệu chứng sớm của bệnh dại thường gặp như: sốt, đau đầu, đau họng, buồn n&ocirc;n&hellip; Ở giai đoạn muộn người mắc dại thường trở n&ecirc;n hung hăng, hay nhầm lẫn, kh&oacute; thở, sợ nước, s&ugrave;i bọt m&eacute;p, h&ocirc;n m&ecirc; v&agrave; c&oacute; thể tử vong bất cứ l&uacute;c n&agrave;o.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Bệnh dại tuy hiếm gặp nhưng lại rất nguy hiểm, 100% người mắc dại sẽ tử vong nếu như kh&ocirc;ng được ti&ecirc;m ph&ograve;ng đầy đủ. Ch&iacute;nh v&igrave; vậy, chủ động ti&ecirc;m ph&ograve;ng bệnh dại l&agrave; việc rất cần thiết. Hiện VNVC đang c&oacute; sẵn vắc xin Abhayrab của Ấn Độ, c&oacute; t&aacute;c dụng tạo miễn dịch chủ động ph&ograve;ng bệnh dại cho trẻ em v&agrave; người lớn.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về Vắc xin Abhayrab (Ấn Độ)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Tạo miễn dịch chủ động chống lại virus Dại để dự ph&ograve;ng v&agrave; điều trị sau khi phơi nhiễm cho người ở tất cả c&aacute;c lứa tuổi (sau khi tiếp x&uacute;c với con vật bị dại hoặc nghi ngờ dại).</li>
<li style="box-sizing: border-box; text-align: justify;">Để tạo miễn dịch dự ph&ograve;ng của những nh&oacute;m đối tượng c&oacute; nguy cơ cao như b&aacute;c sỹ th&uacute; y, nh&acirc;n vi&ecirc;n y tế, nh&acirc;n vi&ecirc;n l&agrave;m việc trong rừng hoặc sở th&uacute;, thợ săn, nh&acirc;n vi&ecirc;n ph&ograve;ng th&iacute; nghiệm c&oacute; tiếp x&uacute;c với nguy&ecirc;n liệu c&oacute; nghi ngờ mang virus dại, người c&oacute; vật nu&ocirc;i trong nh&agrave;.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">C&aacute;ch d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ho&agrave;n nguy&ecirc;n vắc xin đ&ocirc;ng kh&ocirc; c&ugrave;ng với dung dịch pha lo&atilde;ng k&egrave;m theo. Ti&ecirc;m bắp vắc xin ho&agrave;n nguy&ecirc;n (to&agrave;n bộ vắc xin chứa trong lọ) v&agrave;o v&ugrave;ng cơ delta. Vắc xin ho&agrave;n nguy&ecirc;n phải được sử dụng ngay v&agrave; kh&ocirc;ng giữ lại để sử dụng sau.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp (IM): người lớn ti&ecirc;m ở v&ugrave;ng cơ Delta c&aacute;nh tay, trẻ em ti&ecirc;m ở mặt trước b&ecirc;n đ&ugrave;i. Kh&ocirc;ng ti&ecirc;m v&agrave;o v&ugrave;ng m&ocirc;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Trong một số trường hợp c&oacute; thể &aacute;p dụng Ti&ecirc;m trong da (ID), ti&ecirc;m ở cẳng tay hoặc c&aacute;nh tay.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Liều d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Một liều ti&ecirc;m bắp (IM) l&agrave; 0,5mL vắc xin đ&atilde; ho&agrave;n nguy&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Một liều ti&ecirc;m trong da (ID) l&agrave; 0,1mL vắc xin đ&atilde; ho&agrave;n nguy&ecirc;n, nghĩa l&agrave; bằng 1/5 liều ti&ecirc;m bắp.</li>
<li style="box-sizing: border-box; text-align: justify;">Ph&aacute;c đồ ti&ecirc;m vắc xin n&ecirc;n được &aacute;p dụng theo t&igrave;nh huống ti&ecirc;m vắc xin v&agrave; t&igrave;nh trạng miễn dịch đối với bệnh dại của người ti&ecirc;m.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m vắc xin dại:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;"><span style="box-sizing: border-box; text-decoration-line: underline;"><em style="box-sizing: border-box;">Ph&aacute;c đồ ti&ecirc;m ph&ograve;ng dại trước khi phơi nhiễm (chưa bị cắn)</em></span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box;">Ti&ecirc;m bắp 3 mũi (0.5ml/liều) v&agrave;o c&aacute;c ng&agrave;y N0, N7 v&agrave; N21 hoặc N28.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; text-decoration-line: underline;"><em style="box-sizing: border-box;">Ti&ecirc;m ở bắp, ph&aacute;c đồ ti&ecirc;m ph&ograve;ng dại khi đ&atilde; x&aacute;c định c&oacute; phơi nhiễm (đ&atilde; bị cắn)</em></span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Người chưa ti&ecirc;m dự ph&ograve;ng: Ti&ecirc;m 5 mũi (0.5ml/liều) v&agrave;o c&aacute;c ng&agrave;y 0, 3, 7, 14 v&agrave; 28. Trong trường hợp phơi nhiễm độ III, cần phối hợp ti&ecirc;m Immunoglobulin dại v&agrave; vắc xin dại.</li>
<li style="box-sizing: border-box; text-align: justify;">Người đ&atilde; ti&ecirc;m dự ph&ograve;ng trước hoặc sau phơi nhiễm &iacute;t nhất 3 mũi vắc xin dại nu&ocirc;i cấy tr&ecirc;n tế b&agrave;o: Ti&ecirc;m 2 mũi v&agrave;o ng&agrave;y N0 v&agrave; N3.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; text-decoration-line: underline;"><em style="box-sizing: border-box;">Ti&ecirc;m trong da, ph&aacute;c đồ ti&ecirc;m ph&ograve;ng dại, liều 0.1ml vacxin ho&agrave;n nguy&ecirc;n như sau</em></span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Người chưa ti&ecirc;m dự ph&ograve;ng: Ti&ecirc;m 4 lần x 2 mũi/1 lần: ti&ecirc;m ở vị tr&iacute; hai b&ecirc;n chi kh&aacute;c nhau, mỗi b&ecirc;n liều lượng 0.1ml, v&agrave;o c&aacute;c ng&agrave;y N0, N3, N7 v&agrave; N28.</li>
<li style="box-sizing: border-box; text-align: justify;">Người đ&atilde; ti&ecirc;m dự ph&ograve;ng trước hoặc sau phơi nhiễm &iacute;t nhất 3 mũi vắc xin dại nu&ocirc;i cấy tr&ecirc;n tế b&agrave;o: Ti&ecirc;m 0.1ml/1 mũi v&agrave;o c&aacute;c ng&agrave;y N0 v&agrave; N3.</li>
<li style="box-sizing: border-box; text-align: justify;">Kỹ thuật ti&ecirc;m trong da phải được thực hiện đ&uacute;ng, tr&aacute;nh ti&ecirc;m dưới da.</li>
<li style="box-sizing: border-box; text-align: justify;">Phải d&ugrave;ng bơm kim ti&ecirc;m ri&ecirc;ng để tr&aacute;nh l&acirc;y nhiễm.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m bắp ở người c&oacute; rối loạn chảy m&aacute;u như hemophili hoặc giảm tiểu cầu.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; text-decoration-line: underline;"><em style="box-sizing: border-box;">Trường hợp trước phơi nhiễm:</em></span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Ho&atilde;n ti&ecirc;m khi kh&aacute;ch h&agrave;ng c&oacute; sốt nhiễm tr&ugrave;ng nặng, bệnh cấp t&iacute;nh, đợt tiến triển của bệnh mạn t&iacute;nh.</li>
<li style="box-sizing: border-box; text-align: justify;">Mẫn cảm với bất kỳ th&agrave;nh phần n&agrave;o của vắc xin.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; text-decoration-line: underline;"><em style="box-sizing: border-box;">Trường hợp sau phơi nhiễm:</em></span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Bệnh dại l&agrave; bệnh rất nguy hiểm, do vậy kh&ocirc;ng c&oacute; chống chỉ định n&agrave;o trong trường hợp điều trị sau phơi nhiễm.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng c&oacute; sự tương t&aacute;c với c&aacute;c chế phẩm kh&aacute;c. Tuy nhi&ecirc;n kh&ocirc;ng sử dụng đồng thời c&aacute;c thuốc ức chế miễn dịch như corticosteroid v&igrave; những thuốc n&agrave;y c&oacute; thể cản trở qu&aacute; tr&igrave;nh sinh kh&aacute;ng thể bảo vệ.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng phụ:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Abhayrab l&agrave; vắc xin dại tế b&agrave;o vero tinh chế n&ecirc;n rất an to&agrave;n. C&aacute;c t&aacute;c dụng phụ nhẹ tại chỗ ti&ecirc;m v&agrave; to&agrave;n th&acirc;n như: đau tại chỗ ti&ecirc;m, ngứa, sốt, ch&oacute;ng mặt đau đầu&hellip; n&oacute;i chung &iacute;t gặp. Hiếm hơn nữa l&agrave; c&aacute;c phản ứng sốc phản vệ, m&agrave;y đay.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng sử dụng đồng thời c&aacute;c thuốc ức chế miễn dịch như corticosteroid v&igrave; những thuốc n&agrave;y c&oacute; thể cản trở qu&aacute; tr&igrave;nh sinh kh&aacute;ng thể bảo vệ.</li>
<li style="box-sizing: border-box; text-align: justify;">Sau khi bị cắn, rửa ngay vết thương với x&agrave; ph&ograve;ng v&agrave; dưới v&ograve;i nước chảy li&ecirc;n tục trong thời gian từ 10-15 ph&uacute;t. Nếu kh&ocirc;ng c&oacute; x&agrave; ph&ograve;ng, c&oacute; thể rửa tạm vết thương bằng nước sạch dưới v&ograve;i nước chảy li&ecirc;n tục 15 ph&uacute;t. Đ&acirc;y l&agrave; phương ph&aacute;p sơ cứu hiệu quả nhất để chống lại bệnh dại.</li>
<li style="box-sizing: border-box; text-align: justify;">Tr&aacute;nh băng b&oacute;, đắp thuốc k&iacute;n vết thương, tr&aacute;nh kh&acirc;u vết thương v&igrave; c&oacute; thể khiến virus dại x&acirc;m nhập dễ d&agrave;ng hơn</li>
<li style="box-sizing: border-box; text-align: justify;">Trong trường hợp vết cắn rất nặng v&agrave; tại vị tr&iacute; vết thương, gần đầu, n&ecirc;n thấm tại chỗ vết thương bằng globulin miễn dịch kh&aacute;ng virus dại.</li>
<li style="box-sizing: border-box; text-align: justify;">Tr&igrave; ho&atilde;n việc bắt đầu điều trị sau phơi nhiễm, điều trị kh&ocirc;ng triệt để hay kh&ocirc;ng đủ ph&aacute;c đồ c&oacute; thể l&agrave;m cho việc bảo vệ bị thất bại.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Ch&uacute; &yacute; đặc biệt:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ c&oacute; thai: Hiện chưa c&oacute; c&aacute;c bằng chứng liệu Abhayrab c&oacute; g&acirc;y ra qu&aacute;i thai hay kh&ocirc;ng hoặc ảnh hưởng tr&ecirc;n khả năng sinh sản. V&igrave; vậy chỉ d&ugrave;ng tr&ecirc;n phụ nữ c&oacute; thai khi r&otilde; r&agrave;ng cần thiết</li>
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ cho con b&uacute;: bệnh dại l&agrave; bệnh chết người v&igrave; vậy kh&ocirc;ng c&oacute; hạn chế n&agrave;o tr&ecirc;n phụ nữ cho con b&uacute;</li>
<li style="box-sizing: border-box; text-align: justify;">Sử dụng ở trẻ em: bệnh dại l&agrave; bệnh chết người, kh&ocirc;ng c&oacute; giới hạn tuổi t&aacute;c v&agrave; sử dụng cho trẻ em ở mọi lứa tuổi.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Bảo quản ở nhiệt độ 2-8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C, tr&aacute;nh đ&ocirc;ng đ&aacute;</li>
</ul>', '2021-07-03 20:16:31', '2021-07-04 22:05:54', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (61, 'TyPhoid Vi', 43, 'Đang sử dụng', 286, '/storage/vaccine_photo/sVucVPUl56Drs4nUl2EG.jpg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Thương h&agrave;n l&agrave; bệnh truyền nhiễm cấp t&iacute;nh do vi khuẩn Salmonella typhi g&acirc;y ra. Bệnh dễ d&agrave;ng l&acirc;y nhiễm qua đường ti&ecirc;u h&oacute;a v&agrave; ph&aacute;t triển nhanh th&agrave;nh dịch. Cả trẻ em v&agrave; người lớn đều c&oacute; thể trở th&agrave;nh đối tượng mắc bệnh nếu kh&ocirc;ng được ph&ograve;ng bệnh đ&uacute;ng c&aacute;ch.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">L&agrave; một căn bệnh kh&ocirc;ng được nhiều người quan t&acirc;m, tuy nhi&ecirc;n thương h&agrave;n lại rất nguy hiểm với c&aacute;c triệu chứng sốt, đau đầu, rối loạn ti&ecirc;u h&oacute;a, chảy m&aacute;u cam&hellip; thậm ch&iacute; nếu bệnh trở nặng c&oacute; thể dẫn đến tử vong với c&aacute;c biến chứng nguy hiểm như: thủng ruột non, xuất huyết ti&ecirc;u h&oacute;a, vi&ecirc;m t&uacute;i mật, vi&ecirc;m n&atilde;o&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;">Khuyến kh&iacute;ch ti&ecirc;m vắc xin cho người thường xuy&ecirc;n đi đến v&ugrave;ng c&oacute; dịch bệnh thương h&agrave;n, người sống trong m&ocirc;i trường tập thể, người thường đi du lịch, người hay tiếp x&uacute;c với thực phẩm kh&ocirc;ng đảm bảo vệ sinh an to&agrave;n thực phẩm hoặc di chuyển đến v&ugrave;ng vệ sinh k&eacute;m&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin Vắc xin Thương h&agrave;n TyPhoid Vi (Việt Nam)</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box;">Vắc xin ph&ograve;ng bệnh thương h&agrave;n c&oacute; t&ecirc;n Typhoid Vi được sản xuất tại bởi nh&agrave; sản xuất vắc xin v&agrave; sinh phẩm uy t&iacute;n tại Việt Nam &ndash; Viện Pasteur Đ&agrave; Lạt (DAVAC) v&agrave; được ph&acirc;n phối độc quyền bởi VNVC. Typhoid Vi l&agrave; vắc xin nhằm tạo mi&ecirc;̃n dịch chủ đ&ocirc;̣ng ph&ograve;ng b&ecirc;̣nh thương h&agrave;n cho người lớn v&agrave; trẻ em từ 2 tu&ocirc;̉i trở l&ecirc;n.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin ph&ograve;ng bệnh thương h&agrave;n Typhoid Vi được sản xuất bởi nh&agrave; sản xuất vắc xin v&agrave; sinh phẩm uy t&iacute;n tại Việt Nam &ndash; Viện Pasteur Đ&agrave; Lạt (DAVAC).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Typhoid Vi l&agrave; vắc xin nhằm tạo mi&ecirc;̃n dịch chủ đ&ocirc;̣ng phòng b&ecirc;̣nh thương hàn cho người lớn và trẻ em từ 2 tu&ocirc;̉i trở l&ecirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Khuyến kh&iacute;ch ti&ecirc;m Typhoid Vi cho người thường xuy&ecirc;n đi đến v&ugrave;ng c&oacute; dịch bệnh thương h&agrave;n, người sống trong m&ocirc;i trường tập thể, người thường đi du lịch, người hay tiếp x&uacute;c với thực phẩm kh&ocirc;ng đảm bảo vệ sinh an to&agrave;n thực phẩm hoặc di chuyển đến v&ugrave;ng vệ sinh k&eacute;m&hellip;</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Trẻ em tr&ecirc;n 2 tuổi v&agrave; người lớn ti&ecirc;m 1 mũi duy nhất v&agrave; nhắc lại sau mỗi 3 năm nếu c&oacute; nguy cơ.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m &amp; Liều d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin ph&ograve;ng bệnh thương h&agrave;n Typhoid Vi được chỉ định ti&ecirc;m dưới da hoặc ti&ecirc;m bắp với liều 0.5ml.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Hướng dẫn sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Mở nắp nh&ocirc;m, t&acirc;̉y trùng nắp lọ vắc xin trước khi l&acirc;́y ra m&ocirc;̣t li&ecirc;̀u vắc xin bằng bơm ti&ecirc;m v&agrave; kim ti&ecirc;m v&ocirc; khuẩn ph&ugrave; hợp.</li>
<li style="box-sizing: border-box; text-align: justify;">Thay kim ti&ecirc;m kh&aacute;c để tiến h&agrave;nh ti&ecirc;m chủng.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng sử dụng Typhoid Vi cho trường hợp có ti&ecirc;̀n sử dị ứng với m&ocirc;̣t trong các thành ph&acirc;̀n của vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ đang mang thai, trường hợp bắt bu&ocirc;̣c phải ti&ecirc;m, c&acirc;̀n hỏi ý ki&ecirc;́n của bác sĩ.</li>
<li style="box-sizing: border-box; text-align: justify;">Trong trường hợp bị sốt hoặc mắc bệnh cấp t&iacute;nh, n&ecirc;n ho&atilde;n ti&ecirc;m vắc xin.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng khuyến c&aacute;o ti&ecirc;m cho trẻ em dưới 2 tuổi</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m theo đường nội mạch.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng thường nhẹ, nhanh ch&oacute;ng biến mất. Khi ti&ecirc;m vắc xin, tại chỗ ti&ecirc;m c&oacute; thể sưng đỏ. C&oacute; triệu chứng sốt nhẹ v&agrave; sẽ hết sau 24h kể từ khi ti&ecirc;m vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Hiếm gặp phản ứng phản vệ.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Typhoid Vi kh&ocirc;ng được trộn lẫn với bất cứ vắc xin hoặc dược phẩm n&agrave;o trong c&ugrave;ng 1 ống ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Nếu phải ti&ecirc;m th&ecirc;m 1 loại vắc xin kh&aacute;c c&ugrave;ng l&uacute;c với Typhoid Vi, vắc xin cần được ti&ecirc;m ở 1 vị tr&iacute; ri&ecirc;ng biệt.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; caret-color: #6d6e70; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-size-adjust: auto;">
<li style="box-sizing: border-box; text-align: justify;">Bảo quản ở nhiệt độ lạnh (từ 2 &ndash; 8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C). Kh&ocirc;ng được đ&oacute;ng băng.</li>
</ul>', '2021-07-03 20:29:13', '2021-07-04 22:11:38', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (62, 'Vaxigrip', 22, 'Đang sử dụng', null, '/storage/vaccine_photo/klEVIMqTGQsU8NWybf3d.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Theo thống k&ecirc;, khoảng 10% d&acirc;n số thế giới (500-800 triệu người) mắc c&uacute;m mỗi năm. Trong suốt m&ugrave;a c&uacute;m, c&oacute; đến 40% trẻ nhỏ v&agrave; 30% học sinh nhiễm bệnh.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">C&uacute;m l&agrave; bệnh truyền nhiễm cấp t&iacute;nh, l&acirc;y lan nhanh theo đường h&ocirc; hấp. Một người nhiễm c&uacute;m c&oacute; thể l&acirc;y nhiễm cho gia đ&igrave;nh, những người xung quanh khi ho, hắt hơi v&agrave; nhanh ch&oacute;ng trở th&agrave;nh dịch nếu kh&ocirc;ng c&oacute; biện ph&aacute;p ph&ograve;ng ngừa.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Hiện nay, ti&ecirc;m vắc xin ph&ograve;ng c&uacute;m h&agrave;ng năm l&agrave; biện ph&aacute;p hiệu quả, đơn giản v&agrave; chi ph&iacute; &iacute;t nhất để ph&ograve;ng ngừa c&uacute;m.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin về vắc xin ngừa c&uacute;m Vaxigrip (Ph&aacute;p)</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Vaxigrip c&oacute; nguồn gốc từ Sanofi Pasteur (Ph&aacute;p), được chỉ định dự ph&ograve;ng bệnh c&uacute;m cho trẻ từ 6 th&aacute;ng tuổi v&agrave; người lớn, đặc biệt tr&ecirc;n những người c&oacute; nguy cơ biến chứng cao.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Sanofi Pasteur (Ph&aacute;p)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Vaxigrip được chỉ định dự ph&ograve;ng bệnh c&uacute;m, đặc biệt tr&ecirc;n những người c&oacute; nguy cơ biến chứng cao</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m chủng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Trẻ từ 6 th&aacute;ng &ndash; 9 tuổi chưa từng ti&ecirc;m vắc xin c&uacute;m:</li>
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m 2 mũi c&aacute;ch nhau tối thiểu 1 th&aacute;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Sau đ&oacute; ti&ecirc;m nhắc lại 1 mũi hằng năm.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ tr&ecirc;n 9 tuổi v&agrave; người lớn:</li>
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m 1 mũi 0.5ml</li>
<li style="box-sizing: border-box; text-align: justify;">Sau đ&oacute; ti&ecirc;m nhắc lại h&agrave;ng năm.</li>
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ c&oacute; kế hoạch mang thai n&ecirc;n ti&ecirc;m vắc xin ph&ograve;ng c&uacute;m trước khi c&oacute; thai hoặc 3 th&aacute;ng giữa thai kỳ.</li>
<li style="box-sizing: border-box; text-align: justify;">N&ecirc;n ti&ecirc;m chủng nhắc lại h&agrave;ng năm hoặc v&agrave;o đầu c&aacute;c m&ugrave;a c&oacute; nguy cơ b&ugrave;ng ph&aacute;t dịch.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Liều d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Trẻ từ 6-36 th&aacute;ng tuổi: 0,25ml.</li>
<li style="box-sizing: border-box; text-align: justify;">Trẻ từ 36 th&aacute;ng tuổi trở l&ecirc;n v&agrave; người lớn: 0,5ml.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp hay ti&ecirc;m dưới da s&acirc;u.</li>
<li style="box-sizing: border-box; text-align: justify;">Lắc kỹ để tạo hỗn dịch đồng nhất trước khi ti&ecirc;m.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">C&aacute;ch d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Để d&ugrave;ng dạng tr&igrave;nh b&agrave;y một liều 0.5ml chứa sẵn trong bơm ti&ecirc;m cho c&aacute;c trẻ c&oacute; chỉ định liều 0.25ml (nửa liều): đẩy piston một c&aacute;ch ch&iacute;nh x&aacute;c đến mức vạch tr&ecirc;n bơm ti&ecirc;m để loại bỏ nửa thể t&iacute;ch. Thể t&iacute;ch c&ograve;n lại được d&ugrave;ng để ti&ecirc;m cho trẻ.</li>
<li style="box-sizing: border-box; text-align: justify;">Để d&ugrave;ng dạng tr&igrave;nh b&agrave;y một liều 0.5 ml trong ống ti&ecirc;m cho c&aacute;c trẻ c&oacute; chỉ định liều 0.25ml (nửa liều): D&ugrave;ng bơm ti&ecirc;m chia vạch h&uacute;t một nửa thể t&iacute;ch c&oacute; trong ống, phần thể t&iacute;ch c&ograve;n lại trong ống ti&ecirc;m kh&ocirc;ng được ti&ecirc;m hay giữ lại.</li>
<li style="box-sizing: border-box; text-align: justify;">Để d&ugrave;ng dạng lọ đa liều: s&aacute;t khuẩn nắp lọ ti&ecirc;m, d&ugrave;ng bơm ti&ecirc;m v&ocirc; khuẩn gắn sẵn kim ti&ecirc;m r&uacute;t từng liều đơn từ lọ đa liều. Lặp lại thao t&aacute;c n&agrave;y cho c&aacute;c liều tiếp theo.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Lưu &yacute;: giữa c&aacute;c lần r&uacute;t thuốc v&agrave; trong bất kỳ trường hợp n&agrave;o, kh&ocirc;ng qu&aacute; 5 ph&uacute;t kể từ khi ngưng r&uacute;t thuốc, lọ vắc xin phải được đặt v&agrave;o tủ lạnh để bảo quản ở nhiệt độ 2-80C (kh&ocirc;ng để đ&ocirc;ng băng).</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được ti&ecirc;m Vaxigrip v&agrave;o tĩnh mạch.</li>
<li style="box-sizing: border-box; text-align: justify;">Thận trọng khi ti&ecirc;m vắc xin cho người bị giảm tiểu cầu hay bị rối loạn chảy m&aacute;u v&igrave; những người n&agrave;y c&oacute; thể bị chảy m&aacute;u khi ti&ecirc;m bắp.<br style="box-sizing: border-box;" />Do trong vắc xin c&oacute; vết như trứng (ovalbumin, protein của g&agrave;), neomycin, formaldehydel n&ecirc;n cẩn trọng với người dị ứng với c&aacute;c th&agrave;nh phần n&agrave;y.</li>
<li style="box-sizing: border-box; text-align: justify;">Thận trọng khi sử dụng cho người suy giảm miễn dịch.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng tại chỗ: ban đỏ (quầng đỏ), sưng, đau, bầm m&aacute;u, nốt cứng.</li>
<li style="box-sizing: border-box; text-align: justify;">Phản ứng to&agrave;n th&acirc;n: sốt, kh&oacute; chịu, run rẩy, mệt mỏi, đau đầu, đổ mồ h&ocirc;i, đau khớp v&agrave; đau cơ.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Vaxigrip c&oacute; thể ti&ecirc;m c&ugrave;ng thời điểm với c&aacute;c vắc xin kh&aacute;c nhưng phải ti&ecirc;m ở c&aacute;c vị tr&iacute; kh&aacute;c nhau.</li>
<li style="box-sizing: border-box; text-align: justify;">Đ&aacute;p ứng miễn dịch c&oacute; thể giảm khi đang d&ugrave;ng thuốc ức chế miễn dịch kh&aacute;c.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin Vaxigrip được bảo quản ở nhiệt độ 2-8<span style="box-sizing: border-box; position: relative; font-size: 12px; line-height: 0; vertical-align: baseline; top: -0.5em;">o</span>C nhưng kh&ocirc;ng để đ&ocirc;ng băng v&agrave; tr&aacute;nh &aacute;nh s&aacute;ng.</li>
</ul>', '2021-07-03 20:36:19', '2021-07-03 20:36:54', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (63, 'PHÒNG BỆNH BẠCH HẦU – HO GÀ – UỐN VÁN', 0, null, null, null, null, null, '2021-07-03 20:40:44', '2021-07-03 20:40:44', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (65, 'Adacel', 63, 'Đang sử dụng', 286, '/storage/vaccine_photo/QUtV69sxOqxVmNyKspAq.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Bạch hầu &ndash; Ho g&agrave; &ndash; Uốn v&aacute;n l&agrave; những căn bệnh nguy hiểm h&agrave;ng đầu đối với trẻ nhỏ đặc biệt trong những năm đầu đời. Bệnh để lại những hậu quả nặng nề như vi&ecirc;m cơ tim, vi&ecirc;m d&acirc;y thần kinh, co thắt, ngừng thở thậm ch&iacute; tử vong.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Uốn v&aacute;n l&agrave; bệnh nghi&ecirc;m trọng do vi khuẩn g&acirc;y n&ecirc;n, c&oacute; thể dẫn đến co thắt cơ v&agrave; c&aacute;c vấn đề về h&ocirc; hấp. Một số loại vết thương c&oacute; xu hướng k&iacute;ch th&iacute;ch sự ph&aacute;t triển của vi khuẩn uốn v&aacute;n như: g&atilde;y xương hợp chất, vết cắn của động vật, vết bỏng hoặc vết thương ở v&ugrave;ng đất bị &ocirc; nhiễm,&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Ho g&agrave; l&agrave; bệnh nhiễm tr&ugrave;ng h&ocirc; hấp c&oacute; t&iacute;nh l&acirc;y nhiễm cao. Khi nhiễm bệnh, trẻ ho li&ecirc;n tiếp từng cơn 15-20 tiếng, kh&ocirc;ng k&igrave;m được, lưỡi đẩy ra ngo&agrave;i, t&iacute;m t&aacute;i, chảy nước mắt. Về sau tiếng ho yếu dần, chỉ thấy trẻ t&iacute;m t&aacute;i do ngừng thở.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Bạch hầu l&agrave; bệnh nhiễm khuẩn cấp t&iacute;nh c&oacute; giả mạc ở tuyến hạnh nh&acirc;n, hầu họng, thanh quản, mũi. Nếu kh&ocirc;ng được ph&aacute;t hiện v&agrave; điều trị kịp thời, bệnh nh&acirc;n c&oacute; thể gặp biến chứng vi&ecirc;m cơ tim, tổn thương hệ thần kinh, tử vong do đột ngột trụy tim mạch&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin ph&ograve;ng bệnh Bạch hầu &ndash; Ho g&agrave; &ndash; Uốn v&aacute;n được khuyến c&aacute;o n&ecirc;n ti&ecirc;m đ&uacute;ng lịch, đủ mũi bao gồm cả mũi ti&ecirc;m nhắc lại do sự b&ugrave;ng ph&aacute;t của c&aacute;c bệnh n&agrave;y tr&ecirc;n to&agrave;n thế giới ng&agrave;y c&agrave;ng gia tăng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin Vắc xin Adacel (Canada) ph&ograve;ng bệnh Bạch hầu &ndash; Ho g&agrave; &ndash; Uốn v&aacute;n</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin 3 trong 1 Adacel ph&ograve;ng 3 bệnh Bạch hầu &ndash; Ho g&agrave; &ndash; Uốn v&aacute;n trong c&ugrave;ng 1 mũi ti&ecirc;m được chỉ định ti&ecirc;m cho trẻ từ 4 tuổi trở l&ecirc;n v&agrave; người lớn đến 64 tuổi. Ph&aacute;c đồ chỉ 1 mũi duy nhất c&oacute; thể nhắc lại sau 10 năm để bổ sung kh&aacute;ng thể cho cơ thể.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Sanofi Pasteur &ndash; Ph&aacute;p. Sản xuất tại Canada.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Adacel được chỉ định để g&acirc;y miễn dịch chủ động nhắc lại nhằm ph&ograve;ng bệnh uốn v&aacute;n, bạch hầu v&agrave; ho g&agrave;, với một liều duy nhất ở người từ 4 đến 64 tuổi.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m bắp (n&ecirc;n ti&ecirc;m ở v&ugrave;ng cơ delta).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Người qu&aacute; mẫn với th&agrave;nh phần của vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Người rối loạn thần kinh cấp t&iacute;nh.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m chủng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Người từ 4 &ndash; 64 tuổi: 1 liều duy nhất. Ti&ecirc;m nhắc lại sau mỗi 10 năm.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng sử dụng c&aacute;c đường ti&ecirc;m ngo&agrave;i khuyến c&aacute;o.</li>
<li style="box-sizing: border-box; text-align: justify;">Ho&atilde;n việc sử dụng vắc xin khi c&oacute; sốt cao hoặc c&aacute;c bệnh cấp t&iacute;nh.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng n&ecirc;n ti&ecirc;m bắp Adacel cho những người bị c&aacute;c rối loạn chảy m&aacute;u, như bệnh m&aacute;u kh&oacute; đ&ocirc;ng hoặc giảm tiểu cầu.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể ti&ecirc;m Adacel c&ugrave;ng l&uacute;c với vắc-xin c&uacute;m v&agrave; vắc-xin vi&ecirc;m gan B.</li>
<li style="box-sizing: border-box; text-align: justify;">Khi ti&ecirc;m c&aacute;c vắc-xin c&ugrave;ng l&uacute;c th&igrave; phải d&ugrave;ng những bơm ti&ecirc;m ri&ecirc;ng v&agrave; ti&ecirc;m ở những vị tr&iacute; kh&aacute;c nhau v&agrave; tốt nhất l&agrave; n&ecirc;n ti&ecirc;m ở c&aacute;c chi kh&aacute;c nhau.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Đau tại nơi ti&ecirc;m l&agrave; phản ứng tại chỗ hay gặp nhất, mệt mỏi ở trẻ em v&agrave; nhức đầu ở thiếu ni&ecirc;n v&agrave; người lớn, sốt được b&aacute;o c&aacute;o ở dưới 10% số người được ti&ecirc;m vắc-xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Phản ứng dị ứng qu&aacute; mẫn, ngứa, m&agrave;y đay.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Bảo quản ở 2&deg;- 8&deg;C. Kh&ocirc;ng được để đ&ocirc;ng băng.</li>
</ul>', '2021-07-03 20:41:43', '2021-07-03 20:42:51', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (66, 'PHÒNG BỆNH UỐN VÁN - BẠCH HẦU', 0, null, null, null, null, null, '2021-07-03 20:46:06', '2021-07-03 20:46:06', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (67, 'Vắc xin uốn ván – bạch hầu hấp phụ', 66, 'Đang sử dụng', 286, '/storage/vaccine_photo/4MMY8h9ZnxxnFZS9hAp0.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin uốn v&aacute;n &ndash; bạch hầu hấp phụ (Td) được chỉ định cho trẻ em lứa tuổi lớn (từ 7 tuổi trở l&ecirc;n) v&agrave; người lớn nhằm g&acirc;y miễn dịch, ph&ograve;ng c&aacute;c bệnh uốn v&aacute;n v&agrave; bạch hầu.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">L&agrave; vắc xin được sản xuất bởi Viện vắc xin v&agrave; sinh phẩm y tế Nha Trang IVAC (Việt Nam)</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin uốn v&aacute;n &ndash; bạch hầu hấp phụ (Td) được chỉ định chủng ngừa cho người từ 7 tuổi trở l&ecirc;n tạo đ&aacute;p ứng kh&aacute;ng thể chống 2 bệnh bạch hầu v&agrave; uốn v&aacute;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin uốn v&aacute;n &ndash; bạch hầu hấp phụ (Td) kh&ocirc;ng chống chỉ định cho phụ nữ c&oacute; thai v&agrave; đang cho con b&uacute;. C&acirc;n nhắc sử dụng theo chỉ định của b&aacute;c sĩ.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Với đối tượng đ&atilde; được ti&ecirc;m đủ liều miễn dịch cơ bản ph&ograve;ng bệnh uốn v&aacute;n v&agrave; bạch hầu th&igrave; ti&ecirc;m nhắc lại 1 liều vắc xin uốn v&aacute;n &ndash; bạch hầu hấp phụ (Td) v&agrave;o lứa tuổi thứ 7. Sau đ&oacute; cứ 10 năm ti&ecirc;m ph&ograve;ng lại 1 liều vắc xin Td để củng cố miễn dịch.</li>
<li style="box-sizing: border-box; text-align: justify;">Với trẻ từ 7 tuổi trở l&ecirc;n nhưng trước đ&oacute; chưa được ti&ecirc;m vắc xin ph&ograve;ng uốn v&aacute;n, bạch hầu, th&igrave; ti&ecirc;m miễn dịch cơ bản 2 liều, liều thứ hai c&aacute;ch liều thứ nhất 1 th&aacute;ng, sau 6 th&aacute;ng ti&ecirc;m nhắc 1 liều. Sau đ&oacute; cứ 10 năm sau ti&ecirc;m nhắc lại 1 lần để củng cố miễn dịch.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đường ti&ecirc;m &ndash; liều d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin uốn v&aacute;n &ndash; bạch hầu hấp phụ (Td) được chỉ định ti&ecirc;m bắp s&acirc;u. Kh&ocirc;ng được ti&ecirc;m dưới da hoặc tĩnh mạch.</li>
<li style="box-sizing: border-box; text-align: justify;">Lắc tan đều trước khi d&ugrave;ng.</li>
<li style="box-sizing: border-box; text-align: justify;">Liều ti&ecirc;m 0,5 ml.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Tiền sử qu&aacute; mẫn với th&agrave;nh phần của vắc xin. C&oacute; biểu hiện dị ứng với kh&aacute;ng nguy&ecirc;n bạch hầu v&agrave; uốn v&aacute;n ở những lần ti&ecirc;m trước.</li>
<li style="box-sizing: border-box; text-align: justify;">Tạm ho&atilde;n ti&ecirc;m vắc xin uốn v&aacute;n &ndash; bạch hầu hấp phụ (Td) trong những trường hợp c&oacute; bệnh nhiễm tr&ugrave;ng cấp t&iacute;nh, sốt chưa r&otilde; nguy&ecirc;n nh&acirc;n.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m bắp cho người c&oacute; rối loạn chảy m&aacute;u như Hemophilia hoặc giảm tiểu cầu.</li>
<li style="box-sizing: border-box; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng khi sử dụng:</span></li>
<li style="box-sizing: border-box; text-align: justify;">Đối tượng đang sốt cao cấp t&iacute;nh (ho&atilde;n ti&ecirc;m cho đến khi trẻ hết sốt).</li>
<li style="box-sizing: border-box; text-align: justify;">Thăm kh&aacute;m trước khi ti&ecirc;m để loại trừ những trường hợp chống chỉ định, bảo đảm an to&agrave;n v&agrave; hạn chế c&aacute;c t&aacute;c dụng bất lợi của vắc xin trong l&uacute;c ti&ecirc;m chủng.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m qu&aacute; liều vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m vắc xin v&agrave;o mạch m&aacute;u, tr&aacute;nh sốc phản vệ.</li>
<li style="box-sizing: border-box; text-align: justify;">Đ&ocirc;i khi tại chỗ ti&ecirc;m xuất hiện nốt cứng k&eacute;o d&agrave;i 2 &ndash; 3 tuần rồi tự khỏi.</li>
<li style="box-sizing: border-box; text-align: justify;">Nếu ti&ecirc;m kết hợp với vắc xin kh&aacute;c hoặc huyết thanh miễn dịch th&igrave; ti&ecirc;m vắc xin Td kh&aacute;c vị tr&iacute;.</li>
<li style="box-sizing: border-box; text-align: justify;">Nếu ti&ecirc;m nhầm vắc xin v&agrave;o dưới da th&igrave; phản ứng phụ sẽ rất rầm rộ do vắc xin chứa t&aacute; chất nh&ocirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Lu&ocirc;n c&oacute; sẵn thiết bị v&agrave; ph&aacute;c đồ ph&ograve;ng sốc phản vệ.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Thăm kh&aacute;m trước khi ti&ecirc;m để loại trừ những trường hợp chống chỉ định, bảo đảm an to&agrave;n v&agrave; hạn chế c&aacute;c t&aacute;c dụng bất lợi của vắc xin trong l&uacute;c ti&ecirc;m chủng.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Phản ứng tại chỗ như đau, quầng đỏ, sưng nhẹ tại vị tr&iacute; ti&ecirc;m (từ 10% &ndash; 75% trường hợp).</li>
<li style="box-sizing: border-box; text-align: justify;">Đ&ocirc;i khi c&oacute; sốt nhẹ 38 độ C &ndash; 39 độ C, v&agrave;i ng&agrave;y sau tự hết.</li>
<li style="box-sizing: border-box; text-align: justify;">&Aacute;p xe v&ocirc; khuẩn khoảng 6 &ndash; 10 trường hợp/1 triệu liều.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể xuất hiện phản ứng phụ to&agrave;n th&acirc;n như sốt, đau cơ c&aacute;nh tay, đau đầu (khoảng 10% trường hợp).</li>
<li style="box-sizing: border-box; text-align: justify;">C&aacute;c phản ứng phụ n&oacute;i chung l&agrave; nhẹ v&agrave; tự khỏi.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể xuất hiện vi&ecirc;m d&acirc;y thần kinh ngoại bi&ecirc;n, hội chứng Guillain-Barr&eacute; nhưng rất hiếm gặp.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Đối với phụ nữ c&oacute; thai v&agrave; cho con b&uacute;:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng chống chỉ định cho phụ nữ c&oacute; thai v&agrave; cho con b&uacute;.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Tương t&aacute;c thuốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Nếu đang d&ugrave;ng thuốc hoặc muốn ti&ecirc;m c&ugrave;ng với c&aacute;c vắc xin kh&aacute;c v&agrave;o c&ugrave;ng một thời điểm, h&atilde;y hỏi &yacute; kiến của b&aacute;c sĩ hoặc dược sĩ.</li>
<li style="box-sizing: border-box; text-align: justify;">Người đang d&ugrave;ng c&aacute;c thuốc hoặc liệu ph&aacute;p g&acirc;y ức chế miễn dịch c&oacute; thể l&agrave;m giảm đ&aacute;p ứng miễn dịch của vắc xin TD (Kh&ocirc;ng &aacute;p dụng với liệu ph&aacute;p Corticosteroid ngắn hạn dưới 2 tuần).</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Vắc xin n&ecirc;n được bảo quản ở nhiệt độ từ 2 độ C đến 8 độ C, v&agrave; kh&ocirc;ng được để đ&ocirc;ng băng.</li>
<li style="box-sizing: border-box; text-align: justify;">Để xa tầm tay trẻ em.</li>
</ul>', '2021-07-03 20:46:45', '2021-07-03 20:47:21', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (68, 'PHÒNG BỆNH UỐN VÁN', 0, null, null, null, null, null, '2021-07-03 20:47:58', '2021-07-03 20:47:58', null, null);
INSERT INTO vaccination.vaccines (id, vaccine_name, idparent, status, amount, image, important_note, discription, created_at, updated_at, views_count, price) VALUES (69, 'VAT', 68, 'Đang sử dụng', 286, '/storage/vaccine_photo/DWEN9g5YcdjHdSCg03jz.jpeg', null, '<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Uốn v&aacute;n l&agrave; bệnh nhiễm tr&ugrave;ng cấp t&iacute;nh do vi khuẩn yếm kh&iacute; Clostridium tetani g&acirc;y ra. Theo Tổ chức Y tế Thế giới (WHO), mỗi năm c&oacute; h&agrave;ng trăm ngh&igrave;n người tử vong do căn bệnh n&agrave;y. Uốn v&aacute;n cực kỳ nguy hiểm, khi bệnh trở nặng v&agrave; kh&ocirc;ng được cấp cứu kịp thời, bệnh nh&acirc;n c&oacute; thể tử vong v&igrave; suy h&ocirc; hấp, trụy tim mạch, rối loạn thần kinh tự chủ&hellip;</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Những người bị suy giảm miễn dịch, chưa được ti&ecirc;m ph&ograve;ng vắc xin khi bị c&aacute;c vết thương hở, s&acirc;u, trầy xước&hellip; rất dễ bị vi tr&ugrave;ng uốn v&aacute;n c&oacute; sẵn trong đất x&acirc;m nhập v&agrave;o cơ thể tạo nhiễm tr&ugrave;ng v&agrave; g&acirc;y ra bệnh uốn v&aacute;n.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Mọi đối tượng đều c&oacute; thể l&agrave; nạn nh&acirc;n của bệnh uốn v&aacute;n, đặc biệt ở thai phụ khả năng bị uốn v&aacute;n trong l&uacute;c sinh nở dẫn đến uốn v&aacute;n tử cung rất cao, trẻ sơ sinh nếu kh&ocirc;ng may mắc uốn v&aacute;n c&oacute; thể bị suy h&ocirc; hấp, thập ch&iacute; tim ngừng đập, tử vong. Ch&iacute;nh v&igrave; thế, ti&ecirc;m vắc xin uốn v&aacute;n ph&ograve;ng bệnh l&agrave; biện ph&aacute;p tối ưu nhất hiện nay.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Th&ocirc;ng tin Vắc xin uốn v&aacute;n VAT (Việt Nam)</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin VAT (Việt Nam) ph&ograve;ng bệnh uốn v&aacute;n VAT l&agrave; hoạt chất c&oacute; xuất xứ từ Việt Nam, chứa giải độc tố uốn v&aacute;n hấp phụ, c&oacute; khả năng tạo miễn dịch chủ động với kh&aacute;ng nguy&ecirc;n uốn v&aacute;n khi tạo k&iacute;ch th&iacute;ch cho hệ miễn dịch cơ thể sản xuất kh&aacute;ng độc tố ri&ecirc;ng biệt. Đ&acirc;y l&agrave; vắc xin ph&ograve;ng ngừa, kh&ocirc;ng được chỉ định sử dụng trong điều trị uốn v&aacute;n.</p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Nguồn gốc:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Nghi&ecirc;n cứu v&agrave; sản xuất:Viện vắc xin v&agrave; sinh phẩm y tế Nha Trang IVAC &ndash; Việt Nam</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chỉ định:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin VAT gi&uacute;p tạo miễn dịch chủ động ph&ograve;ng bệnh uốn v&aacute;n cho người lớn v&agrave; trẻ em. Đặc biệt l&agrave; c&aacute;c đối tượng c&oacute; nguy cơ cao như:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Phụ nữ c&oacute; thai.</li>
<li style="box-sizing: border-box; text-align: justify;">C&ocirc;ng nh&acirc;n vệ sinh m&ocirc;i trường, cống r&atilde;nh, nước thải.</li>
<li style="box-sizing: border-box; text-align: justify;">Người thường xuy&ecirc;n l&agrave;m việc tại chuồng trại chăn nu&ocirc;i gia s&uacute;c, gia cầm.</li>
<li style="box-sizing: border-box; text-align: justify;">Người l&agrave;m vườn, người l&agrave;m việc ở c&aacute;c trang trại, n&ocirc;ng trường.</li>
<li style="box-sizing: border-box; text-align: justify;">C&ocirc;ng nh&acirc;n x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh.</li>
<li style="box-sizing: border-box; text-align: justify;">Bộ đội v&agrave; thanh ni&ecirc;n xung phong.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Lịch ti&ecirc;m ph&ograve;ng:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Vắc xin VAT ph&ograve;ng uốn v&aacute;n cho người lớn v&agrave; trẻ em c&oacute; lịch ti&ecirc;m cơ bản 3 mũi như sau:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Mũi 1: L&acirc;̀n ti&ecirc;m đầu ti&ecirc;n</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 2: 1 th&aacute;ng sau mũi 1</li>
<li style="box-sizing: border-box; text-align: justify;">Mũi 3: 6 th&aacute;ng sau mũi 2</li>
<li style="box-sizing: border-box; text-align: justify;">Ti&ecirc;m nhắc mỗi 5 &ndash; 10 năm</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Tham khảo lịch ti&ecirc;m ph&ograve;ng uốn v&aacute;n d&agrave;nh ri&ecirc;ng cho phụ nữ mang thai:&nbsp;<a style="box-sizing: border-box; background-color: transparent; color: #337ab7; text-decoration-line: none; outline: none !important;" href="https://vnvc.vn/lich-chich-ngua-uon-van-cho-ba-bau/" target="_blank" rel="noopener noreferrer">https://vnvc.vn/lich-chich-ngua-uon-van-cho-ba-bau/</a></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">C&aacute;ch d&ugrave;ng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">VAT được chỉ định ti&ecirc;m bắp s&acirc;u, liều ti&ecirc;m 0,5ml</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m tĩnh mạch trong bất cứ trường hợp n&agrave;o.</li>
<li style="box-sizing: border-box; text-align: justify;">Lắc tan đều trước khi ti&ecirc;m.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Chống chỉ định:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m cho người dị ứng, qu&aacute; mẫn với bất kỳ th&agrave;nh phần n&agrave;o của vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m cho đối tượng c&oacute; c&aacute;c biểu hiện dị ứng ở lần ti&ecirc;m vắc xin trước.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng d&ugrave;ng cho người c&oacute; c&aacute;c dấu hiệu, triệu chứng thần kinh sau khi ti&ecirc;m c&aacute;c liều trước đ&oacute;.</li>
<li style="box-sizing: border-box; text-align: justify;">Ho&atilde;n ti&ecirc;m với c&aacute;c trường hợp sốt cao hoặc đang mắc c&aacute;c bệnh cấp t&iacute;nh.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Thận trọng:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Đ&aacute;p ứng miễn dịch của vắc xin VAT c&oacute; thể bị giảm nếu đang d&ugrave;ng c&aacute;c liệu ph&aacute;p ức chế miễn dịch.</li>
<li style="box-sizing: border-box; text-align: justify;">Nếu ti&ecirc;m nhầm VAT dưới da th&igrave; c&aacute;c phản ứng phụ sẽ rất rầm rộ do vắc xin c&oacute; chứa muối nh&ocirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng ti&ecirc;m qu&aacute; liều.</li>
<li style="box-sizing: border-box; text-align: justify;">Vắc xin kh&ocirc;ng chống chỉ định cho đối tượng phụ nữ đang cho con b&uacute;.</li>
<li style="box-sizing: border-box; text-align: justify;">Cũng giống như tất cả c&aacute;c loại vắc xin kh&aacute;c phải chuẩn bị đầy đủ c&aacute;c phương tiện y tế v&agrave; thuốc cấp cứu để đề ph&ograve;ng phản ứng phản vệ sau khi ti&ecirc;m.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">T&aacute;c dụng kh&ocirc;ng mong muốn:</span></p>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;">Một số t&aacute;c dụng kh&ocirc;ng mong muốn c&oacute; thể gặp được ghi nhận sau khi sử dụng vắc xin VAT như:</p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Sốt; Đau, sưng, đỏ chỗ ti&ecirc;m. Tuy nhi&ecirc;n c&aacute;c triệu chứng n&agrave;y thường nhẹ v&agrave; tự mất đi</li>
<li style="box-sizing: border-box; text-align: justify;">Sưng hạch bạch huyết gần nơi ti&ecirc;m.</li>
<li style="box-sizing: border-box; text-align: justify;">C&oacute; thể c&oacute; c&aacute;c phản ứng to&agrave;n th&acirc;n: Dị ứng; đau đầu; đổ mồ h&ocirc;i; ớn lạnh; đau cơ, đau khớp.</li>
<li style="box-sizing: border-box; text-align: justify;">Hiếm gặp: Rối loạn chức năng thần kinh c&aacute;nh tay, bả vai.</li>
<li style="box-sizing: border-box; text-align: justify;">Phải th&ocirc;ng b&aacute;o ngay cho b&aacute;c sĩ những t&aacute;c dụng kh&ocirc;ng mong muốn gặp phải sau khi ti&ecirc;m vắc xin.</li>
</ul>
<p style="box-sizing: border-box; margin: 0px 0px 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px; text-align: justify;"><span style="box-sizing: border-box; font-weight: bold;">Bảo quản:</span></p>
<ul style="box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; color: #6d6e70; font-family: Arial, Helvetica, sans-serif; font-size: 16px;">
<li style="box-sizing: border-box; text-align: justify;">Bảo quản ở nhiệt độ từ 2-8&deg;C.</li>
<li style="box-sizing: border-box; text-align: justify;">Kh&ocirc;ng được để đ&ocirc;ng đ&aacute; vắc xin.</li>
<li style="box-sizing: border-box; text-align: justify;">Loại bỏ vắc xin nếu bị đ&ocirc;ng đ&aacute;.</li>
</ul>', '2021-07-03 20:48:12', '2021-07-03 20:48:50', null, null);
